import React, { useEffect, useMemo, useRef, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";
import supabase from "../supabaseClient";
import "./Inbox.css";

/**
 * NOT: Supabase Realtime için tabloda yayın açık olmalı:
 *   alter publication supabase_realtime add table public.talepler;
 */

/** DB anahtar -> ekranda görünen */
const STATUS_DISPLAY = {
    Yeni: "Yeni",
    Inceleme: "İnceleme",
    Atandi: "Atandı",
    Calisiliyor: "Çalışılıyor",
    Beklemede: "Beklemede",
    Tamamlandi: "Tamamlandı",
};

const BOARD_COLUMNS = [
    { key: "Yeni", title: "Yeni" },
    { key: "Inceleme", title: "İnceleme" },
    { key: "Atandi", title: "Atandı" },
    { key: "Calisiliyor", title: "Çalışılıyor" },
    { key: "Beklemede", title: "Beklemede" },
    { key: "Tamamlandi", title: "Tamamlandı" },
];

const PILL_COLOR = { P1: "p1", P2: "p2", P3: "p3" };

export default function InboxModern() {
    const navigate = useNavigate();

    const me = useMemo(() => {
        try {
            return JSON.parse(localStorage.getItem("user_profile") || "{}");
        } catch {
            return {};
        }
    }, []);
    const myEmail = me?.email || "";

    const [view, setView] = useState("board"); // "board" | "list"
    const [q, setQ] = useState("");
    const [mineOnly, setMineOnly] = useState(false);
    const [onlySLA, setOnlySLA] = useState(false);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");
    const [rows, setRows] = useState([]);
    const [active, setActive] = useState(null);

    // refs for stable access in callbacks
    const mineOnlyRef = useRef(mineOnly);
    useEffect(() => {
        mineOnlyRef.current = mineOnly;
    }, [mineOnly]);

    useEffect(() => {
        const token = localStorage.getItem("auth_token");
        if (!token) navigate("/login", { replace: true });
    }, [navigate]);

    useEffect(() => {
        fetchData(); // filtreler değişince tazele
        // eslint-disable-next-line
    }, [mineOnly, onlySLA]);

    // Realtime aboneliği (INSERT/UPDATE/DELETE yakala)
    useEffect(() => {
        const channel = supabase
            .channel("talepler-realtime")
            .on(
                "postgres_changes",
                { event: "*", schema: "public", table: "talepler" },
                (payload) => {
                    setRows((prev) => applyRealtime(prev, payload, mineOnlyRef.current));
                }
            )
            .subscribe();

        return () => {
            supabase.removeChannel(channel);
        };
    }, []);

    // Görünüm değişince mevcut state’i yeniden sırala (DB’ye yazma yok)
    useEffect(() => {
        setRows((prev) => sortRows(prev, mineOnly));
    }, [mineOnly]);

    async function fetchData() {
        if (!myEmail) return;
        setLoading(true);
        setErr("");
        try {
            const now = new Date();

            let query = supabase.from("talepler").select(`
        id, baslik, durum, oncelik, talep_eden_email, atanan_email,
        guncelleme_tarihi, bitis_tarihi, sla_son_tarih, aciklama,
        kolon_sira, atanan_sira
      `);

            // Sıralama stratejisi:
            if (mineOnly) {
                query = query
                    .eq("atanan_email", myEmail)
                    .order("atanan_sira", { ascending: true, nullsFirst: false })
                    .order("guncelleme_tarihi", { ascending: false });
            } else {
                query = query
                    .order("kolon_sira", { ascending: true, nullsFirst: true })
                    .order("guncelleme_tarihi", { ascending: false });
            }

            if (onlySLA) {
                const t24 = new Date(
                    now.getTime() + 24 * 60 * 60 * 1000
                ).toISOString();
                query = query
                    .lt("sla_son_tarih", t24)
                    .not("durum", "in", ["Tamamlandi", "Reddedildi"]);
            }

            const { data, error } = await query.limit(500);
            if (error) throw error;
            setRows(sortRows(data || [], mineOnly));
        } catch (e) {
            console.error(e);
            setErr("Kayıtlar çekilemedi.");
        } finally {
            setLoading(false);
        }
    }

    /* ---------- helpers ---------- */
    function groupByStatus(items) {
        const map = Object.fromEntries(BOARD_COLUMNS.map((c) => [c.key, []]));
        items.forEach((i) => {
            (map[i.durum || "Yeni"] ??= []).push(i);
        });
        return map;
    }

    function recomputeAssigneeRanks(list) {
        const byA = {};
        list.forEach((r) => {
            if (!r.atanan_email) return;
            (byA[r.atanan_email] ??= []).push(r);
        });
        Object.values(byA).forEach((arr) => {
            // Göründüğü sıraya göre 1..N
            arr.forEach((r, idx) => {
                r.atanan_sira = idx + 1;
            });
        });
    }

    async function persistOrder(updates) {
        // updates: [{id, durum?, kolon_sira?, atanan_sira?}]
        if (!updates?.length) return;
        const nowIso = new Date().toISOString();
        const payload = updates.map((u) => ({
            id: u.id,
            ...(u.durum !== undefined ? { durum: u.durum } : {}),
            ...(u.kolon_sira !== undefined ? { kolon_sira: u.kolon_sira } : {}),
            ...(u.atanan_sira !== undefined ? { atanan_sira: u.atanan_sira } : {}),
            guncelleme_tarihi: nowIso,
        }));
        const { error } = await supabase
            .from("talepler")
            .upsert(payload, { onConflict: "id" });
        if (error) console.error("order save error", error);
    }

    /* ---------- DnD: Kanban ---------- */
    async function onBoardDragEnd(result) {
        const { source, destination, draggableId } = result;
        if (!destination) return;

        const statusFrom = source.droppableId;
        const statusTo = destination.droppableId;

        const byStatus = groupByStatus([...rows]);
        const idx = byStatus[statusFrom].findIndex(
            (i) => String(i.id) === String(draggableId)
        );
        if (idx === -1) return;

        const item = byStatus[statusFrom][idx];
        byStatus[statusFrom].splice(idx, 1);
        byStatus[statusTo].splice(destination.index, 0, item);

        // Flatten + önceki snapshot
        const flattened = BOARD_COLUMNS.flatMap((c) => byStatus[c.key]);
        const prevById = new Map(rows.map((r) => [r.id, r]));

        // kolon_sira + durum hesapla (sadece değişenleri topla)
        const updates = [];
        BOARD_COLUMNS.forEach((col) => {
            byStatus[col.key].forEach((it, i) => {
                const newDurum = col.key;
                const newKolonSira = i + 1;
                const prev = prevById.get(it.id) || {};
                let dirty = false;

                if (it.durum !== newDurum) {
                    it.durum = newDurum;
                    dirty = true;
                }
                if (it.kolon_sira !== newKolonSira) {
                    it.kolon_sira = newKolonSira;
                    dirty = true;
                }
                if (dirty)
                    updates.push({ id: it.id, durum: newDurum, kolon_sira: newKolonSira });
            });
        });

        // atanan_sira’ları yeniden hesapla (görünür sıraya göre 1..N)
        recomputeAssigneeRanks(flattened);
        flattened.forEach((it) => {
            const prev = prevById.get(it.id) || {};
            if ((prev.atanan_sira ?? null) !== (it.atanan_sira ?? null)) {
                const u = updates.find((u) => u.id === it.id);
                if (u) u.atanan_sira = it.atanan_sira;
                else updates.push({ id: it.id, atanan_sira: it.atanan_sira });
            }
        });

        // Optimistic UI
        setRows(sortRows(flattened, mineOnly));

        // DB’ye yaz → Realtime herkese gidecek
        await persistOrder(updates);
    }

    /* ---------- DnD: Liste (Bana atananlar) ---------- */
    async function onListDragEnd(result) {
        const { source, destination, draggableId } = result;
        if (!destination) return;
        if (!mineOnly) return; // sadece "Bana atananlar" açıkken izin ver

        // şu an görünür listedeki (filtrelenmiş) sırayı kullan
        const list = filtered.filter((r) => r.atanan_email === myEmail);
        const from = list.findIndex((i) => String(i.id) === String(draggableId));
        if (from === -1) return;

        const moved = list[from];
        list.splice(from, 1);
        list.splice(destination.index, 0, moved);

        // yeni 1..N
        list.forEach((r, i) => {
            r.atanan_sira = i + 1;
        });

        // rows içine geri yaz
        const newRows = rows.map((r) => {
            const hit = list.find((x) => x.id === r.id);
            return hit ? { ...r, atanan_sira: hit.atanan_sira } : r;
        });
        setRows(sortRows(newRows, mineOnly)); // optimistic

        // Sadece değişenleri gönder
        const updates = list
            .filter(
                (r) => r.id && rows.find((x) => x.id === r.id)?.atanan_sira !== r.atanan_sira
            )
            .map(({ id, atanan_sira }) => ({ id, atanan_sira }));

        await persistOrder(updates);
    }

    /* ---------- quick actions ---------- */
    async function takeOwnership(id) {
        if (!myEmail) return;
        const myColMax =
            rows
                .filter((r) => r.durum === "Inceleme")
                .reduce((m, r) => Math.max(m, r.kolon_sira || 0), 0) + 1;
        const myQueueMax =
            rows
                .filter((r) => r.atanan_email === myEmail)
                .reduce((m, r) => Math.max(m, r.atanan_sira || 0), 0) + 1;

        const { error } = await supabase
            .from("talepler")
            .update({
                atanan_email: myEmail,
                durum: "Inceleme",
                kolon_sira: myColMax,
                atanan_sira: myQueueMax,
                guncelleme_tarihi: new Date().toISOString(),
            })
            .eq("id", id);
        if (!error) fetchData();
    }

    async function moveTo(id, statusKey) {
        const max =
            rows
                .filter((r) => r.durum === statusKey)
                .reduce((m, r) => Math.max(m, r.kolon_sira || 0), 0) + 1;
        const { error } = await supabase
            .from("talepler")
            .update({
                durum: statusKey,
                kolon_sira: max,
                guncelleme_tarihi: new Date().toISOString(),
            })
            .eq("id", id);
        if (!error) fetchData();
    }

    async function markDone(id) {
        await moveTo(id, "Tamamlandi");
    }

    function handleLogout() {
        localStorage.removeItem("auth_token");
        localStorage.removeItem("user_profile");
        navigate("/login", { replace: true });
    }

    /* ---------- filter ---------- */
    const filtered = rows.filter((r) => {
        if (!q) return true;
        const text = `${r.id} ${r.baslik} ${r.talep_eden_email} ${r.atanan_email || ""
            }`.toLowerCase();
        return text.includes(q.toLowerCase());
    });

    const grouped = groupByStatus(filtered);

    return (
        <div className="mx">
            {/* Command bar */}
            <div className="mx-top">
                <div className="brand">
                    <div className="logo">🧭</div>
                    <div className="bt">
                        <h1>Gelen Kutusu</h1>
                        <span className="muted">İş & Süreç Geliştirme</span>
                    </div>
                </div>

                <div className="cmd">
                    <div className="seg">
                        <button
                            className={`seg-btn ${view === "board" ? "on" : ""}`}
                            onClick={() => setView("board")}
                        >
                            Kanban
                        </button>
                        <button
                            className={`seg-btn ${view === "list" ? "on" : ""}`}
                            onClick={() => setView("list")}
                        >
                            Liste
                        </button>
                    </div>

                    <div className="search-wrap">
                        <input
                            className="search"
                            placeholder="Ara: #id, başlık, kişi…"
                            value={q}
                            onChange={(e) => setQ(e.target.value)}
                        />
                    </div>

                    <div className="toggles">
                        <label className="toggle">
                            <input
                                type="checkbox"
                                checked={mineOnly}
                                onChange={(e) => setMineOnly(e.target.checked)}
                            />
                            <span>Bana atananlar</span>
                        </label>
                        <label className="toggle">
                            <input
                                type="checkbox"
                                checked={onlySLA}
                                onChange={(e) => setOnlySLA(e.target.checked)}
                            />
                            <span>SLA (&lt;24s)</span>
                        </label>
                    </div>

                    <Link to="/newrequest" className="btn primary">
                        + Yeni Talep
                    </Link>
                    <div className="chip">{me?.kullanici || me?.email}</div>
                    <button className="btn danger" onClick={handleLogout}>
                        Çıkış
                    </button>
                </div>
            </div>

            {err && <div className="mx-alert err">{err}</div>}

            {loading ? (
                <div className="mx-skel">Yükleniyor…</div>
            ) : view === "board" ? (
                <DragDropContext onDragEnd={onBoardDragEnd}>
                    <div className="board">
                        {BOARD_COLUMNS.map((col) => {
                            const items = grouped[col.key] || [];
                            return (
                                <Droppable droppableId={col.key} key={col.key}>
                                    {(provided) => (
                                        <div
                                            className="col"
                                            ref={provided.innerRef}
                                            {...provided.droppableProps}
                                        >
                                            <div className="col-head">
                                                <span className={`dot s-${slug(col.title)}`}></span>
                                                <h3>{col.title}</h3>
                                                <span className="count">{items.length}</span>
                                            </div>
                                            <div className="col-body">
                                                {items.length === 0 && (
                                                    <div className="col-empty">Henüz kayıt yok</div>
                                                )}
                                                {items.map((item, index) => (
                                                    <Draggable
                                                        draggableId={String(item.id)}
                                                        index={index}
                                                        key={item.id}
                                                    >
                                                        {(pp) => (
                                                            <div
                                                                ref={pp.innerRef}
                                                                {...pp.draggableProps}
                                                                {...pp.dragHandleProps}
                                                            >
                                                                <Card
                                                                    data={item}
                                                                    onOpen={() => setActive(item)}
                                                                    onTake={() => takeOwnership(item.id)}
                                                                    onMove={(st) => moveTo(item.id, st)}
                                                                    onDone={() => markDone(item.id)}
                                                                    me={myEmail}
                                                                />
                                                            </div>
                                                        )}
                                                    </Draggable>
                                                ))}
                                                {provided.placeholder}
                                            </div>
                                        </div>
                                    )}
                                </Droppable>
                            );
                        })}
                    </div>
                </DragDropContext>
            ) : (
                // LISTE GÖRÜNÜMÜ — mineOnly açıkken sürükleyerek atanan_sira değiştir
                <DragDropContext onDragEnd={onListDragEnd}>
                    <Droppable droppableId="listMine" isDropDisabled={!mineOnly}>
                        {(provided) => (
                            <div
                                className="listv"
                                ref={provided.innerRef}
                                {...provided.droppableProps}
                            >
                                {filtered.length === 0 && (
                                    <div className="col-empty">Kayıt bulunamadı</div>
                                )}
                                {filtered.map((r, index) => (
                                    <Draggable
                                        draggableId={String(r.id)}
                                        index={index}
                                        key={r.id}
                                        isDragDisabled={!mineOnly}
                                    >
                                        {(pp) => (
                                            <div
                                                ref={pp.innerRef}
                                                {...pp.draggableProps}
                                                {...pp.dragHandleProps}
                                                className="rowv"
                                                onClick={() => setActive(r)}
                                            >
                                                <span className={`pill ${PILL_COLOR[r.oncelik] || "p3"}`}>
                                                    {r.oncelik || "P3"}
                                                </span>
                                                <span
                                                    className={`badge s-${slug(STATUS_DISPLAY[r.durum] || "Yeni")
                                                        }`}
                                                >
                                                    {STATUS_DISPLAY[r.durum] || "Yeni"}
                                                </span>
                                                <span className="r-title">
                                                    #{r.id} — {r.baslik}
                                                </span>
                                                <span className="r-sub">
                                                    {r.talep_eden_email} → {r.atanan_email || "—"}
                                                    {mineOnly && r.atanan_email === myEmail && (
                                                        <b style={{ marginLeft: 8, opacity: 0.8 }}>
                                                            (Sıra: {r.atanan_sira ?? "—"})
                                                        </b>
                                                    )}
                                                </span>
                                                <span className="r-time">{fmtTime(r.guncelleme_tarihi)}</span>
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                                {!mineOnly && (
                                    <div className="hint" style={{ padding: 8, opacity: 0.7 }}>
                                        Listeyi sürükleyerek sıralamak için “Bana atananlar”ı işaretleyin.
                                    </div>
                                )}
                            </div>
                        )}
                    </Droppable>
                </DragDropContext>
            )}

            {/* Detay çekmecesi */}
            {active && (
                <div className="drawer" role="dialog" aria-modal="true">
                    <div className="drawer-panel">
                        <button className="drawer-close" onClick={() => setActive(null)}>
                            ×
                        </button>

                        <div className="drawer-top">
                            <span className={`pill ${PILL_COLOR[active.oncelik] || "p3"}`}>
                                {active.oncelik || "P3"}
                            </span>
                            <span
                                className={`badge s-${slug(STATUS_DISPLAY[active.durum] || "Yeni")
                                    }`}
                            >
                                {STATUS_DISPLAY[active.durum] || "Yeni"}
                            </span>
                            {active.sla_son_tarih &&
                                new Date(active.sla_son_tarih).getTime() - Date.now() <
                                24 * 60 * 60 * 1000 && <span className="sla">SLA</span>}
                            <h2>
                                #{active.id} — {active.baslik}
                            </h2>
                            <div className="d-sub">
                                <span>
                                    İsteyen: <b>{active.talep_eden_email}</b>
                                </span>
                                <span> · </span>
                                <span>
                                    Atanan: <b>{active.atanan_email || "—"}</b>
                                </span>
                                <span> · </span>
                                <span>Güncellendi: {fmtTime(active.guncelleme_tarihi)}</span>
                            </div>
                        </div>

                        <div className="drawer-body">
                            <Section title="Açıklama">
                                <p className="desc">{active.aciklama || "Açıklama girilmemiş."}</p>
                            </Section>

                            <Section title="Hızlı İşlemler">
                                <div className="act-grid">
                                    {!active.atanan_email && (
                                        <button
                                            className="btn ghost"
                                            onClick={() => {
                                                setActive(null);
                                                takeOwnership(active.id);
                                            }}
                                        >
                                            Üstlen
                                        </button>
                                    )}
                                    <button
                                        className="btn ghost"
                                        onClick={() => {
                                            setActive(null);
                                            moveTo(active.id, "Inceleme");
                                        }}
                                    >
                                        İnceleme
                                    </button>
                                    <button
                                        className="btn ghost"
                                        onClick={() => {
                                            setActive(null);
                                            moveTo(active.id, "Calisiliyor");
                                        }}
                                    >
                                        Çalışılıyor
                                    </button>
                                    <button
                                        className="btn ghost"
                                        onClick={() => {
                                            setActive(null);
                                            moveTo(active.id, "Beklemede");
                                        }}
                                    >
                                        Beklemede
                                    </button>
                                    <button
                                        className="btn primary"
                                        onClick={() => {
                                            setActive(null);
                                            markDone(active.id);
                                        }}
                                    >
                                        ✓ Tamamlandı
                                    </button>
                                </div>
                            </Section>

                            <Section title="Zaman">
                                <div className="kv">
                                    <div>
                                        <span>Hedef Tarih:</span>
                                        <b>{active.bitis_tarihi ? fmtDate(active.bitis_tarihi) : "—"}</b>
                                    </div>
                                    <div>
                                        <span>SLA Son:</span>
                                        <b>{active.sla_son_tarih ? fmtDate(active.sla_son_tarih) : "—"}</b>
                                    </div>
                                </div>
                            </Section>
                        </div>
                    </div>
                    <div className="drawer-backdrop" onClick={() => setActive(null)} />
                </div>
            )}
        </div>
    );
}

/* components */
function Card({ data, onOpen, onTake, onMove, onDone, me }) {
    const mine = data.atanan_email && data.atanan_email === me;
    const slaSoon =
        data.sla_son_tarih &&
        new Date(data.sla_son_tarih).getTime() - Date.now() <
        24 * 60 * 60 * 1000;

    return (
        <div className={`card ${mine ? "mine" : ""}`}>
            <div className="c-top">
                <span className={`pill ${PILL_COLOR[data.oncelik] || "p3"}`}>
                    {data.oncelik || "P3"}
                </span>
                <span
                    className={`badge s-${slug(STATUS_DISPLAY[data.durum] || "Yeni")}`}
                >
                    {STATUS_DISPLAY[data.durum] || "Yeni"}
                </span>
                {slaSoon && <span className="sla">SLA</span>}
                <button className="icn" onClick={onOpen} title="Detay">
                    ⋯
                </button>
            </div>

            <div className="c-title">
                #{data.id} — {data.baslik}
            </div>
            <div className="c-sub">
                <span>{data.talep_eden_email}</span>
                <span>→</span>
                <span>{data.atanan_email || "—"}</span>
            </div>

            <div className="c-foot">
                <span className="time">{fmtTime(data.guncelleme_tarihi)}</span>
                <div className="act">
                    {!data.atanan_email && (
                        <button className="btn ghost xs" onClick={onTake}>
                            Üstlen
                        </button>
                    )}
                    <button className="btn ghost xs" onClick={() => onMove("Inceleme")}>
                        İnceleme
                    </button>
                    <button className="btn ghost xs" onClick={() => onMove("Calisiliyor")}>
                        Çalışılıyor
                    </button>
                    <button className="btn primary xs" onClick={onDone}>
                        Tamamla
                    </button>
                </div>
            </div>
        </div>
    );
}

function Section({ title, children }) {
    return (
        <section className="sec">
            <div className="sec-h">
                <h4>{title}</h4>
            </div>
            <div className="sec-b">{children}</div>
        </section>
    );
}

/* utils */
function slug(s) {
    return (s || "")
        .toLowerCase()
        .replaceAll("ı", "i")
        .replaceAll("ğ", "g")
        .replaceAll("ş", "s")
        .replaceAll("ö", "o")
        .replaceAll("ç", "c")
        .replaceAll("ü", "u")
        .replace(/\s+/g, "-");
}
function fmtTime(ts) {
    try {
        return new Date(ts).toLocaleString();
    } catch {
        return "—";
    }
}
function fmtDate(ts) {
    try {
        return new Date(ts).toLocaleDateString();
    } catch {
        return "—";
    }
}

/* ------ realtime yardımcıları ------ */
function dateNum(v) {
    try {
        return new Date(v).getTime() || 0;
    } catch {
        return 0;
    }
}

// Görünüm moduna göre stabil sıralama (kolon_sira / atanan_sira)
function sortRows(list, mineOnly) {
    const a = [...(list || [])];
    if (mineOnly) {
        a.sort((x, y) => {
            const ax = x.atanan_sira ?? 1e9;
            const ay = y.atanan_sira ?? 1e9;
            if (ax !== ay) return ax - ay;
            const tx = dateNum(y.guncelleme_tarihi) - dateNum(x.guncelleme_tarihi);
            if (tx !== 0) return tx;
            return (x.id ?? 0) - (y.id ?? 0);
        });
    } else {
        a.sort((x, y) => {
            const kx = x.kolon_sira ?? 1e9;
            const ky = y.kolon_sira ?? 1e9;
            if (kx !== ky) return kx - ky;
            const tx = dateNum(y.guncelleme_tarihi) - dateNum(x.guncelleme_tarihi);
            if (tx !== 0) return tx;
            return (x.id ?? 0) - (y.id ?? 0);
        });
    }
    return a;
}

// Realtime’dan gelen INSERT/UPDATE/DELETE olayını tek satır bazında uygula
function applyRealtime(prev, payload, mineOnly) {
    const type = payload.eventType;
    const next = [...prev];
    if (type === "DELETE") {
        return next.filter((r) => r.id !== payload.old?.id);
    }
    const row = payload.new;
    const i = next.findIndex((r) => r.id === row.id);
    if (i === -1) next.push(row);
    else next[i] = { ...next[i], ...row };
    return sortRows(next, mineOnly);
}
