// src/pages/Inbox.js
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";
import supabase from "../supabaseClient";
import "./Inbox.css";

const STATUS_DISPLAY = {
    Yeni: "Yeni",
    Inceleme: "Ä°nceleme",
    Atandi: "AtandÄ±",
    Calisiliyor: "Ã‡alÄ±ÅŸÄ±lÄ±yor",
    Beklemede: "Beklemede",
    Tamamlandi: "TamamlandÄ±",
};
const BOARD_COLUMNS = [
    { key: "Yeni", title: "Yeni" },
    { key: "Inceleme", title: "Ä°nceleme" },
    { key: "Atandi", title: "AtandÄ±" },
    { key: "Calisiliyor", title: "Ã‡alÄ±ÅŸÄ±lÄ±yor" },
    { key: "Beklemede", title: "Beklemede" },
    { key: "Tamamlandi", title: "TamamlandÄ±" },
];
const PILL_COLOR = { P1: "p1", P2: "p2", P3: "p3" };

export default function InboxModern() {
    const navigate = useNavigate();
    const me = useMemo(() => { try { return JSON.parse(localStorage.getItem("user_profile") || "{}"); } catch { return {}; } }, []);
    const myEmail = me?.email || "";

    const [view, setView] = useState("board");
    const [q, setQ] = useState("");
    const [mineOnly, setMineOnly] = useState(false);
    const [onlySLA, setOnlySLA] = useState(false);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");
    const [rows, setRows] = useState([]);
    const [active, setActive] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem("auth_token");
        if (!token) navigate("/login", { replace: true });
    }, [navigate]);

    useEffect(() => { fetchData(); /* eslint-disable-next-line */ }, [mineOnly, onlySLA]);

    async function fetchData() {
        if (!myEmail) return;
        setLoading(true); setErr("");
        try {
            const now = new Date();
            let query = supabase.from("talepler").select(`
        id, baslik, durum, oncelik, talep_eden_email, atanan_email,
        guncelleme_tarihi, bitis_tarihi, sla_son_tarih, aciklama, kolon_sira, atanan_sira
      `).order("kolon_sira", { ascending: true }).order("guncelleme_tarihi", { ascending: false }).limit(500);

            if (mineOnly) query = query.eq("atanan_email", myEmail);
            if (onlySLA) {
                const t24 = new Date(now.getTime() + 24 * 60 * 60 * 1000).toISOString();
                query = query.lt("sla_son_tarih", t24).not("durum", "in", ["Tamamlandi", "Reddedildi"]);
            }

            const { data, error } = await query;
            if (error) throw error;
            setRows(data || []);
        } catch (e) {
            console.error(e);
            setErr("KayÄ±tlar Ã§ekilemedi.");
        } finally {
            setLoading(false);
        }
    }

    // --- DnD helpers
    function groupByStatus(items) {
        const map = Object.fromEntries(BOARD_COLUMNS.map(c => [c.key, []]));
        items.forEach(i => { map[i.durum || "Yeni"].push(i); });
        // her kolonda kolon_sira deÄŸerine gÃ¶re zaten sÄ±ralÄ± geliyor
        return map;
    }

    async function persistOrder(updates) {
        // updates = [{id, durum, kolon_sira, atanan_sira?}]
        const payload = updates.map(u => ({ id: u.id, durum: u.durum, kolon_sira: u.kolon_sira, atanan_sira: u.atanan_sira ?? null, guncelleme_tarihi: new Date().toISOString() }));
        const { error } = await supabase.from("talepler").upsert(payload, { onConflict: "id" });
        if (error) console.error("order save error", error);
    }

    function recomputeAssigneeRanks(list) {
        // aynÄ± atanan_email iÃ§in sÄ±ralarÄ± 1..N ver
        const byA = {};
        list.forEach((r) => {
            if (!r.atanan_email) return;
            if (!byA[r.atanan_email]) byA[r.atanan_email] = [];
            byA[r.atanan_email].push(r);
        });
        Object.values(byA).forEach(arr => {
            arr.sort((a, b) => (a.atanan_sira ?? 1) - (b.atanan_sira ?? 1));
            arr.forEach((r, idx) => { r.atanan_sira = idx + 1; });
        });
    }

    async function onDragEnd(result) {
        const { source, destination, draggableId } = result;
        if (!destination) return;

        const statusFrom = source.droppableId;
        const statusTo = destination.droppableId;
        if (statusFrom === undefined || statusTo === undefined) return;

        // mevcut stateâ€™ten Ã§alÄ±ÅŸ
        const byStatus = groupByStatus([...rows]);
        const itemIndex = byStatus[statusFrom].findIndex(i => String(i.id) === String(draggableId));
        if (itemIndex === -1) return;

        const item = byStatus[statusFrom][itemIndex];
        // kolonlar arasÄ±nda Ã§Ä±kar/ekle
        byStatus[statusFrom].splice(itemIndex, 1);
        byStatus[statusTo].splice(destination.index, 0, item);

        // itemâ€™in durumunu gÃ¼ncelle
        item.durum = statusTo;

        // her kolon iÃ§in kolon_siraâ€™yÄ± yeniden ata (1..N)
        BOARD_COLUMNS.forEach(col => {
            byStatus[col.key].forEach((it, idx) => { it.kolon_sira = idx + 1; });
        });

        // atanan sÄ±ralarÄ±nÄ± da gÃ¼ncelle (kullanÄ±cÄ± kuyruklarÄ±)
        const newFlat = BOARD_COLUMNS.flatMap(c => byStatus[c.key]);
        recomputeAssigneeRanks(newFlat);

        // optimistic update
        setRows(newFlat);

        // dbâ€™ye yaz
        const updates = newFlat.map(({ id, durum, kolon_sira, atanan_sira }) => ({ id, durum, kolon_sira, atanan_sira }));
        await persistOrder(updates);
    }

    // --- quick actions
    async function takeOwnership(id) {
        if (!myEmail) return;
        // benim kolonumdaki en alta yerleÅŸtir
        const myMax = rows.filter(r => r.durum === "Inceleme").reduce((m, r) => Math.max(m, r.kolon_sira || 0), 0) + 1;
        const myAssigneeMax = rows.filter(r => r.atanan_email === myEmail).reduce((m, r) => Math.max(m, r.atanan_sira || 0), 0) + 1;

        const { error } = await supabase.from("talepler").update({
            atanan_email: myEmail, durum: "Inceleme",
            kolon_sira: myMax, atanan_sira: myAssigneeMax,
            guncelleme_tarihi: new Date().toISOString()
        }).eq("id", id);
        if (!error) fetchData();
    }

    async function moveTo(id, statusKey) {
        // gittiÄŸi kolonun en altÄ±na koy
        const max = rows.filter(r => r.durum === statusKey).reduce((m, r) => Math.max(m, r.kolon_sira || 0), 0) + 1;
        const { error } = await supabase.from("talepler").update({
            durum: statusKey, kolon_sira: max, guncelleme_tarihi: new Date().toISOString()
        }).eq("id", id);
        if (!error) fetchData();
    }

    async function markDone(id) {
        await moveTo(id, "Tamamlandi");
    }

    function handleLogout() {
        localStorage.removeItem("auth_token");
        localStorage.removeItem("user_profile");
        navigate("/login", { replace: true });
    }

    // filtre
    const filtered = rows.filter(r => {
        if (!q) return true;
        const text = `${r.id} ${r.baslik} ${r.talep_eden_email} ${r.atanan_email || ""}`.toLowerCase();
        return text.includes(q.toLowerCase());
    });

    // board groups
    const grouped = groupByStatus(filtered);

    return (
        <div className="mx">
            {/* Command bar */}
            <div className="mx-top">
                <div className="brand">
                    <div className="logo">ðŸ§­</div>
                    <div className="bt">
                        <h1>Gelen Kutusu</h1>
                        <span className="muted">Ä°ÅŸ & SÃ¼reÃ§ GeliÅŸtirme</span>
                    </div>
                </div>

                <div className="cmd">
                    <div className="seg">
                        <button className={`seg-btn ${view === "board" ? "on" : ""}`} onClick={() => setView("board")}>Kanban</button>
                        <button className={`seg-btn ${view === "list" ? "on" : ""}`} onClick={() => setView("list")}>Liste</button>
                    </div>

                    <div className="search-wrap">
                        <input className="search" placeholder="Ara: #id, baÅŸlÄ±k, kiÅŸiâ€¦" value={q} onChange={(e) => setQ(e.target.value)} />
                    </div>

                    <div className="toggles">
                        <label className="toggle">
                            <input type="checkbox" checked={mineOnly} onChange={e => setMineOnly(e.target.checked)} />
                            <span>Bana atananlar</span>
                        </label>
                        <label className="toggle">
                            <input type="checkbox" checked={onlySLA} onChange={e => setOnlySLA(e.target.checked)} />
                            <span>SLA (&lt;24s)</span>
                        </label>
                    </div>

                    <Link to="/newrequest" className="btn primary">+ Yeni Talep</Link>
                    <div className="chip">{me?.kullanici || me?.email}</div>
                    <button className="btn danger" onClick={handleLogout}>Ã‡Ä±kÄ±ÅŸ</button>
                </div>
            </div>

            {err && <div className="mx-alert err">{err}</div>}

            {loading ? (
                <div className="mx-skel">YÃ¼kleniyorâ€¦</div>
            ) : view === "board" ? (
                <DragDropContext onDragEnd={onDragEnd}>
                    <div className="board">
                        {BOARD_COLUMNS.map(col => {
                            const items = grouped[col.key] || [];
                            return (
                                <Droppable droppableId={col.key} key={col.key}>
                                    {(provided) => (
                                        <div className="col" ref={provided.innerRef} {...provided.droppableProps}>
                                            <div className="col-head">
                                                <span className={`dot s-${slug(col.title)}`}></span>
                                                <h3>{col.title}</h3>
                                                <span className="count">{items.length}</span>
                                            </div>
                                            <div className="col-body">
                                                {items.length === 0 && <div className="col-empty">HenÃ¼z kayÄ±t yok</div>}
                                                {items.map((item, index) => (
                                                    <Draggable draggableId={String(item.id)} index={index} key={item.id}>
                                                        {(pp) => (
                                                            <div ref={pp.innerRef} {...pp.draggableProps} {...pp.dragHandleProps}>
                                                                <Card
                                                                    data={item}
                                                                    onOpen={() => setActive(item)}
                                                                    onTake={() => takeOwnership(item.id)}
                                                                    onMove={(st) => moveTo(item.id, st)}
                                                                    onDone={() => markDone(item.id)}
                                                                    me={myEmail}
                                                                />
                                                            </div>
                                                        )}
                                                    </Draggable>
                                                ))}
                                                {provided.placeholder}
                                            </div>
                                        </div>
                                    )}
                                </Droppable>
                            );
                        })}
                    </div>
                </DragDropContext>
            ) : (
                <div className="listv">
                    {filtered.length === 0 && <div className="col-empty">KayÄ±t bulunamadÄ±</div>}
                    {filtered.map(r => (
                        <div key={r.id} className="rowv" onClick={() => setActive(r)}>
                            <span className={`pill ${PILL_COLOR[r.oncelik] || "p3"}`}>{r.oncelik || "P3"}</span>
                            <span className={`badge s-${slug(STATUS_DISPLAY[r.durum] || "Yeni")}`}>{STATUS_DISPLAY[r.durum] || "Yeni"}</span>
                            <span className="r-title">#{r.id} â€” {r.baslik}</span>
                            <span className="r-sub">{r.talep_eden_email} â†’ {r.atanan_email || "â€”"}</span>
                            <span className="r-time">{fmtTime(r.guncelleme_tarihi)}</span>
                        </div>
                    ))}
                </div>
            )}

