import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import supabase from "../supabaseClient";
import {
    ResponsiveContainer, LineChart, Line, BarChart, Bar,
    XAxis, YAxis, CartesianGrid, Tooltip, Legend, AreaChart, Area
} from "recharts";
import "./Inbox.css";

/* ---- Sabitler ---- */
const STATUS = {
    Yeni: "Yeni",
    SirayaAlindi: "SirayaAlindi",
    IslemeAlindi: "IslemeAlindi",
    TestEdiliyor: "TestEdiliyor",
    Tamamlandi: "Tamamlandi",
};
const STATUS_DISPLAY = {
    [STATUS.Yeni]: "Yeni",
    [STATUS.SirayaAlindi]: "Sıraya Alındı",
    [STATUS.IslemeAlindi]: "İşleme Alındı",
    [STATUS.TestEdiliyor]: "Test Ediliyor",
    [STATUS.Tamamlandi]: "Tamamlandı",
};
const MONITORED_USERS = [
    { name: "GÖRKEM ÇADIRCI" },
    { name: "FURKAN BİLGİLİ" },
    { name: "YAĞIZ EFE BULUTCU" },
];
const ALLOWED_VIEWER = "ONUR KEREM ÖZTÜRK";

/* ---- Utils ---- */
const ts = (v) => { try { return new Date(v).getTime() || 0; } catch { return 0; } };
const fmtDT = (v) => { try { return new Date(v).toLocaleString(); } catch { return "—"; } };
const day = 24 * 60 * 60 * 1000;
const toDayKey = (t) => new Date(new Date(t).getFullYear(), new Date(t).getMonth(), new Date(t).getDate()).getTime();
const clampNum = (n) => Number.isFinite(n) ? n : 0;

// proptan farklı isimler gelebilir; ilk yanıt tarihi için esnek okuma
const getFirstResponseTs = (r) => ts(r.ilk_aksiyon_tarihi || r.ilk_yanit_tarihi || r.first_response_at || r.guncelleme_tarihi);

// tamamlanma zamanını esnek okuma: bitis_tarihi öncelikli, sonra tamamlanma_tarihi, sonra guncelleme_tarihi
const getDoneTs = (r) => ts(r.bitis_tarihi || r.tamamlanma_tarihi || r.bitisTarihi || r.guncelleme_tarihi);

// son aktivite (aging WIP için): guncelleme_tarihi öncelikli, yoksa oluşturma/giriş/açılış tarihi
const getLastActivityTs = (r) =>
    ts(r.guncelleme_tarihi || r.olusturma_tarihi || r.giristarihi || r.acilis_tarihi);

/* ---- Bileşen ---- */
export default function TeamMonitor() {
    const navigate = useNavigate();

    // auth
    const me = useMemo(() => {
        try { return JSON.parse(localStorage.getItem("user_profile") || "{}"); }
        catch { return {}; }
    }, []);
    const myName = (me?.kullanici || "").trim();
    useEffect(() => {
        if (!localStorage.getItem("auth_token")) navigate("/login", { replace: true });
    }, [navigate]);
    const canView = useMemo(
        () => (myName || "").toLocaleUpperCase("tr-TR") === ALLOWED_VIEWER,
        [myName]
    );

    // UI
    const [selectedUser, setSelectedUser] = useState(MONITORED_USERS[0].name);
    const [q, setQ] = useState("");
    const [onlySLA, setOnlySLA] = useState(false);

    // data
    const [rows, setRows] = useState([]);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");

    // fetch (seçili kullanıcı)
    useEffect(() => {
        if (!canView) return;
        (async () => {
            try {
                setLoading(true); setErr("");
                let query = supabase.from("talepler").select("*").eq("atanan_kullanici", selectedUser);
                if (onlySLA) {
                    const t24 = new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString();
                    query = query.lt("sla_son_tarih", t24).not("durum", "in", '("Tamamlandi","Reddedildi")');
                }
                query = query.order("guncelleme_tarihi", { ascending: false }).limit(1000);
                const { data, error } = await query;
                if (error) throw error;
                setRows(data || []);
            } catch (e) {
                console.error(e); setErr("Kayıtlar çekilemedi.");
            } finally { setLoading(false); }
        })();
    }, [selectedUser, onlySLA, canView]);

    // realtime (yalnız seçili kullanıcı)
    useEffect(() => {
        if (!canView) return;
        const ch = supabase
            .channel("tm-realtime-analytics")
            .on("postgres_changes", { event: "*", schema: "public", table: "talepler" }, (payload) => {
                setRows((prev) => {
                    const r = payload.new || payload.old || {};
                    if (r.atanan_kullanici !== selectedUser) return prev;
                    if (payload.eventType === "DELETE") return prev.filter(x => x.id !== r.id);
                    const next = [...prev];
                    const i = next.findIndex(x => x.id === r.id);
                    if (i === -1 && payload.eventType === "INSERT") next.unshift(r);
                    else if (i > -1) next[i] = { ...next[i], ...r };
                    return next.sort((a, b) => ts(b.guncelleme_tarihi) - ts(a.guncelleme_tarihi));
                });
            })
            .subscribe();
        return () => supabase.removeChannel(ch);
    }, [selectedUser, canView]);

    // inbox (üstte küçük liste)
    const inboxList = useMemo(() => {
        const qq = q.trim().toLowerCase();
        const arr = !qq
            ? rows
            : rows.filter((r) => {
                const hay = `${r.id} ${r.baslik} ${r.talep_eden_kullanici || ""} ${r.talep_eden_email || ""}`.toLowerCase();
                return hay.includes(qq);
            });
        return arr.sort((a, b) => ts(b.guncelleme_tarihi) - ts(a.guncelleme_tarihi)).slice(0, 30);
    }, [rows, q]);

    /* ---------- Analitik veri ---------- */

    // Tamamlanan talepler (bitis_tarihi bazlı)
    const completed = useMemo(
        () => rows.filter(r => r.durum === STATUS.Tamamlandi && getDoneTs(r)),
        [rows]
    );

    // Lead time trend (tamamlanan, gün • son 60) — bitis_tarihi bazlı
    const leadTimeTrend = useMemo(() => {
        const since = Date.now() - 60 * day;
        const byDay = new Map();
        completed.forEach(r => {
            const done = getDoneTs(r);            // bitis_tarihi öncelikli
            if (done < since) return;
            const created = ts(r.olusturma_tarihi || r.giristarihi || r.acilis_tarihi);
            const lt = clampNum((done - created) / day);
            const key = toDayKey(done);
            if (!byDay.has(key)) byDay.set(key, []);
            byDay.get(key).push(lt);
        });
        return Array.from(byDay.entries())
            .sort((a, b) => a[0] - b[0])
            .map(([t, vals]) => ({
                date: new Date(t).toLocaleDateString(),
                lead: vals.reduce((s, n) => s + n, 0) / vals.length
            }));
    }, [completed]);

    // İlk cevap süresi histogram (saat) — ilk cevap = ilk_aksiyon/ilk_yanit/first_response_at; referans = oluşturma
    const responseHist = useMemo(() => {
        const buckets = [
            { label: "<1s", min: 0, max: 1 },
            { label: "1–4s", min: 1, max: 4 },
            { label: "4–8s", min: 4, max: 8 },
            { label: "8–24s", min: 8, max: 24 },
            { label: "24–48s", min: 24, max: 48 },
            { label: ">48s", min: 48, max: Infinity },
        ].map(b => ({ ...b, count: 0 }));
        rows.forEach(r => {
            const created = ts(r.olusturma_tarihi || r.giristarihi || r.acilis_tarihi);
            const first = getFirstResponseTs(r);
            if (!created || !first || first < created) return;
            const hrs = (first - created) / (60 * 60 * 1000);
            const b = buckets.find(x => hrs >= x.min && hrs < x.max);
            if (b) b.count++;
        });
        return buckets.map(b => ({ label: b.label, value: b.count }));
    }, [rows]);

    // Haftalık throughput (tamamlanan/hafta • son 10) — bitis_tarihi bazlı
    const weeklyThroughput = useMemo(() => {
        const weeks = [];
        // pazartesi başlangıç
        const startWeek = (d) => {
            const x = new Date(d);
            const dif = (x.getDay() + 6) % 7;
            x.setHours(0, 0, 0, 0);
            x.setDate(x.getDate() - dif);
            return x;
        };
        let cur = startWeek(new Date());
        for (let i = 0; i < 10; i++) {
            const end = new Date(cur);
            const start = new Date(cur); start.setDate(start.getDate() - 7);
            weeks.unshift({
                label: `${start.getDate()}.${start.getMonth() + 1}`,
                start: start.getTime(),
                end: end.getTime(),
                count: 0
            });
            cur = start;
        }
        completed.forEach(r => {
            const done = getDoneTs(r); // bitis_tarihi öncelikli
            weeks.forEach(w => { if (done >= w.start && done < w.end) w.count++; });
        });
        return weeks;
    }, [completed]);

    // Aging WIP (açık işlerin "son aktivite"ye göre yaşı, gün • top 10) — guncelleme_tarihi bazlı
    const agingWip = useMemo(() => {
        const open = rows.filter(r => r.durum !== STATUS.Tamamlandi && r.durum !== "Reddedildi");
        const today = Date.now();
        return open
            .map(r => ({
                label: `#${r.id}`,
                age: clampNum((today - getLastActivityTs(r)) / day), // son güncellenmeden beri geçen gün
            }))
            .sort((a, b) => b.age - a.age)
            .slice(0, 10);
    }, [rows]);

    if (!canView) {
        return (
            <div className="mx mx-contrast">
                <div className="mx-top sticky modern">
                    <div className="brand">
                        <div className="logo">👀</div>
                        <div className="bt"><h1>Takip Ekranı</h1><span className="muted">Erişim yetkiniz yok</span></div>
                    </div>
                </div>
                <div className="mx-alert err">Bu ekran yalnızca <b>{ALLOWED_VIEWER}</b> için yetkilidir.</div>
            </div>
        );
    }

    return (
        <div className="mx mx-contrast" style={{ gap: 12 }}>
            {/* Üst bar */}
            <div className="mx-top sticky modern" style={{ marginBottom: 8 }}>
                <div className="brand">
                    <div className="logo">📊</div>
                    <div className="bt">
                        <h1 style={{ color: "#F9FAFB" }}>{selectedUser} — Canlı Inbox & Analitik</h1>
                        <span className="muted">Üstte canlı inbox; sağda/alta canlı grafikler.</span>
                    </div>
                </div>

                <div className="cmd">
                    <select
                        className="input"
                        value={selectedUser}
                        onChange={(e) => setSelectedUser(e.target.value)}
                        style={{ minWidth: 260 }}
                        aria-label="Kullanıcı seç"
                    >
                        {MONITORED_USERS.map(u => <option key={u.name} value={u.name}>{u.name}</option>)}
                    </select>

                    <div className="search-wrap">
                        <input
                            className="search"
                            placeholder="Ara: #id, başlık, kişi…"
                            value={q}
                            onChange={(e) => setQ(e.target.value)}
                        />
                    </div>

                    <label className="toggle">
                        <input type="checkbox" checked={onlySLA} onChange={(e) => setOnlySLA(e.target.checked)} />
                        <span>SLA (&lt;24s)</span>
                    </label>

                    <button className="btn primary" onClick={() => navigate("/inbox?assignee=" + encodeURIComponent(selectedUser))}>
                        {selectedUser.split(" ")[0]} — Inbox’ı (tam ekran)
                    </button>
                </div>
            </div>

            {/* Üst sıra: Sol canlı inbox (liste) + Sağ lead time trend */}
            <div className="board" style={{ gridTemplateColumns: "minmax(420px, 1.1fr) minmax(420px, 1fr)", gap: 12 }}>
                <div className="col modern">
                    <div className="col-head mini"><span className="dot" /><h4 className="mini-title">Canlı Inbox (son 30)</h4><span className="count">{inboxList.length}</span></div>
                    <ul className="col-body" style={{ listStyle: "none", margin: 0, padding: 0, maxHeight: 480, overflow: "auto" }}>
                        {inboxList.map(r => (
                            <li key={r.id} style={{ marginBottom: 8 }}>
                                <button
                                    className="card modern"
                                    onClick={() => navigate(`/inbox?assignee=${encodeURIComponent(r.atanan_email || "")}`)}
                                    title={`#${r.id} — ${r.baslik}\n${STATUS_DISPLAY[r.durum] || "—"} • ${fmtDT(r.guncelleme_tarihi)}`}
                                    style={{ width: "100%", textAlign: "left" }}
                                >
                                    <div className="c-title line-1">#{r.id} — {r.baslik}</div>
                                    <div className="c-desc line-2">{r.talep_eden_kullanici || r.talep_eden_email || "—"}</div>
                                    <div className="c-meta"><b>{STATUS_DISPLAY[r.durum] || "—"}</b> • <span className="time">{fmtDT(r.guncelleme_tarihi)}</span></div>
                                </button>
                            </li>
                        ))}
                        {inboxList.length === 0 && <div className="col-empty">Kayıt yok</div>}
                    </ul>
                </div>

                <ChartCard title="Lead Time Trend (tamamlanan, gün • son 60)">
                    <ResponsiveContainer width="100%" height={480}>
                        <LineChart data={leadTimeTrend}>
                            <CartesianGrid stroke="rgba(255,255,255,.06)" />
                            <XAxis dataKey="date" tick={{ fill: "#d1d5db", fontSize: 12 }} />
                            <YAxis tick={{ fill: "#d1d5db", fontSize: 12 }} />
                            <Tooltip contentStyle={{ background: "#0b0b10", border: "1px solid rgba(255,255,255,.08)", color: "#e5e7eb" }} />
                            <Legend />
                            <Line type="monotone" dataKey="lead" stroke="#60A5FA" strokeWidth={2} dot={false} />
                        </LineChart>
                    </ResponsiveContainer>
                </ChartCard>
            </div>

            {/* Alt sıra: Response histogram + throughput + Aging WIP */}
            <div className="board" style={{ gridTemplateColumns: "repeat(3, minmax(320px, 1fr))", gap: 12 }}>
                <ChartCard title="İlk Cevap Süresi (saat) — Histogram">
                    <ResponsiveContainer width="100%" height={280}>
                        <BarChart data={responseHist}>
                            <CartesianGrid stroke="rgba(255,255,255,.06)" />
                            <XAxis dataKey="label" tick={{ fill: "#d1d5db", fontSize: 12 }} />
                            <YAxis allowDecimals={false} tick={{ fill: "#d1d5db", fontSize: 12 }} />
                            <Tooltip contentStyle={{ background: "#0b0b10", border: "1px solid rgba(255,255,255,.08)", color: "#e5e7eb" }} />
                            <Bar dataKey="value" fill="#34D399" />
                        </BarChart>
                    </ResponsiveContainer>
                </ChartCard>

                <ChartCard title="Throughput (tamamlanan/hafta • son 10)">
                    <ResponsiveContainer width="100%" height={280}>
                        <AreaChart data={weeklyThroughput}>
                            <defs>
                                <linearGradient id="g1" x1="0" y1="0" x2="0" y2="1">
                                    <stop offset="0%" stopColor="#8B5CF6" stopOpacity={0.8} />
                                    <stop offset="100%" stopColor="#8B5CF6" stopOpacity={0.2} />
                                </linearGradient>
                            </defs>
                            <CartesianGrid stroke="rgba(255,255,255,.06)" />
                            <XAxis dataKey="label" tick={{ fill: "#d1d5db", fontSize: 12 }} />
                            <YAxis allowDecimals={false} tick={{ fill: "#d1d5db", fontSize: 12 }} />
                            <Tooltip contentStyle={{ background: "#0b0b10", border: "1px solid rgba(255,255,255,.08)", color: "#e5e7eb" }} />
                            <Area type="monotone" dataKey="count" stroke="#8B5CF6" fill="url(#g1)" />
                        </AreaChart>
                    </ResponsiveContainer>
                </ChartCard>

                <ChartCard title="Aging WIP (son aktiviteye göre gün • top 10)">
                    <ResponsiveContainer width="100%" height={280}>
                        <BarChart data={agingWip}>
                            <CartesianGrid stroke="rgba(255,255,255,.06)" />
                            <XAxis dataKey="label" tick={{ fill: "#d1d5db", fontSize: 11 }} interval={0} angle={-25} dy={10} />
                            <YAxis tick={{ fill: "#d1d5db", fontSize: 12 }} />
                            <Tooltip contentStyle={{ background: "#0b0b10", border: "1px solid rgba(255,255,255,.08)", color: "#e5e7eb" }} />
                            <Bar dataKey="age" fill="#F59E0B" />
                        </BarChart>
                    </ResponsiveContainer>
                </ChartCard>
            </div>
        </div>
    );
}

/* ---- Görsel yardımcı bileşen ---- */
function ChartCard({ title, children }) {
    return (
        <div className="col modern">
            <div className="col-head mini">
                <span className="dot" />
                <h4 className="mini-title">{title}</h4>
            </div>
            <div className="col-body" style={{ height: 300 }}>{children}</div>
        </div>
    );
}
