import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Inbox from "./pages/Inbox";
import NewRequest from "./pages/NewRequest";
import supabase from "./supabaseClient"; // default import


export default function App() {
    const [user, setUser] = useState(null);
    const [role, setRole] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Oturum var m� kontrol et
        supabase.auth.getUser().then(async ({ data }) => {
            if (data.user) {
                setUser(data.user);

                // Kullan�c�n�n rol�n� �ek
                const { data: profile } = await supabase
                    .from("profiles")
                    .select("role")
                    .eq("id", data.user.id)
                    .single();

                setRole(profile?.role || null);
            }
            setLoading(false);
        });

        // Oturum de�i�ti�inde dinle
        const { data: listener } = supabase.auth.onAuthStateChange(
            async (event, session) => {
                if (session?.user) {
                    setUser(session.user);
                    const { data: profile } = await supabase
                        .from("profiles")
                        .select("role")
                        .eq("id", session.user.id)
                        .single();
                    setRole(profile?.role || null);
                } else {
                    setUser(null);
                    setRole(null);
                }
            }
        );

        return () => {
            listener.subscription.unsubscribe();
        };
    }, []);

    if (loading) return <div>Y�kleniyor...</div>;

    return (
        <BrowserRouter>
            <Routes>
                {/* Login */}
                <Route path="/login" element={<Login />} />

                {/* Talep kar��layan (agent / approver) */}
                <Route
                    path="/inbox"
                    element={
                        user && (role === "agent" || role === "approver") ? (
                            <Inbox />
                        ) : (
                            <Navigate to="/login" replace />
                        )
                    }
                />

                {/* Talep olu�turan */}
                <Route
                    path="/new-request"
                    element={
                        user && role === "requester" ? (
                            <NewRequest />
                        ) : (
                            <Navigate to="/login" replace />
                        )
                    }
                />

                {/* Varsay�lan y�nlendirme */}
                <Route
                    path="/"
                    element={
                        user ? (
                            role === "agent" || role === "approver" ? (
                                <Navigate to="/inbox" replace />
                            ) : (
                                <Navigate to="/new-request" replace />
                            )
                        ) : (
                            <Navigate to="/login" replace />
                        )
                    }
                />

                <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
        </BrowserRouter>
    );
}
