// src/App.jsx
import React, { useMemo } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Inbox from "./pages/Inbox";
import NewRequest from "./pages/NewRequest";
import Requests from "./pages/Requests";

function RequireAuth({ children }) {
    const isAuthed = !!localStorage.getItem("auth_token");
    return isAuthed ? children : <Navigate to="/login" replace />;
}

export default function App() {
    // localStorage'daki profil bilgisi
    const profile = useMemo(() => {
        try {
            return JSON.parse(localStorage.getItem("user_profile") || "{}");
        } catch {
            return {};
        }
    }, []);

    const birim = profile?.birim || "";
    const isAuthed = !!localStorage.getItem("auth_token");

    // Giriş yaptıktan sonra yönlenecek varsayılan sayfa
    const defaultAfterLogin =
        birim === "İŞ VE SÜREÇ GELİŞTİRME" ? "/requests" : "/newrequest";

    // İlk açılışta login'e zorla (auth yoksa)
    const defaultRoute = isAuthed ? defaultAfterLogin : "/login";

    return (
        <BrowserRouter>
            <Routes>
                {/* Login: zaten girişliyse varsayılan sayfaya yönlendir */}
                <Route
                    path="/login"
                    element={
                        isAuthed ? <Navigate to={defaultAfterLogin} replace /> : <Login />
                    }
                />

                {/* Korumalı sayfalar */}
                <Route
                    path="/requests"
                    element={
                        <RequireAuth>
                            <Requests />
                        </RequireAuth>
                    }
                />

                <Route
                    path="/newrequest"
                    element={
                        <RequireAuth>
                            <NewRequest />
                        </RequireAuth>
                    }
                />

                <Route
                    path="/inbox"
                    element={
                        <RequireAuth>
                            <Inbox />
                        </RequireAuth>
                    }
                />

                {/* default yönlendirme */}
                <Route path="*" element={<Navigate to={defaultRoute} replace />} />
            </Routes>
        </BrowserRouter>
    );
}
