// src/pages/Requests.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import supabase from "../supabaseClient";
import "./Requests.css";

const STATUS_LABEL = {
    Yeni: "Yeni",
    SirayaAlindi: "SÄ±raya AlÄ±ndÄ±",
    IslemeAlindi: "Ä°ÅŸleme AlÄ±ndÄ±",
    TestEdiliyor: "Test Ediliyor",
    Tamamlandi: "TamamlandÄ±",
};

// DurumlarÄ±n gÃ¶rÃ¼nmesi/sÄ±ralanmasÄ± iÃ§in sabit sÄ±ra (Inbox ile uyumlu)
const STATUS_ORDER = ["Yeni", "SirayaAlindi", "IslemeAlindi", "TestEdiliyor", "Tamamlandi"];
const statusRank = (s) => {
    const i = STATUS_ORDER.indexOf(s);
    return i === -1 ? 999 : i;
};

export default function Requests() {
    const navigate = useNavigate();

    // profil
    const profile = useMemo(() => {
        try {
            return JSON.parse(localStorage.getItem("user_profile") || "{}");
        } catch {
            return {};
        }
    }, []);
    const userEmail = profile?.email || "";
    const userName = profile?.kullanici || userEmail;

    // state
    const [rows, setRows] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const [q, setQ] = useState("");
    const [onlyOpen, setOnlyOpen] = useState(false); // tamamlananlarÄ± gizle

    useEffect(() => {
        // auth yoksa login'e
        if (!localStorage.getItem("auth_token")) {
            navigate("/login", { replace: true });
            return;
        }
        if (userEmail) {
            fetchData();
        } else {
            setLoading(false);
            setError("KullanÄ±cÄ± e-postasÄ± bulunamadÄ±.");
        }
        // eslint-disable-next-line
    }, [userEmail, onlyOpen]);

    async function fetchData() {
        try {
            setLoading(true);
            setError("");

            let query = supabase
                .from("talepler")
                .select(`
          id, baslik, aciklama, oncelik, durum,
          talep_eden_email, atanan_email,
          guncelleme_tarihi, bitis_tarihi, sla_son_tarih,
          kolon_sira, atanan_sira, global_sira,
          talep_eden_kullanici, atanan_kullanici
        `)
                .eq("talep_eden_email", userEmail);

            if (onlyOpen) {
                // Tamamlandi/Reddedildi dÄ±ÅŸÄ±ndakiler
                query = query.not("durum", "in", '("Tamamlandi","Reddedildi")');
            }

            // Sunucu tarafÄ±nda kaba bir sÄ±ralama
            query = query.order("guncelleme_tarihi", { ascending: false });

            const { data, error } = await query;
            if (error) throw error;

            setRows(data || []);
        } catch (err) {
            console.error("fetch error (Supabase):", err);
            setError("Talepler alÄ±namadÄ±.");
        } finally {
            setLoading(false);
        }
    }

    // Arama filtresi
    const filtered = rows.filter((r) => {
        if (!q) return true;
        const hay = `${r.id} ${r.baslik} ${r.aciklama || ""} ${r.talep_eden_kullanici || ""} ${r.atanan_kullanici || ""} ${r.durum || ""}`.toLowerCase();
        return hay.includes(q.toLowerCase());
    });

    // SÄ±ralama: Durum â†’ (SÄ±raya AlÄ±ndÄ± ise) KiÅŸisel sÄ±ra â†’ Kolon sÄ±rasÄ± â†’ Son gÃ¼ncelleme (desc) â†’ id
    function dateNum(v) {
        try {
            return new Date(v).getTime() || 0;
        } catch {
            return 0;
        }
    }
    const sorted = [...filtered].sort((a, b) => {
        const sa = statusRank(a.durum), sb = statusRank(b.durum);
        if (sa !== sb) return sa - sb;

        // YalnÄ±zca SÄ±raya AlÄ±ndÄ± durumunda atanan_sira ile sÄ±rala; diÄŸer durumlarda etkisiz
        const qa = a.durum === "SirayaAlindi" ? (a.atanan_sira ?? 1e9) : 1e9;
        const qb = b.durum === "SirayaAlindi" ? (b.atanan_sira ?? 1e9) : 1e9;
        if (qa !== qb) return qa - qb;

        const ka = a.kolon_sira ?? 1e9;
        const kb = b.kolon_sira ?? 1e9;
        if (ka !== kb) return ka - kb;

        const ta = dateNum(b.guncelleme_tarihi) - dateNum(a.guncelleme_tarihi);
        if (ta !== 0) return ta;

        return (a.id ?? 0) - (b.id ?? 0);
    });

    function fmtDateTime(ts) {
        if (!ts) return "â€”";
        try {
            return new Date(ts).toLocaleString();
        } catch {
            return ts;
        }
    }

    function handleLogout() {
        const ok = window.confirm("Oturumu kapatmak istediÄŸinize emin misiniz?");
        if (!ok) return;
        localStorage.removeItem("auth_token");
        localStorage.removeItem("user_profile");
        ["nr_title", "nr_desc", "nr_prio", "nr_due", "nr_assignee"].forEach((k) => localStorage.removeItem(k));
        navigate("/login", { replace: true });
