// src/pages/Requests.jsx
import React, { useEffect, useState } from "react";
import supabase from "../supabaseClient";

export default function Requests() {
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    // LocalStorage'dan kullanƒ±cƒ± mailini al
    const profile = JSON.parse(localStorage.getItem("user_profile") || "{}");
    const userEmail = profile?.email || "";

    async function fetchRequests() {
        try {
            setLoading(true);
            setError("");

            const { data, error } = await supabase
                .from("talepler") // ‚úÖ tablo adƒ± d√ºzeltildi
                .select("*")
                .eq("talep_eden_email", userEmail)
                .order("guncelleme_tarihi", { ascending: false });

            if (error) throw error;

            setRequests(data || []);
        } catch (err) {
            console.error("fetchRequests error:", err);
            setError("Talepler alƒ±namadƒ±");
        } finally {
            setLoading(false);
        }
    }

    useEffect(() => {
        if (userEmail) {
            fetchRequests();
        }
    }, [userEmail]);

    if (!userEmail) {
        return <div className="warning">‚ö†Ô∏è Kullanƒ±cƒ± bulunamadƒ±, l√ºtfen tekrar giri≈ü yapƒ±n.</div>;
    }

    return (
        <div className="page">
            <h1>üìã Taleplerim</h1>

            {loading && <p>Y√ºkleniyor...</p>}
            {error && <p className="error">{error}</p>}

            {!loading && !error && requests.length === 0 && (
                <p>Hen√ºz talebiniz yok.</p>
            )}

            {!loading && !error && requests.length > 0 && (
                <table className="req-table">
                    <thead>
                        <tr>
                            <th>Ba≈ülƒ±k</th>
                            <th>Durum</th>
                            <th>√ñncelik</th>
                            <th>Atanan</th>
                            <th>G√ºncelleme</th>
                        </tr>
                    </thead>
                    <tbody>
                        {requests.map((req) => (
                            <tr key={req.id}>
                                <td>{req.baslik}</td>
                                <td>{req.durum}</td>
                                <td>{req.oncelik}</td>
                                <td>{req.atanan_kullanici || "-"}</td>
                                <td>{req.guncelleme_tarihi || "-"}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}
