import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";

export default function Login() {
    const nav = useNavigate();
    const [email, setEmail] = useState("");
    const [pw, setPw] = useState("");
    const [showPw, setShowPw] = useState(false);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErr("");
        if (!email || !pw) {
            setErr("E-posta ve parola zorunludur");
            return;
        }
        try {
            setLoading(true);
            if (email === "admin@example.com" && pw === "123456") {
                const token = "mock-token-" + Date.now();
                localStorage.setItem("auth_token", token);
                console.log("Login OK, token:", token);
                alert("Giri� ba�ar�l�!");
            } else {
                setErr("E-posta veya parola hatal�");
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-wrap">
            <form onSubmit={handleSubmit} className="login-card">
                <h1>Talep Sistemi</h1>
                <p className="muted">Giri� yap�n</p>

                <label className="field">
                    <span>E-posta</span>
                    <input
                        type="email"
                        placeholder="admin@example.com"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </label>

                <label className="field">
                    <span>Parola</span>
                    <div className="pw">
                        <input
                            type={showPw ? "text" : "password"}
                            placeholder="������"
                            value={pw}
                            onChange={(e) => setPw(e.target.value)}
                        />
                        <button
                            type="button"
                            onClick={() => setShowPw((v) => !v)}
                            className="ghost-btn"
                        >
                            {showPw ? "Gizle" : "G�ster"}
                        </button>
                    </div>
                </label>

                {err && <div className="error">{err}</div>}

                <button type="submit" disabled={loading} className="primary-btn">
                    {loading ? "Giri� yap�l�yor�" : "Giri� Yap"}
                </button>

                <div className="hint">
                    Demo: <code>admin@example.com</code> / <code>123456</code>
                </div>
            </form>
        </div>
    );
}
