// src/App.jsx
import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Inbox from "./pages/Inbox";
import NewRequest from "./pages/NewRequest";
import Requests from "./pages/Requests";
import TeamMonitor from "./pages/TeamMonitor";

function RequireAuth({ children }) {
    const isAuthed = !!localStorage.getItem("auth_token");
    return isAuthed ? children : <Navigate to="/login" replace />;
}

// ---- Yardımcılar ----
function getUserProfile() {
    try { return JSON.parse(localStorage.getItem("user_profile") || "{}"); }
    catch { return {}; }
}
function getUserNameUpper() {
    const p = getUserProfile();
    return String(p?.kullanici || "").trim().toLocaleUpperCase("tr-TR");
}

const ONUR = "ONUR KEREM ÖZTÜRK";
const INBOX_USERS = new Set([
    "GÖRKEM ÇADIRCI",
    "YAĞIZ EFE BULUTCU",
    "FURKAN BİLGİLİ",
]);

function HomeRedirect() {
    const isAuthed = !!localStorage.getItem("auth_token");
    if (!isAuthed) return <Login />;

    const nameU = getUserNameUpper();
    if (nameU === ONUR) return <Navigate to="/team-monitor" replace />;
    if (INBOX_USERS.has(nameU)) return <Navigate to="/inbox" replace />;
    return <Navigate to="/requests" replace />;
}

export default function App() {
    return (
        <BrowserRouter>
            <Routes>
                {/* Giriş: kullanıcıya göre yönlendir */}
                <Route path="/" element={<HomeRedirect />} />
                <Route path="/login" element={<Login />} />

                {/* Korumalı sayfalar */}
                <Route
                    path="/requests"
                    element={
                        <RequireAuth>
                            <Requests />
                        </RequireAuth>
                    }
                />
                <Route
                    path="/newrequest"
                    element={
                        <RequireAuth>
                            <NewRequest />
                        </RequireAuth>
                    }
                />
                <Route
                    path="/inbox"
                    element={
                        <RequireAuth>
                            {
                                INBOX_USERS.has(getUserNameUpper())
                                    ? <Inbox />
                                    : <Navigate to="/requests" replace />
                            }
                        </RequireAuth>
                    }
                />
                <Route
                    path="/team-monitor"
                    element={
                        <RequireAuth>
                            {
                                getUserNameUpper() === ONUR
                                    ? <TeamMonitor />
                                    : <Navigate to="/requests" replace />
                            }
                        </RequireAuth>
                    }
                />

                {/* Bilinmeyen rota */}
                <Route path="*" element={<Navigate to="/login" replace />} />
            </Routes>
        </BrowserRouter>
    );
}
