// src/App.jsx
import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Inbox from "./pages/Inbox";
import NewRequest from "./pages/NewRequest";
import Requests from "./pages/Requests";
import TeamMonitor from "./pages/TeamMonitor";

function RequireAuth({ children }) {
    const isAuthed = !!localStorage.getItem("auth_token");
    return isAuthed ? children : <Navigate to="/login" replace />;
}

//  Yard覺mc覺: localStorage'dan kullan覺c覺 ad覺n覺 g羹venle 癟ek
function getUserName() {
    try {
        const p = JSON.parse(localStorage.getItem("user_profile") || "{}");
        return String(p?.kullanici || "").trim();
    } catch {
        return "";
    }
}

export default function App() {
    const currentUserName = getUserName();
    const isOnur =
        currentUserName.toLocaleUpperCase("tr-TR") === "ONUR KEREM ZTRK";

    return (
        <BrowserRouter>
            <Routes>
                {/* Login */}
                <Route path="/" element={<Login />} />
                <Route path="/login" element={<Login />} />

                {/* Korumal覺 sayfalar */}
                <Route
                    path="/requests"
                    element={
                        <RequireAuth>
                            {isOnur ? <Navigate to="/team-monitor" replace /> : <Requests />}
                        </RequireAuth>
                    }
                />
                <Route
                    path="/newrequest"
                    element={
                        <RequireAuth>
                            {isOnur ? <Navigate to="/team-monitor" replace /> : <NewRequest />}
                        </RequireAuth>
                    }
                />
                <Route
                    path="/inbox"
                    element={
                        <RequireAuth>
                            {isOnur ? <Navigate to="/team-monitor" replace /> : <Inbox />}
                        </RequireAuth>
                    }
                />
                <Route
                    path="/team-monitor"
                    element={
                        <RequireAuth>
                            <TeamMonitor />
                        </RequireAuth>
                    }
                />

                {/* Bilinmeyen rota -> login */}
                <Route path="*" element={<Navigate to="/login" replace />} />
            </Routes>
        </BrowserRouter>
    );
}
