import React from "react";
import { BrowserRouter, Routes, Route, Navigate, useLocation } from "react-router-dom";
import Login from "./pages/Login";
import Inbox from "./pages/Inbox";
import NewRequest from "./pages/NewRequest";
import Requests from "./pages/Requests"; // ✅ takip ekranı

// --- yardımcı fonksiyonlar ---
function getProfile() {
    try { return JSON.parse(localStorage.getItem("user_profile") || "null"); }
    catch { return null; }
}
function isAuthed() {
    return !!localStorage.getItem("auth_token");
}
function trUp(s) { return (s || "").trim().toLocaleUpperCase("tr-TR"); }
function isISG() {
    const p = getProfile();
    return p && trUp(p.birim) === "İŞ VE SÜREÇ GELİŞTİRME";
}

// login koruması
function RequireAuth({ children }) {
    const location = useLocation();
    if (!isAuthed()) {
        return <Navigate to="/login" replace state={{ from: location }} />;
    }
    return children;
}

// köke geleni direkt newrequest’e gönderelim
function RootRedirect() {
    if (!isAuthed()) return <Navigate to="/login" replace />;
    return <Navigate to="/newrequest" replace />;
}

export default function App() {
    return (
        <BrowserRouter>
            <Routes>
                {/* herkese açık */}
                <Route path="/login" element={<Login />} />

                {/* kök -> newrequest */}
                <Route path="/" element={<RootRedirect />} />

                {/* herkes talep açabilir */}
                <Route
                    path="/newrequest"
                    element={
                        <RequireAuth>
                            <NewRequest />
                        </RequireAuth>
                    }
                />

                {/* takip ekranları */}
                <Route
                    path="/inbox"
                    element={
                        <RequireAuth>
                            <Inbox />
                        </RequireAuth>
                    }
                />
                <Route
                    path="/requests"
                    element={
                        <RequireAuth>
                            <Requests />
                        </RequireAuth>
                    }
                />

                {/* bilinmeyen rota */}
                <Route path="*" element={<Navigate to="/login" replace />} />
            </Routes>
        </BrowserRouter>
    );
}
