import React, { useState } from "react";
import supabase from "../supabaseClient";
import "./Login.css";

export default function Login() {
    const [email, setEmail] = useState("");
    const [pw, setPw] = useState("");
    const [showPw, setShowPw] = useState(false);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErr("");

        if (!email || !pw) {
            setErr("E-posta ve parola zorunludur");
            return;
        }

        try {
            setLoading(true);

            // ✅ SQL yok — yerleşik Supabase Auth
            const { data, error } = await supabase.auth.signInWithPassword({
                email,
                password: pw,
            });

            if (error) {
                // Örn: "Invalid login credentials", "Email not confirmed"
                setErr(error.message || "Giriş başarısız");
                return;
            }

            const { session, user } = data || {};
            // İstersen token'ı localStorage'a koy
            localStorage.setItem("auth_token", session?.access_token || "");

            // Kullanıcıyı karşılama (user_metadata'ya profil bilgisi koyduysan buradan gelir)
            const meta = user?.user_metadata || {};
            alert(`Hoş geldin ${meta.kullanici || user?.email}!`);

            // TODO: burada dashboard'a yönlendirebilirsin (router eklediğinde)
            // navigate("/");
        } catch (e) {
            console.error(e);
            setErr("Beklenmeyen bir hata oluştu");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-wrap">
            <form className="login-card" onSubmit={handleSubmit}>
                <h1>Talep Sistemi</h1>
                <p className="muted">Giriş yapın</p>

                <label className="field">
                    <span>E-posta</span>
                    <input
                        type="email"
                        placeholder="ornek@firma.com"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        autoComplete="email"
                    />
                </label>

                <label className="field">
                    <span>Parola</span>
                    <div className="pw">
                        <input
                            type={showPw ? "text" : "password"}
                            placeholder="••••••"
                            value={pw}
                            onChange={(e) => setPw(e.target.value)}
                            autoComplete="current-password"
                        />
                        <button
                            type="button"
                            className="ghost-btn"
                            onClick={() => setShowPw((v) => !v)}
                        >
                            {showPw ? "Gizle" : "Göster"}
                        </button>
                    </div>
                </label>

                {err && <div className="error">{err}</div>}

                <button className="primary-btn" disabled={loading}>
                    {loading ? "Giriş yapılıyor…" : "Giriş Yap"}
                </button>
            </form>
        </div>
    );
}
