import React, { useState } from "react";
import supabase from "../supabaseClient";
import "./Login.css";

export default function Login() {
    const [email, setEmail] = useState("");
    const [pw, setPw] = useState("");
    const [showPw, setShowPw] = useState(false);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");

    async function handleSubmit(e) {
        e.preventDefault();
        setErr("");

        const emailTrim = (email || "").trim().toLowerCase();
        const pwTrim = (pw || "").trim();
        if (!emailTrim || !pwTrim) return setErr("E-posta ve parola zorunludur");

        try {
            setLoading(true);

            const { data, error } = await supabase
                .from("login")
                .select("id, email, kullanici, birim, title")
                .eq("email", emailTrim)
                .eq("sifre", pwTrim)
                .maybeSingle();

            if (error) {
                console.error("DB error:", error);
                setErr("GiriÅŸ yapÄ±lamadÄ±. LÃ¼tfen tekrar deneyin.");
                return;
            }
            if (!data) {
                setErr("E-posta veya parola hatalÄ±");
                return;
            }

            localStorage.setItem("auth_token", "ok-" + Date.now());
            localStorage.setItem("user_profile", JSON.stringify(data));
            alert(`HoÅŸ geldin ${data.kullanici || data.email}!`);
            // TODO: navigate("/");
        } catch (e2) {
            console.error(e2);
            setErr("Beklenmeyen bir hata oluÅŸtu");
        } finally {
            setLoading(false);
        }
    }

    return (
        <div className="auth">
            <div className="auth-card">
                <div className="brand">
                    <div className="logo" aria-hidden="true">ðŸ§­</div>
                    <div className="title">
                        <h1>Talep Sistemi</h1>
                        <p className="muted">HesabÄ±nÄ±za giriÅŸ yapÄ±n</p>
                    </div>
                </div>

                <form onSubmit={handleSubmit} className="form">
                    <label className="field">
                        <span className="label">E-posta</span>
                        <input
                            type="email"
                            inputMode="email"
                            autoCapitalize="none"
                            autoCorrect="off"
                            placeholder="ornek@firma.com"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            autoComplete="email"
                            className="input"
                        />
                    </label>

                    <label className="field">
                        <span className="label">Parola</span>
                        <div className="pw">
                            <input
                                type={showPw ? "text" : "password"}
                                placeholder="â€¢â€¢â€¢â€¢â€¢â€¢"
                                value={pw}
                                onChange={(e) => setPw(e.target.value)}
                                autoComplete="current-password"
                                className="input"
                            />
                            <button
                                type="button"
                                className="ghost-btn"
                                onClick={() => setShowPw((v) => !v)}
                                aria-label={showPw ? "ParolayÄ± gizle" : "ParolayÄ± gÃ¶ster"}
                            >
                                {showPw ? "Gizle" : "GÃ¶ster"}
                            </button>
                        </div>
                    </label>

                    {err && <div className="error">{err}</div>}

                    <button className="primary-btn" disabled={loading}>
                        {loading ? <span className="spinner" /> : "GiriÅŸ Yap"}
                    </button>
                </form>

                <div className="footer">
                    <span className="muted">Â© {new Date().getFullYear()} Biriminiz</span>
                </div>
            </div>

            <div className="auth-aside" aria-hidden="true">
                <div className="aside-content">
                    <h2>HoÅŸ geldiniz</h2>
                    <p>Taleplerinizi kolayca oluÅŸturun, takip edin ve yÃ¶netin.</p>
                </div>
            </div>
        </div>
    );
}
