// src/pages/Inbox.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";
import supabase from "../supabaseClient";
import "./Inbox.css";

/**
 * Realtime:
 *   alter publication supabase_realtime add table public.talepler;
 *
 * Liste görünümünde sürükle-bırak -> LOKAL sırayı günceller.
 * Kullanıcı "Sırayı Kaydet" derse:
 *   - mineOnly=false => set_global_order(ids)
 *   - mineOnly=true  => set_assignee_order(myEmail, ids)
 */

/* ---- DURUM KODLARI (DB ile birebir) ---- */
const STATUS = {
    Yeni: "Yeni",
    SirayaAlindi: "SirayaAlindi",
    IslemeAlindi: "IslemeAlindi",
    TestEdiliyor: "TestEdiliyor",
    Tamamlandi: "Tamamlandi",
    // Reddedildi: "Reddedildi",
};

/* UI etiketleri */
const STATUS_DISPLAY = {
    [STATUS.Yeni]: "Yeni",
    [STATUS.SirayaAlindi]: "Sıraya Alındı",
    [STATUS.IslemeAlindi]: "İşleme Alındı",
    [STATUS.TestEdiliyor]: "Test Ediliyor",
    [STATUS.Tamamlandi]: "Tamamlandı",
};

const BOARD_COLUMNS = [
    { key: STATUS.Yeni, title: STATUS_DISPLAY[STATUS.Yeni] },
    { key: STATUS.SirayaAlindi, title: STATUS_DISPLAY[STATUS.SirayaAlindi] },
    { key: STATUS.IslemeAlindi, title: STATUS_DISPLAY[STATUS.IslemeAlindi] },
    { key: STATUS.TestEdiliyor, title: STATUS_DISPLAY[STATUS.TestEdiliyor] },
    { key: STATUS.Tamamlandi, title: STATUS_DISPLAY[STATUS.Tamamlandi] },
];

const PILL_COLOR = { P1: "p1", P2: "p2", P3: "p3" };

/* ---- YETKİ ---- */
function trUp(s) {
    return (s || "").trim().toLocaleUpperCase("tr-TR");
}
const ALLOWED_NAMES = new Set(["GÖRKEM ÇADIRCI", "FURKAN BİLGİLİ", "YAĞIZ EFE BULUTCU"]);
function computeIsEditor(profile) {
    if (profile && typeof profile.isEditor === "boolean") return profile.isEditor;
    return (
        trUp(profile?.birim) === "İŞ VE SÜREÇ GELİŞTİRME" &&
        ALLOWED_NAMES.has(trUp(profile?.kullanici || profile?.email))
    );
}

/* ---- Tek kayıt sıralama: araya yerleştir (lokal) ---- */
const GAP = 1000;
function between(prev, next) {
    const hasPrev = Number.isFinite(prev);
    const hasNext = Number.isFinite(next);
    if (hasPrev && hasNext) return (prev + next) / 2;
    if (hasPrev && !hasNext) return prev + GAP;
    if (!hasPrev && hasNext) return next - GAP;
    return GAP;
}

export default function InboxModern() {
    const navigate = useNavigate();

    const me = useMemo(() => {
        try {
            return JSON.parse(localStorage.getItem("user_profile") || "{}");
        } catch {
            return {};
        }
    }, []);
    const myEmail = me?.email || "";
    const myName = (me?.kullanici || "").trim();
    const canEdit = useMemo(() => computeIsEditor(me), [me]);

    const [view, setView] = useState("board"); // "board" | "list"
    const [q, setQ] = useState("");
    const [mineOnly, setMineOnly] = useState(false);
    const [onlySLA, setOnlySLA] = useState(false);
    const [loading, setLoading] = useState(false);
    const [saving, setSaving] = useState(false);
    const [dirty, setDirty] = useState(false); // <— Liste'de değişiklik var mı?
    const [msg, setMsg] = useState(""); // bilgi mesajı
    const [err, setErr] = useState("");
    const [rows, setRows] = useState([]);
    const [active, setActive] = useState(null);

    const mineOnlyRef = useRef(mineOnly);
    useEffect(() => {
        mineOnlyRef.current = mineOnly;
    }, [mineOnly]);

    // guard
    useEffect(() => {
        if (!localStorage.getItem("auth_token")) navigate("/login", { replace: true });
    }, [navigate]);

    // data
    useEffect(() => {
        fetchData();
        setDirty(false); // görünüm/filtre değişince "kaydedilmemiş" bayrağını sıfırla
        setMsg("");
        // eslint-disable-next-line
    }, [mineOnly, onlySLA, view]);

    // realtime
    useEffect(() => {
        const channel = supabase
            .channel("talepler-realtime")
            .on(
                "postgres_changes",
                { event: "*", schema: "public", table: "talepler" },
                (payload) => setRows((prev) => applyRealtime(prev, payload, mineOnlyRef.current, view))
            )
            .subscribe();
        return () => supabase.removeChannel(channel);
    }, [view]);

    // görünüm değişince sırala
    useEffect(() => {
        setRows((prev) => sortRows(prev, mineOnly, view));
    }, [mineOnly, view]);

    async function fetchData() {
        if (!myEmail) return;
        setLoading(true);
        setErr("");
        try {
            let query = supabase.from("talepler").select(`
        id, baslik, durum, oncelik,
        talep_eden_email, atanan_email,
        talep_eden_kullanici, atanan_kullanici,
        guncelleme_tarihi, bitis_tarihi, sla_son_tarih, aciklama,
        kolon_sira, atanan_sira, global_sira
      `);

            if (view === "list" && !mineOnly) {
                query = query
                    .order("global_sira", { ascending: true, nullsFirst: true })
                    .order("guncelleme_tarihi", { ascending: false });
            } else if (mineOnly) {
                query = query
                    .eq("atanan_email", myEmail)
                    .order("atanan_sira", { ascending: true, nullsFirst: false })
                    .order("guncelleme_tarihi", { ascending: false });
            } else {
                query = query
                    .order("kolon_sira", { ascending: true, nullsFirst: true })
                    .order("guncelleme_tarihi", { ascending: false });
            }

            if (onlySLA) {
                const t24 = new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString();
                query = query.lt("sla_son_tarih", t24).not("durum", "in", '("Tamamlandi","Reddedildi")');
            }

            const { data, error } = await query.limit(500);
            if (error) throw error;

            setRows(sortRows(data || [], mineOnly, view));
        } catch (e) {
            console.error(e);
            setErr("Kayıtlar çekilemedi.");
        } finally {
            setLoading(false);
        }
    }

    /* ---------- helpers ---------- */
    function groupByStatus(items) {
        const map = Object.fromEntries(BOARD_COLUMNS.map((c) => [c.key, []]));
        items.forEach((i) => {
            (map[i.durum || STATUS.Yeni] ??= []).push(i);
        });
        for (const k of Object.keys(map)) {
            map[k].sort((a, b) => (a.kolon_sira ?? 1e15) - (b.kolon_sira ?? 1e15));
        }
        return map;
    }

    async function updateRow(id, changes) {
        setSaving(true);
        const { error } = await supabase
            .from("talepler")
            .update({ ...changes, guncelleme_tarihi: new Date().toISOString() })
            .eq("id", id);
        setSaving(false);
        if (error) {
            console.error("updateRow error", error);
            setErr(error.message || "Güncelleme başarısız.");
            fetchData();
            return false;
        }
        return true;
    }

    /* ---------- DnD: Kanban (tek kayıt — anında kaydet) ---------- */
    async function onBoardDragEnd(result) {
        if (!canEdit) return;
        const { source, destination, draggableId } = result;
        if (!destination) return;

        const byStatus = groupByStatus([...rows]);
        const srcArr = byStatus[source.droppableId];
        const dstArr = byStatus[destination.droppableId];

        const idx = srcArr.findIndex((i) => String(i.id) === String(draggableId));
        if (idx === -1) return;

        const item = srcArr[idx];
        if (item.atanan_email !== myEmail) {
            setErr("Bu kart size atanmadığı için taşıyamazsınız.");
            return;
        }

        // UI
        srcArr.splice(idx, 1);
        const destIndex = destination.index;
        dstArr.splice(destIndex, 0, item);

        const prev = dstArr[destIndex - 1]?.kolon_sira;
        const next = dstArr[destIndex + 1]?.kolon_sira;
        const newKolonSira = between(prev, next);
        const newDurum = destination.droppableId;

        setRows((prevRows) =>
            sortRows(
                prevRows.map((r) => (r.id === item.id ? { ...r, durum: newDurum, kolon_sira: newKolonSira } : r)),
                mineOnly,
                view
            )
        );

        await updateRow(item.id, { durum: newDurum, kolon_sira: newKolonSira });
    }

    /* ---------- DnD: Liste (tek kayıt — LOKAL değiştir, sonra "Kaydet") ---------- */
    async function onListDragEnd(result) {
        const { destination, draggableId } = result;
        if (!destination) return;

        // GLOBAL görünüm
        if (!mineOnly) {
            if (!canEdit) return;

            const list = [...filtered];
            const from = list.findIndex((i) => String(i.id) === String(draggableId));
            if (from === -1) return;
            const moved = list[from];
            list.splice(from, 1);
            const to = destination.index;
            list.splice(to, 0, moved);

            // 1..N: ekranda görünenlerin tamamını yeniden numaralandır (global_sira)
            setRows((prevRows) => {
                const orderMap = new Map(list.map((r, i) => [r.id, i + 1]));
                const nextRows = prevRows.map((r) =>
                    orderMap.has(r.id) ? { ...r, global_sira: orderMap.get(r.id) } : r
                );
                return sortRows(nextRows, mineOnly, view);
            });

            setDirty(true); // <— kullanıcı kaydetmeli
            setMsg("Global sıralamada kaydedilmemiş değişiklikler var.");
            return;
        }

        // Bana atananlar görünümü
        if (!canEdit) return;

        const listMine = filtered.filter((r) => r.atanan_email === myEmail);
        const from = listMine.findIndex((i) => String(i.id) === String(draggableId));
        if (from === -1) return;
        const moved = listMine[from];
        listMine.splice(from, 1);
        const to = destination.index;
        listMine.splice(to, 0, moved);

        // 1..N: sadece bana atanmışları yeniden numaralandır (atanan_sira)
        setRows((prevRows) => {
            const orderMap = new Map(listMine.map((r, i) => [r.id, i + 1]));
            const nextRows = prevRows.map((r) =>
                orderMap.has(r.id) ? { ...r, atanan_sira: orderMap.get(r.id) } : r
            );
            return sortRows(nextRows, mineOnly, view);
        });

        setDirty(true); // <— kullanıcı kaydetmeli
        setMsg("Kişisel kuyruğunda kaydedilmemiş değişiklikler var.");
    }

    /* ---------- "Sırayı Kaydet" ---------- */
    async function saveOrder() {
        if (!canEdit || view !== "list") return;

        try {
            setSaving(true);
            setErr("");
            setMsg("");

            if (!mineOnly) {
                // GLOBAL
                const ids = filtered.map((r) => Number(r.id));
                if (ids.length === 0) return;
                const { error } = await supabase.rpc("set_global_order", { ids });
                if (error) {
                    // Fallback: 1..N olarak tek tek global_sira güncelle
                    for (let i = 0; i < ids.length; i++) {
                        const id = ids[i];
                        const { error: upErr } = await supabase
                            .from("talepler")
                            .update({ global_sira: i + 1, guncelleme_tarihi: new Date().toISOString() })
                            .eq("id", id);
                        if (upErr) throw upErr;
                    }
                }
                setMsg("Global sıralama kaydedildi.");
            } else {
                // KİŞİSEL
                const mine = filtered.filter((r) => r.atanan_email === myEmail);
                const ids = mine.map((r) => Number(r.id));
                if (ids.length === 0) return;
                const { error } = await supabase.rpc("set_assignee_order", { p_email: myEmail, p_ids: ids });
                if (error) {
                    // Fallback: 1..N olarak atanan_sira yaz
                    for (let i = 0; i < ids.length; i++) {
                        const id = ids[i];
                        const { error: upErr } = await supabase
                            .from("talepler")
                            .update({ atanan_sira: i + 1, guncelleme_tarihi: new Date().toISOString() })
                            .eq("id", id);
                        if (upErr) throw upErr;
                    }
                }
                setMsg("Kişisel sıra kaydedildi.");
            }

            setDirty(false);
            await fetchData();
        } catch (e) {
            console.error(e);
            setErr("Sıralama kaydedilemedi.");
        } finally {
            setSaving(false);
        }
    }

    /* ---------- hızlı işlemler (anında kaydet) ---------- */
    async function takeOwnership(id) {
        if (!canEdit || !myEmail) return;
        const row = rows.find((r) => r.id === id);
        if (row && row.atanan_email && row.atanan_email !== myEmail) {
            setErr("Bu kayıt size atanmadığı için işlem yapamazsınız.");
            return;
        }

        const byStatus = groupByStatus(rows);
        const dst = byStatus[STATUS.SirayaAlindi];
        const last = dst[dst.length - 1]?.kolon_sira;
        const newKolonSira = between(last, undefined);

        const mineList = rows
            .filter((r) => r.atanan_email === myEmail)
            .sort((a, b) => (a.atanan_sira ?? 0) - (b.atanan_sira ?? 0));
        const lastMine = mineList[mineList.length - 1]?.atanan_sira;
        const newMyRank = between(lastMine, undefined);

        setRows((prevRows) =>
            sortRows(
                prevRows.map((r) =>
                    r.id === id
                        ? {
                            ...r,
                            atanan_email: myEmail,
                            atanan_kullanici: myName || null,
                            durum: STATUS.SirayaAlindi,
                            kolon_sira: newKolonSira,
                            atanan_sira: newMyRank,
                        }
                        : r
                ),
                mineOnly,
                view
            )
        );

        await updateRow(id, {
            atanan_email: myEmail,
            atanan_kullanici: myName || null,
            durum: STATUS.SirayaAlindi,
            kolon_sira: newKolonSira,
            atanan_sira: newMyRank,
        });
    }

    async function moveTo(id, statusKey) {
        if (!canEdit) return;
        const row = rows.find((r) => r.id === id);
        if (!row || row.atanan_email !== myEmail) {
            setErr("Bu kayıt size atanmadığı için işlem yapamazsınız.");
            return;
        }

        const byStatus = groupByStatus(rows);
        const dst = byStatus[statusKey];
        const last = dst[dst.length - 1]?.kolon_sira;
        const newKolonSira = between(last, undefined);

        setRows((prevRows) =>
            sortRows(
                prevRows.map((r) => (r.id === id ? { ...r, durum: statusKey, kolon_sira: newKolonSira } : r)),
                mineOnly,
                view
            )
        );

        await updateRow(id, { durum: statusKey, kolon_sira: newKolonSira });
    }

    async function markDone(id) {
        await moveTo(id, STATUS.Tamamlandi);
    }

    function handleLogout() {
        localStorage.removeItem("auth_token");
        localStorage.removeItem("user_profile");
        navigate("/login", { replace: true });
    }

    /* ---------- filter ---------- */
    const filtered = rows.filter((r) => {
        if (!q) return true;
        const text = `${r.id} ${r.baslik} ${r.talep_eden_email} ${r.atanan_email || ""} ${r.talep_eden_kullanici || ""} ${r.atanan_kullanici || ""}`.toLowerCase();
        return text.includes(q.toLowerCase());
    });

    const grouped = groupByStatus(filtered);

    return (
        <div className="mx">
            <div className="mx-top">
                <div className="brand">
                    <div className="logo">🧭</div>
                    <div className="bt">
                        <h1>Gelen Kutusu</h1>
                        <span className="muted">İş & Süreç Geliştirme</span>
                    </div>
                </div>

                <div className="cmd">
                    <div className="seg">
                        <button className={`seg-btn ${view === "board" ? "on" : ""}`} onClick={() => setView("board")}>
                            Kanban
                        </button>
                        <button className={`seg-btn ${view === "list" ? "on" : ""}`} onClick={() => setView("list")}>
                            Liste
                        </button>
                    </div>

                    <div className="search-wrap">
                        <input className="search" placeholder="Ara: #id, başlık, kişi…" value={q} onChange={(e) => setQ(e.target.value)} />
                    </div>

                    <div className="toggles">
                        <label className="toggle">
                            <input type="checkbox" checked={mineOnly} onChange={(e) => setMineOnly(e.target.checked)} />
                            <span>Bana atananlar</span>
                        </label>
                        <label className="toggle">
                            <input type="checkbox" checked={onlySLA} onChange={(e) => setOnlySLA(e.target.checked)} />
                            <span>SLA (&lt;24s)</span>
                        </label>
                    </div>

                    {/* Liste görünümünde "Sırayı Kaydet" */}
                    {view === "list" && canEdit && (
                        <button
                            className={`btn ${dirty ? "primary" : "ghost"}`}
                            onClick={saveOrder}
                            disabled={!dirty || saving}
                            title={mineOnly ? "Kişisel sıranı kaydet" : "Global sırayı kaydet"}
                        >
                            {mineOnly ? "Sıramı Kaydet" : "Global Sırayı Kaydet"}
                        </button>
                    )}

                    <Link to="/newrequest" className="btn primary">
                        + Yeni Talep
                    </Link>
                    <div className="chip">{(me?.kullanici || me?.email) + (canEdit ? " • editör" : "")}</div>
                    <button className="btn danger" onClick={handleLogout}>
                        Çıkış
                    </button>
                    {saving && <div className="saving-ind">Kaydediliyor…</div>}
                </div>
            </div>

            {err && <div className="mx-alert err">{err}</div>}
            {msg && !err && <div className="mx-alert ok">{msg}</div>}

            {loading ? (
                <div className="mx-skel">Yükleniyor…</div>
            ) : view === "board" ? (
                <DragDropContext onDragEnd={onBoardDragEnd}>
                    <div className="board">
                        {BOARD_COLUMNS.map((col) => {
                            const items = grouped[col.key] || [];
                            return (
                                <Droppable droppableId={col.key} key={col.key} isDropDisabled={!canEdit}>
                                    {(provided) => (
                                        <div className="col" ref={provided.innerRef} {...provided.droppableProps}>
                                            <div className="col-head">
                                                <span className={`dot s-${slug(col.title)}`}></span>
                                                <h3>{col.title}</h3>
                                                <span className="count">{items.length}</span>
                                            </div>
                                            <div className="col-body">
                                                {items.length === 0 && <div className="col-empty">Henüz kayıt yok</div>}
                                                {items.map((item, index) => (
                                                    <Draggable
                                                        draggableId={String(item.id)}
                                                        index={index}
                                                        key={item.id}
                                                        isDragDisabled={!canEdit || !(item.atanan_email && item.atanan_email === myEmail)}
                                                    >
                                                        {(pp) => (
                                                            <div ref={pp.innerRef} {...pp.draggableProps} {...pp.dragHandleProps}>
                                                                <Card
                                                                    data={item}
                                                                    onOpen={() => setActive(item)}
                                                                    onTake={() => canEdit && takeOwnership(item.id)}
                                                                    onMove={(st) => canEdit && moveTo(item.id, st)}
                                                                    onDone={() => canEdit && markDone(item.id)}
                                                                    me={myEmail}
                                                                />
                                                            </div>
                                                        )}
                                                    </Draggable>
                                                ))}
                                                {provided.placeholder}
                                            </div>
                                        </div>
                                    )}
                                </Droppable>
                            );
                        })}
                    </div>
                </DragDropContext>
            ) : (
                <DragDropContext onDragEnd={onListDragEnd}>
                    <Droppable droppableId="listMine" isDropDisabled={!canEdit && !mineOnly}>
                        {(provided) => (
                            <div className="listv" ref={provided.innerRef} {...provided.droppableProps}>
                                {filtered.length === 0 && <div className="col-empty">Kayıt bulunamadı</div>}
                                {filtered.map((r, index) => (
                                    <Draggable
                                        draggableId={String(r.id)}
                                        index={index}
                                        key={r.id}
                                        isDragDisabled={!canEdit && !mineOnly}
                                    >
                                        {(pp) => (
                                            <div
                                                ref={pp.innerRef}
                                                {...pp.draggableProps}
                                                {...pp.dragHandleProps}
                                                className="rowv"
                                                onClick={() => setActive(r)}
                                            >
                                                {!mineOnly && (
                                                    <span
                                                        className="r-idx"
                                                        style={{ minWidth: 24, textAlign: "right", opacity: 0.7, marginRight: 8 }}
                                                    >
                                                        {r.global_sira ?? index + 1}
                                                    </span>
                                                )}
                                                <span className={`pill ${PILL_COLOR[r.oncelik] || "p3"}`}>{r.oncelik || "P3"}</span>
                                                <span className={`badge s-${slug(STATUS_DISPLAY[r.durum] || "Yeni")}`}>
                                                    {STATUS_DISPLAY[r.durum] || "Yeni"}
                                                </span>
                                                <span className="r-title">
                                                    #{r.id} — {r.baslik}
                                                </span>
                                                <span className="r-sub">
                                                    {(r.talep_eden_kullanici || r.talep_eden_email) || "—"} →{" "}
                                                    {(r.atanan_kullanici || r.atanan_email) || "—"}
                                                    {mineOnly && r.atanan_email === myEmail && (
                                                        <b style={{ marginLeft: 8, opacity: 0.8 }}>(Sıra: {r.atanan_sira ?? "—"})</b>
                                                    )}
                                                </span>
                                                <span className="r-time">{fmtTime(r.guncelleme_tarihi)}</span>
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                                {!mineOnly && (
                                    <div className="hint" style={{ padding: 8, opacity: 0.7 }}>
                                        {canEdit
                                            ? dirty
                                                ? "Kaydet'e basarak sıralamayı herkese yansıtabilirsin."
                                                : "Bu listede sürüklediğin sıra herkese yansır (Kaydet ile)."
                                            : "Sıralamayı yalnızca yetkili kullanıcılar değiştirebilir."}
                                    </div>
                                )}
                            </div>
                        )}
                    </Droppable>
                </DragDropContext>
            )}

            {/* Detay çekmecesi */}
            {active && (
                <div className="drawer" role="dialog" aria-modal="true">
                    <div className="drawer-panel">
                        <button className="drawer-close" onClick={() => setActive(null)}>
                            ×
                        </button>

                        <div className="drawer-top">
                            <span className={`pill ${PILL_COLOR[active.oncelik] || "p3"}`}>{active.oncelik || "P3"}</span>
                            <span className={`badge s-${slug(STATUS_DISPLAY[active.durum] || "Yeni")}`}>
                                {STATUS_DISPLAY[active.durum] || "Yeni"}
                            </span>
                            {active.sla_son_tarih &&
                                new Date(active.sla_son_tarih).getTime() - Date.now() < 24 * 60 * 60 * 1000 && (
                                    <span className="sla">SLA</span>
                                )}
                            <h2>
                                #{active.id} — {active.baslik}
                            </h2>
                            <div className="d-sub">
                                <span>
                                    İsteyen: <b>{active.talep_eden_kullanici || active.talep_eden_email}</b>
                                </span>
                                <span> · </span>
                                <span>
                                    Atanan: <b>{active.atanan_kullanici || active.atanan_email || "—"}</b>
                                </span>
                                <span> · </span>
                                <span>Güncellendi: {fmtTime(active.guncelleme_tarihi)}</span>
                            </div>
                        </div>

                        <div className="drawer-body">
                            <Section title="Açıklama">
                                <p className="desc">{active.aciklama || "Açıklama girilmemiş."}</p>
                            </Section>

                            <Section title="Hızlı İşlemler">
                                <div className="act-grid">
                                    <button
                                        className="btn ghost"
                                        onClick={() => {
                                            if (canEdit) {
                                                setActive(null);
                                                takeOwnership(active.id);
                                            }
                                        }}
                                        disabled={!canEdit || (!!active.atanan_email && active.atanan_email !== myEmail)}
                                        title={
                                            !canEdit
                                                ? "Yalnızca yetkili kullanıcılar işlem yapabilir"
                                                : active.atanan_email && active.atanan_email !== myEmail
                                                    ? "Sadece atanan işlem yapabilir"
                                                    : undefined
                                        }
                                    >
                                        Üstlen
                                    </button>

                                    <button
                                        className="btn ghost"
                                        onClick={() => {
                                            if (canEdit && active.atanan_email === myEmail) {
                                                setActive(null);
                                                moveTo(active.id, STATUS.SirayaAlindi);
                                            }
                                        }}
                                        disabled={!canEdit || active.atanan_email !== myEmail}
                                    >
                                        Sıraya alındı
                                    </button>

                                    <button
                                        className="btn ghost"
                                        onClick={() => {
                                            if (canEdit && active.atanan_email === myEmail) {
                                                setActive(null);
                                                moveTo(active.id, STATUS.IslemeAlindi);
                                            }
                                        }}
                                        disabled={!canEdit || active.atanan_email !== myEmail}
                                    >
                                        İşleme alındı
                                    </button>

                                    <button
                                        className="btn ghost"
                                        onClick={() => {
                                            if (canEdit && active.atanan_email === myEmail) {
                                                setActive(null);
                                                moveTo(active.id, STATUS.TestEdiliyor);
                                            }
                                        }}
                                        disabled={!canEdit || active.atanan_email !== myEmail}
                                    >
                                        Test ediliyor
                                    </button>

                                    <button
                                        className="btn primary"
                                        onClick={() => {
                                            if (canEdit && active.atanan_email === myEmail) {
                                                setActive(null);
                                                markDone(active.id);
                                            }
                                        }}
                                        disabled={!canEdit || active.atanan_email !== myEmail}
                                    >
                                        ✓ Tamamlandı
                                    </button>
                                </div>
                            </Section>

                            <Section title="Zaman">
                                <div className="kv">
                                    <div>
                                        <span>Hedef Tarih:</span>
                                        <b>{active.bitis_tarihi ? fmtDate(active.bitis_tarihi) : "—"}</b>
                                    </div>
                                    <div>
                                        <span>SLA Son:</span>
                                        <b>{active.sla_son_tarih ? fmtDate(active.sla_son_tarih) : "—"}</b>
                                    </div>
                                </div>
                            </Section>
                        </div>
                    </div>
                    <div className="drawer-backdrop" onClick={() => setActive(null)} />
                </div>
            )}
        </div>
    );
}

/* components */
function Card({ data, onOpen, onTake, onMove, onDone, me }) {
    const mine = data.atanan_email && data.atanan_email === me;
    const slaSoon = data.sla_son_tarih && new Date(data.sla_son_tarih).getTime() - Date.now() < 24 * 60 * 60 * 1000;

    const fromLabel = data.talep_eden_kullanici || data.talep_eden_email || "—";
    const toLabel = data.atanan_kullanici || data.atanan_email || "—";

    return (
        <div className={`card ${mine ? "mine" : ""}`}>
            <div className="c-top">
                <span className={`pill ${PILL_COLOR[data.oncelik] || "p3"}`}>{data.oncelik || "P3"}</span>
                <span className={`badge s-${slug(STATUS_DISPLAY[data.durum] || "Yeni")}`}>
                    {STATUS_DISPLAY[data.durum] || "Yeni"}
                </span>
                {slaSoon && <span className="sla">SLA</span>}
                <button className="icn" onClick={onOpen} title="Detay">
                    ⋯
                </button>
            </div>

            <div className="c-title">
                #{data.id} — {data.baslik}
            </div>
            <div className="c-sub">
                <span>{fromLabel}</span>
                <span>→</span>
                <span>{toLabel}</span>
            </div>

            <div className="c-foot">
                <span className="time">{fmtTime(data.guncelleme_tarihi)}</span>
                <div className="act">
                    {!data.atanan_email && (
                        <button className="btn ghost xs" onClick={onTake}>
                            Üstlen
                        </button>
                    )}
                    <button className="btn ghost xs" onClick={() => mine && onMove(STATUS.SirayaAlindi)} disabled={!mine}>
                        Sıraya alındı
                    </button>
                    <button className="btn ghost xs" onClick={() => mine && onMove(STATUS.IslemeAlindi)} disabled={!mine}>
                        İşleme alındı
                    </button>
                    <button className="btn ghost xs" onClick={() => mine && onMove(STATUS.TestEdiliyor)} disabled={!mine}>
                        Test ediliyor
                    </button>
                    <button className="btn primary xs" onClick={mine ? onDone : undefined} disabled={!mine}>
                        Tamamla
                    </button>
                </div>
            </div>
        </div>
    );
}

function Section({ title, children }) {
    return (
        <section className="sec">
            <div className="sec-h">
                <h4>{title}</h4>
            </div>
            <div className="sec-b">{children}</div>
        </section>
    );
}

/* utils */
function slug(s) {
    return (s || "")
        .toLowerCase()
        .replaceAll("ı", "i")
        .replaceAll("ğ", "g")
        .replaceAll("ş", "s")
        .replaceAll("ö", "o")
        .replaceAll("ç", "c")
        .replaceAll("ü", "u")
        .replace(/\s+/g, "-");
}
function fmtTime(ts) {
    try {
        return new Date(ts).toLocaleString();
    } catch {
        return "—";
    }
}
function fmtDate(ts) {
    try {
        return new Date(ts).toLocaleDateString();
    } catch {
        return "—";
    }
}

/* ------ realtime yardımcıları ------ */
function dateNum(v) {
    try {
        return new Date(v).getTime() || 0;
    } catch {
        return 0;
    }
}

function sortRows(list, mineOnly, view) {
    const a = [...(list || [])];
    if (view === "list" && !mineOnly) {
        a.sort((x, y) => {
            const gx = x.global_sira ?? 1e15;
            const gy = y.global_sira ?? 1e15;
            if (gx !== gy) return gx - gy;
            const tx = dateNum(y.guncelleme_tarihi) - dateNum(x.guncelleme_tarihi);
            if (tx !== 0) return tx;
            return (x.id ?? 0) - (y.id ?? 0);
        });
    } else if (mineOnly) {
        a.sort((x, y) => {
            const ax = x.atanan_sira ?? 1e15;
            const ay = y.atanan_sira ?? 1e15;
            if (ax !== ay) return ax - ay;
            const tx = dateNum(y.guncelleme_tarihi) - dateNum(x.guncelleme_tarihi);
            if (tx !== 0) return tx;
            return (x.id ?? 0) - (y.id ?? 0);
        });
    } else {
        a.sort((x, y) => {
            const kx = x.kolon_sira ?? 1e15;
            const ky = y.kolon_sira ?? 1e15;
            if (kx !== ky) return kx - ky;
            const tx = dateNum(y.guncelleme_tarihi) - dateNum(x.guncelleme_tarihi);
            if (tx !== 0) return tx;
            return (x.id ?? 0) - (y.id ?? 0);
        });
    }
    return a;
}

function applyRealtime(prev, payload, mineOnly, view) {
    const type = payload.eventType;
    const next = [...prev];
    if (type === "DELETE") {
        return next.filter((r) => r.id !== payload.old?.id);
    }
    const row = payload.new;
    const i = next.findIndex((r) => r.id === row.id);
    if (i === -1) next.push(row);
    else next[i] = { ...next[i], ...row };
    return sortRows(next, mineOnly, view);
}
