// src/pages/Inbox.js
import React, { useEffect, useMemo, useRef, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";
import supabase from "../supabaseClient";
import "./Inbox.css";

/** DB anahtar -> ekranda gÃ¶rÃ¼nen */
const STATUS_DISPLAY = {
    Yeni: "Yeni",
    Inceleme: "Ä°nceleme",
    Atandi: "AtandÄ±",
    Calisiliyor: "Ã‡alÄ±ÅŸÄ±lÄ±yor",
    Beklemede: "Beklemede",
    Tamamlandi: "TamamlandÄ±",
};

const BOARD_COLUMNS = [
    { key: "Yeni", title: "Yeni" },
    { key: "Inceleme", title: "Ä°nceleme" },
    { key: "Atandi", title: "AtandÄ±" },
    { key: "Calisiliyor", title: "Ã‡alÄ±ÅŸÄ±lÄ±yor" },
    { key: "Beklemede", title: "Beklemede" },
    { key: "Tamamlandi", title: "TamamlandÄ±" },
];

const PILL_COLOR = { P1: "p1", P2: "p2", P3: "p3" };

export default function InboxModern() {
    const navigate = useNavigate();

    // --- auth/profile
    const me = useMemo(() => {
        try { return JSON.parse(localStorage.getItem("user_profile") || "{}"); }
        catch { return {}; }
    }, []);
    const myEmail = me?.email || "";

    // --- ui
    const [view, setView] = useState("board"); // "board" | "list"
    const [q, setQ] = useState("");
    const [mineOnly, setMineOnly] = useState(false);
    const [onlySLA, setOnlySLA] = useState(false);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");
    const [rows, setRows] = useState([]);
    const [active, setActive] = useState(null);

    // --- throttle kuyruk
    const flushTimer = useRef(null);
    const queued = useRef(new Map()); // id -> patch

    // --- guard
    useEffect(() => {
        const token = localStorage.getItem("auth_token");
        if (!token) navigate("/login", { replace: true });
    }, [navigate]);

    // --- realtime: herkes canlÄ± gÃ¶rsÃ¼n
    useEffect(() => {
        const ch = supabase
            .channel("realtime:talepler")
            .on(
                "postgres_changes",
                { event: "*", schema: "public", table: "talepler" },
                (payload) => {
                    setRows((prev) => {
                        const row = payload.new ?? payload.old;
                        if (!row?.id) return prev;

                        if (payload.eventType === "DELETE") {
                            return prev.filter((x) => x.id !== row.id);
                        }
                        const i = prev.findIndex((x) => x.id === row.id);
                        if (i >= 0) {
                            const next = [...prev];
                            next[i] = { ...next[i], ...payload.new };
                            return next;
                        }
                        return [...prev, payload.new];
                    });
                }
            )
            .subscribe();

        return () => { try { supabase.removeChannel(ch); } catch { } };
    }, []);

    // --- data
    useEffect(() => { fetchData(); /* eslint-disable-next-line */ }, [mineOnly, onlySLA]);

    async function fetchData() {
        if (!myEmail) return;
        setLoading(true); setErr("");
        try {
            const now = new Date();

            let query = supabase.from("talepler").select(`
        id, baslik, durum, oncelik, talep_eden_email, atanan_email,
        guncelleme_tarihi, bitis_tarihi, sla_son_tarih, aciklama,
        kolon_sira, atanan_sira
      `);

            if (mineOnly) {
                // Bana atananlar gÃ¶rÃ¼nÃ¼mÃ¼nde kuyruÄŸu DB'den atanan_sira'ya gÃ¶re al
                query = query.eq("atanan_email", myEmail)
                    .order("atanan_sira", { ascending: true, nullsFirst: false })
                    .order("guncelleme_tarihi", { ascending: false });
            } else {
                // Global kolon_sira'ya gÃ¶re sÄ±ralama YOK â€” her kolonu render sÄ±rasÄ±nda kendi iÃ§inde sÄ±ralayacaÄŸÄ±z
                query = query.order("guncelleme_tarihi", { ascending: false });
            }

            if (onlySLA) {
                const t24 = new Date(now.getTime() + 24 * 60 * 60 * 1000).toISOString();
                query = query.lt("sla_son_tarih", t24).not("durum", "in", ["Tamamlandi", "Reddedildi"]);
            }

            const { data, error } = await query.limit(500);
            if (error) throw error;
            setRows(data || []);
        } catch (e) {
            console.error(e);
            setErr("KayÄ±tlar Ã§ekilemedi.");
        } finally {
            setLoading(false);
        }
    }

    /* ---------- yardÄ±mcÄ±lar ---------- */

    function groupByStatus(items) {
        const map = Object.fromEntries(BOARD_COLUMNS.map(c => [c.key, []]));
        items.forEach(i => { (map[i.durum || "Yeni"] ??= []).push(i); });
        return map;
    }

    // Kolon iÃ§i deterministik sÄ±ralama (reload'da bozulmayÄ± bununla Ã§Ã¶zÃ¼yoruz)
    function sortByColRank(a, b) {
        const ka = (a.kolon_sira ?? 999999);
        const kb = (b.kolon_sira ?? 999999);
        if (ka !== kb) return ka - kb;
        return new Date(b.guncelleme_tarihi) - new Date(a.guncelleme_tarihi);
    }

    // sadece verilen kolon(lar) iÃ§in kolon_sira'yÄ± 1..N hesapla
    function recomputeColumnOrder(map, keys) {
        keys.forEach(k => {
            (map[k] || []).forEach((it, idx) => { it.kolon_sira = idx + 1; });
        });
    }

    // 250ms throttle ile patch'leri kuyruÄŸa alÄ±p yaz
    function queuePersist(upds) {
        upds.forEach(u => {
            const prev = queued.current.get(u.id) || {};
            queued.current.set(u.id, { ...prev, ...u });
        });
        if (!flushTimer.current) {
            flushTimer.current = setTimeout(async () => {
                const payload = Array.from(queued.current.values()).map(u => ({
                    id: u.id,
                    ...(u.durum !== undefined ? { durum: u.durum } : {}),
                    ...(u.kolon_sira !== undefined ? { kolon_sira: u.kolon_sira } : {}),
                    ...(u.atanan_sira !== undefined ? { atanan_sira: u.atanan_sira } : {}),
                    guncelleme_tarihi: new Date().toISOString(),
                }));
                queued.current.clear();
                flushTimer.current = null;
                if (payload.length) {
                    const { error } = await supabase.from("talepler").upsert(payload, { onConflict: "id" });
                    if (error) console.error("realtime-save error", error);
                }
            }, 250);
        }
    }

    async function persistOrder(updates) {
        if (!updates?.length) return;
        const payload = updates.map(u => ({
            id: u.id,
            ...(u.durum !== undefined ? { durum: u.durum } : {}),
            ...(u.kolon_sira !== undefined ? { kolon_sira: u.kolon_sira } : {}),
            ...(u.atanan_sira !== undefined ? { atanan_sira: u.atanan_sira } : {}),
            guncelleme_tarihi: new Date().toISOString(),
        }));
        const { error } = await supabase.from("talepler").upsert(payload, { onConflict: "id" });
        if (error) console.error("order save error", error);
    }

    /* ---------- DnD: Kanban â€” anlÄ±k + final ---------- */

    function onBoardDragUpdate(update) {
        const { source, destination, draggableId } = update;
        if (!destination) return;

        const from = source.droppableId;
        const to = destination.droppableId;

        const map = groupByStatus(rows.map(r => ({ ...r })));
        const fromArr = map[from] || [];
        const toArr = map[to] || [];

        const movingIdx = fromArr.findIndex(i => String(i.id) === String(draggableId));
        if (movingIdx === -1) return;

        const moving = fromArr.splice(movingIdx, 1)[0];
        moving.durum = to;

        const destIndex = Math.min(destination.index, toArr.length);
        toArr.splice(destIndex, 0, moving);

        recomputeColumnOrder(map, from === to ? [to] : [from, to]);

        const updates = [];
        (map[from] || []).forEach((it, idx) => updates.push({ id: it.id, kolon_sira: idx + 1, ...(it.id === moving.id ? { durum: it.durum } : {}) }));
        if (from !== to) {
            (map[to] || []).forEach((it, idx) => updates.push({ id: it.id, kolon_sira: idx + 1, ...(it.id === moving.id ? { durum: it.durum } : {}) }));
        }

        queuePersist(updates); // drop'u beklemeden yaz
    }

    async function onBoardDragEnd(result) {
        const { source, destination, draggableId } = result;
        if (!destination) return;

        const statusFrom = source.droppableId;
        const statusTo = destination.droppableId;

        const byStatus = groupByStatus([...rows]);
        const idx = byStatus[statusFrom].findIndex(i => String(i.id) === String(draggableId));
        if (idx === -1) return;

        const item = byStatus[statusFrom][idx];
        byStatus[statusFrom].splice(idx, 1);
        byStatus[statusTo].splice(destination.index, 0, item);
        item.durum = statusTo;

        // her kolonda 1..N
        BOARD_COLUMNS.forEach(col => {
            byStatus[col.key].forEach((it, i) => { it.kolon_sira = i + 1; });
        });

        const flattened = BOARD_COLUMNS.flatMap(c => byStatus[c.key]);
        setRows(flattened); // optimistic

        await persistOrder(
            [statusFrom, statusTo].flatMap(k =>
                (byStatus[k] || []).map((it, i) => ({ id: it.id, durum: it.durum, kolon_sira: i + 1 }))
            )
        );
    }

    /* ---------- DnD: Liste â€” (Bana atananlar) ---------- */

    function onListDragUpdate(update) {
        const { destination, draggableId } = update;
        if (!destination || !mineOnly) return;

        const list = rows
            .filter(r => r.atanan_email === myEmail)
            .map(r => ({ ...r }))
            .sort((a, b) => (a.atanan_sira ?? 999999) - (b.atanan_sira ?? 999999));

        const from = list.findIndex(i => String(i.id) === String(draggableId));
        if (from === -1) return;

        const moved = list.splice(from, 1)[0];
        const destIndex = Math.min(destination.index, list.length);
        list.splice(destIndex, 0, moved);

        list.forEach((r, i) => { r.atanan_sira = i + 1; });

        queuePersist(list.map(r => ({ id: r.id, atanan_sira: r.atanan_sira })));
    }

    async function onListDragEnd(result) {
        const { destination, draggableId } = result;
        if (!destination || !mineOnly) return;

        const list = rows
            .filter(r => r.atanan_email === myEmail)
            .map(r => ({ ...r }))
            .sort((a, b) => (a.atanan_sira ?? 999999) - (b.atanan_sira ?? 999999));

        const from = list.findIndex(i => String(i.id) === String(draggableId));
        if (from === -1) return;

        const moved = list.splice(from, 1)[0];
        list.splice(destination.index, 0, moved);
        list.forEach((r, i) => { r.atanan_sira = i + 1; });

        const next = rows.map(r => {
            const hit = list.find(x => x.id === r.id);
            return hit ? { ...r, atanan_sira: hit.atanan_sira } : r;
        });
        setRows(next);

        await persistOrder(list.map(({ id, atanan_sira }) => ({ id, atanan_sira })));
    }

    /* ---------- tek hÄ±zlÄ± aksiyon: Ãœstlen ---------- */
    async function takeOwnership(id) {
        if (!myEmail) return;
        const myColMax = rows.filter(r => r.durum === "Inceleme")
            .reduce((m, r) => Math.max(m, r.kolon_sira || 0), 0) + 1;
        const myQueueMax = rows.filter(r => r.atanan_email === myEmail)
            .reduce((m, r) => Math.max(m, r.atanan_sira || 0), 0) + 1;

        const { error } = await supabase.from("talepler").update({
            atanan_email: myEmail,
            durum: "Inceleme",
            kolon_sira: myColMax,
            atanan_sira: myQueueMax,
            guncelleme_tarihi: new Date().toISOString()
        }).eq("id", id);
        if (!error) fetchData();
    }

    function handleLogout() {
        localStorage.removeItem("auth_token");
        localStorage.removeItem("user_profile");
        navigate("/login", { replace: true });
    }

    /* ---------- filter ---------- */
    const filtered = rows.filter(r => {
        if (!q) return true;
        const text = `${r.id} ${r.baslik} ${r.talep_eden_email} ${r.atanan_email || ""}`.toLowerCase();
        return text.includes(q.toLowerCase());
    });

    const grouped = groupByStatus(filtered);

    return (
        <div className="mx">
            {/* Command bar */}
            <div className="mx-top">
                <div className="brand">
                    <div className="logo">ðŸ§­</div>
                    <div className="bt">
                        <h1>Gelen Kutusu</h1>
                        <span className="muted">Ä°ÅŸ & SÃ¼reÃ§ GeliÅŸtirme</span>
                    </div>
                </div>

                <div className="cmd">
                    <div className="seg">
                        <button className={`seg-btn ${view === "board" ? "on" : ""}`} onClick={() => setView("board")}>Kanban</button>
                        <button className={`seg-btn ${view === "list" ? "on" : ""}`} onClick={() => setView("list")}>Liste</button>
                    </div>

                    <div className="search-wrap">
                        <input className="search" placeholder="Ara: #id, baÅŸlÄ±k, kiÅŸiâ€¦" value={q} onChange={(e) => setQ(e.target.value)} />
                    </div>

                    <div className="toggles">
                        <label className="toggle">
                            <input type="checkbox" checked={mineOnly} onChange={e => setMineOnly(e.target.checked)} />
                            <span>Bana atananlar</span>
                        </label>
                        <label className="toggle">
                            <input type="checkbox" checked={onlySLA} onChange={e => setOnlySLA(e.target.checked)} />
                            <span>SLA (&lt;24s)</span>
                        </label>
                    </div>

                    <Link to="/newrequest" className="btn primary">+ Yeni Talep</Link>
                    <div className="chip">{me?.kullanici || me?.email}</div>
                    <button className="btn danger" onClick={handleLogout}>Ã‡Ä±kÄ±ÅŸ</button>
                </div>
            </div>

            {err && <div className="mx-alert err">{err}</div>}

            {loading ? (
                <div className="mx-skel">YÃ¼kleniyorâ€¦</div>
            ) : view === "board" ? (
                <DragDropContext onDragUpdate={onBoardDragUpdate} onDragEnd={onBoardDragEnd}>
                    <div className="board">
                        {BOARD_COLUMNS.map(col => {
                            // ðŸ”‘ Her kolonu kendi iÃ§inde kolon_sira'ya gÃ¶re sÄ±rala (reload sonrasÄ± stabil gÃ¶rÃ¼nÃ¼m)
                            const items = (grouped[col.key] || []).slice().sort(sortByColRank);
                            return (
                                <Droppable droppableId={col.key} key={col.key}>
                                    {(provided) => (
                                        <div className="col" ref={provided.innerRef} {...provided.droppableProps}>
                                            <div className="col-head">
                                                <span className={`dot s-${slug(col.title)}`}></span>
                                                <h3>{col.title}</h3>
                                                <span className="count">{items.length}</span>
                                            </div>
                                            <div className="col-body">
                                                {items.length === 0 && <div className="col-empty">HenÃ¼z kayÄ±t yok</div>}
                                                {items.map((item, index) => (
                                                    <Draggable draggableId={String(item.id)} index={index} key={item.id}>
                                                        {(pp) => (
                                                            <div ref={pp.innerRef} {...pp.draggableProps} {...pp.dragHandleProps}>
                                                                <Card
                                                                    data={item}
                                                                    rank={index + 1}
                                                                    onOpen={() => setActive(item)}
                                                                    onTake={() => takeOwnership(item.id)}
                                                                    me={myEmail}
                                                                />
                                                            </div>
                                                        )}
                                                    </Draggable>
                                                ))}
                                                {provided.placeholder}
                                            </div>
                                        </div>
                                    )}
                                </Droppable>
                            );
                        })}
                    </div>
                </DragDropContext>
            ) : (
                // LISTE â€” "Bana atananlar" aÃ§Ä±kken sÃ¼rÃ¼kleyerek kendi kuyruÄŸunu sÄ±rala
                <DragDropContext onDragUpdate={onListDragUpdate} onDragEnd={onListDragEnd}>
                    <Droppable droppableId="listMine" isDropDisabled={!mineOnly}>
                        {(provided) => (
                            <div className="listv" ref={provided.innerRef} {...provided.droppableProps}>
                                {filtered.length === 0 && <div className="col-empty">KayÄ±t bulunamadÄ±</div>}
                                {filtered.map((r, index) => (
                                    <Draggable draggableId={String(r.id)} index={index} key={r.id} isDragDisabled={!mineOnly}>
                                        {(pp) => (
                                            <div
                                                ref={pp.innerRef} {...pp.draggableProps} {...pp.dragHandleProps}
                                                className="rowv" onClick={() => setActive(r)}
                                            >
                                                <span className={`pill ${PILL_COLOR[r.oncelik] || "p3"}`}>{r.oncelik || "P3"}</span>
                                                <span className={`badge s-${slug(STATUS_DISPLAY[r.durum] || "Yeni")}`}>{STATUS_DISPLAY[r.durum] || "Yeni"}</span>
                                                <span className="r-title">#{r.id} â€” {r.baslik}</span>
                                                <span className="r-sub">
                                                    {r.talep_eden_email} â†’ {r.atanan_email || "â€”"}
                                                    {mineOnly && r.atanan_email === myEmail && (
                                                        <b style={{ marginLeft: 8, opacity: .8 }}>(SÄ±ra: {r.atanan_sira ?? "â€”"})</b>
                                                    )}
                                                </span>
                                                <span className="r-time">{fmtTime(r.guncelleme_tarihi)}</span>
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                                {!mineOnly && (
                                    <div className="hint" style={{ padding: 8, opacity: .7 }}>
                                        Listeyi sÃ¼rÃ¼kleyerek sÄ±ralamak iÃ§in â€œBana atananlarâ€Ä± iÅŸaretleyin.
                                    </div>
                                )}
                            </div>
                        )}
                    </Droppable>
                </DragDropContext>
            )}

            {/* Detay Ã§ekmecesi */}
            {active && (
                <div className="drawer" role="dialog" aria-modal="true">
                    <div className="drawer-panel">
                        <button className="drawer-close" onClick={() => setActive(null)}>Ã—</button>

                        <div className="drawer-top">
                            <span className={`pill ${PILL_COLOR[active.oncelik] || "p3"}`}>{active.oncelik || "P3"}</span>
                            <span className={`badge s-${slug(STATUS_DISPLAY[active.durum] || "Yeni")}`}>{STATUS_DISPLAY[active.durum] || "Yeni"}</span>
                            <h2>#{active.id} â€” {active.baslik}</h2>
                            <div className="d-sub">
                                <span>Ä°steyen: <b>{active.talep_eden_email}</b></span>
                                <span> Â· </span>
                                <span>Atanan: <b>{active.atanan_email || "â€”"}</b></span>
                                <span> Â· </span>
                                <span>GÃ¼ncellendi: {fmtTime(active.guncelleme_tarihi)}</span>
                            </div>
                        </div>

                        <div className="drawer-body">
                            <Section title="AÃ§Ä±klama">
                                <p className="desc">{active.aciklama || "AÃ§Ä±klama girilmemiÅŸ."}</p>
                            </Section>

                            {!active.atanan_email && (
                                <Section title="Atama">
                                    <button className="btn primary" onClick={() => { setActive(null); takeOwnership(active.id); }}>
                                        Ãœstlen
                                    </button>
                                </Section>
                            )}

                            <Section title="Zaman">
                                <div className="kv">
                                    <div><span>Hedef Tarih:</span><b>{active.bitis_tarihi ? fmtDate(active.bitis_tarihi) : "â€”"}</b></div>
                                    <div><span>SLA Son:</span><b>{active.sla_son_tarih ? fmtDate(active.sla_son_tarih) : "â€”"}</b></div>
                                </div>
                            </Section>
                        </div>
                    </div>
                    <div className="drawer-backdrop" onClick={() => setActive(null)} />
                </div>
            )}
        </div>
    );
}

/* -------- components -------- */
function Card({ data, rank, onOpen, onTake, me }) {
    const mine = data.atanan_email && data.atanan_email === me;
    const slaSoon = data.sla_son_tarih && new Date(data.sla_son_tarih).getTime() - Date.now() < 24 * 60 * 60 * 1000;

    return (
        <div className={`card ${mine ? "mine" : ""}`}>
            <div className="c-top">
                <span className={`pill ${PILL_COLOR[data.oncelik] || "p3"}`}>{data.oncelik || "P3"}</span>
                <span className={`badge s-${slug(STATUS_DISPLAY[data.durum] || "Yeni")}`}>{STATUS_DISPLAY[data.durum] || "Yeni"}</span>
                {slaSoon && <span className="sla">SLA</span>}
                <span title="Bu kolondaki sÄ±ra" style={{ marginLeft: "auto", fontSize: 12, opacity: .75 }}>#{rank}</span>
                <button className="icn" onClick={onOpen} title="Detay">â‹¯</button>
            </div>

            <div className="c-title">#{data.id} â€” {data.baslik}</div>
            <div className="c-sub">
                <span>{data.talep_eden_email}</span>
                <span>â†’</span>
                <span>{data.atanan_email || "â€”"}</span>
            </div>

            <div className="c-foot">
                <span className="time">{fmtTime(data.guncelleme_tarihi)}</span>
                <div className="act">
                    {!data.atanan_email && <button className="btn ghost xs" onClick={onTake}>Ãœstlen</button>}
                </div>
            </div>
        </div>
    );
}

function Section({ title, children }) {
    return (
        <section className="sec">
            <div className="sec-h"><h4>{title}</h4></div>
            <div className="sec-b">{children}</div>
        </section>
    );
}

/* -------- utils -------- */
function slug(s) { return (s || "").toLowerCase().replaceAll("Ä±", "i").replaceAll("ÄŸ", "g").replaceAll("ÅŸ", "s").replaceAll("Ã¶", "o").replaceAll("Ã§", "c").replaceAll("Ã¼", "u").replace(/\s+/g, "-"); }
function fmtTime(ts) { try { return new Date(ts).toLocaleString(); } catch { return "â€”"; } }
function fmtDate(ts) { try { return new Date(ts).toLocaleDateString(); } catch { return "â€”"; } }
