// src/App.jsx
import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Inbox from "./pages/Inbox";
import NewRequest from "./pages/NewRequest";
import Requests from "./pages/Requests";

export default function App() {
    // localStorage’dan kullanıcı bilgisi çek
    const profile = JSON.parse(localStorage.getItem("user_profile") || "{}");
    const userBirim = profile?.birim || "";

    // login sonrası nereye yönlendirilecek
    function getDefaultRoute() {
        if (!profile?.email) {
            return "/login"; // hiç giriş yapılmadıysa login
        }
        if (userBirim === "İŞ VE SÜREÇ GELİŞTİRME") {
            return "/inbox"; // bu birim Inbox görecek
        }
        return "/newrequest"; // diğer herkes NewRequest görecek
    }

    return (
        <BrowserRouter>
            <Routes>
                <Route path="/login" element={<Login />} />
                <Route path="/inbox" element={<Inbox />} />
                <Route path="/newrequest" element={<NewRequest />} />
                <Route path="/requests" element={<Requests />} />
                {/* login sonrası otomatik yönlendirme */}
                <Route path="*" element={<Navigate to={getDefaultRoute()} replace />} />
            </Routes>
        </BrowserRouter>
    );
}
