// src/utils/auth.js
export function getUser() {
    try {
        return JSON.parse(localStorage.getItem("user_profile") || "{}");
    } catch {
        return {};
    }
}

export function isAuthenticated() {
    return !!localStorage.getItem("auth_token");
}

export function isISurec(user) {
    const birim = (user?.birim || "").toLocaleUpperCase("tr-TR");
    return birim.includes("�� VE S�RE� GEL��T�RME");
}
