// src/pages/TeamMonitor.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import supabase from "../supabaseClient";
import "./Inbox.css";

/* ---- Sabitler ---- */
const STATUS = {
    Yeni: "Yeni",
    SirayaAlindi: "SirayaAlindi",
    IslemeAlindi: "IslemeAlindi",
    TestEdiliyor: "TestEdiliyor",
    Tamamlandi: "Tamamlandi",
};

const STATUS_DISPLAY = {
    [STATUS.Yeni]: "Yeni",
    [STATUS.SirayaAlindi]: "Sıraya Alındı",
    [STATUS.IslemeAlindi]: "İşleme Alındı",
    [STATUS.TestEdiliyor]: "Test Ediliyor",
    [STATUS.Tamamlandi]: "Tamamlandı",
};

const MONITORED_USERS = [
    { name: "GÖRKEM ÇADIRCI" },
    { name: "FURKAN BİLGİLİ" },
    { name: "YAĞIZ EFE BULUTCU" },
];

const ALLOWED_VIEWER = "ONUR KEREM ÖZTÜRK";

/* ---- Utils ---- */
const tnum = (v) => { try { return new Date(v).getTime() || 0; } catch { return 0; } };
const fmtDT = (v) => { try { return new Date(v).toLocaleString(); } catch { return "—"; } };
const daysBetween = (a, b) => Math.max(0, (tnum(b) - tnum(a)) / (24 * 60 * 60 * 1000));

function median(arr) {
    if (!arr.length) return 0;
    const a = [...arr].sort((x, y) => x - y);
    const m = Math.floor(a.length / 2);
    return a.length % 2 ? a[m] : (a[m - 1] + a[m]) / 2;
}

function startOfWeek(d = new Date()) {
    const x = new Date(d);
    const day = x.getDay(); // 0 pazar
    const diff = (day + 6) % 7; // pazartesiyi hafta başı say
    x.setHours(0, 0, 0, 0);
    x.setDate(x.getDate() - diff);
    return x;
}

function bucketsLastNWeeks(n = 6) {
    const arr = [];
    let cur = startOfWeek(new Date());
    for (let i = 0; i < n; i++) {
        const end = new Date(cur);
        const start = new Date(cur); start.setDate(start.getDate() - 7);
        arr.unshift({ label: `${start.getDate()}.${start.getMonth() + 1}`, start, end, count: 0 });
        cur = start;
    }
    return arr; // eskiden yeniye
}

/* ---- Bileşen ---- */
export default function TeamMonitor() {
    const navigate = useNavigate();

    // auth
    const me = useMemo(() => {
        try { return JSON.parse(localStorage.getItem("user_profile") || "{}"); }
        catch { return {}; }
    }, []);
    const myName = (me?.kullanici || "").trim();
    useEffect(() => {
        if (!localStorage.getItem("auth_token")) navigate("/login", { replace: true });
    }, [navigate]);

    const canView = useMemo(
        () => (myName || "").toLocaleUpperCase("tr-TR") === ALLOWED_VIEWER,
        [myName]
    );

    // UI state
    const [selectedUser, setSelectedUser] = useState(MONITORED_USERS[0].name);
    const [q, setQ] = useState("");
    const [onlySLA, setOnlySLA] = useState(false);

    // data
    const [rows, setRows] = useState([]);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");

    // fetch (seçili kullanıcının kayıtları)
    useEffect(() => {
        if (!canView) return;
        (async () => {
            try {
                setLoading(true);
                setErr("");

                let query = supabase.from("talepler").select("*").eq("atanan_kullanici", selectedUser);

                if (onlySLA) {
                    const t24 = new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString();
                    query = query.lt("sla_son_tarih", t24).not("durum", "in", '("Tamamlandi","Reddedildi")');
                }

                query = query
                    .order("guncelleme_tarihi", { ascending: false })
                    .limit(500);

                const { data, error } = await query;
                if (error) throw error;
                setRows(data || []);
            } catch (e) {
                console.error(e);
                setErr("Kayıtlar çekilemedi.");
            } finally {
                setLoading(false);
            }
        })();
    }, [selectedUser, onlySLA, canView]);

    // realtime (yalnız seçili kullanıcı)
    useEffect(() => {
        if (!canView) return;
        const ch = supabase
            .channel("tm-realtime-one")
            .on("postgres_changes", { event: "*", schema: "public", table: "talepler" }, (payload) => {
                setRows((prev) => {
                    const r = payload.new || payload.old || {};
                    if (r.atanan_kullanici !== selectedUser) return prev;

                    if (payload.eventType === "DELETE") return prev.filter(x => x.id !== r.id);
                    const next = [...prev];
                    const i = next.findIndex(x => x.id === r.id);
                    if (i === -1 && payload.eventType === "INSERT") next.unshift(r);
                    else if (i > -1) next[i] = { ...next[i], ...r };
                    return next.sort((a, b) => tnum(b.guncelleme_tarihi) - tnum(a.guncelleme_tarihi));
                });
            })
            .subscribe();
        return () => supabase.removeChannel(ch);
    }, [selectedUser, canView]);

    // arama/filtre
    const inboxList = useMemo(() => {
        const qq = q.trim().toLowerCase();
        const arr = !qq
            ? rows
            : rows.filter((r) => {
                const hay = `${r.id} ${r.baslik} ${r.talep_eden_kullanici || ""} ${r.talep_eden_email || ""}`.toLowerCase();
                return hay.includes(qq);
            });
        return arr.sort((a, b) => tnum(b.guncelleme_tarihi) - tnum(a.guncelleme_tarihi));
    }, [rows, q]);

    // metrikler (son 30 gün)
    const last30 = useMemo(() => {
        const since = Date.now() - 30 * 24 * 60 * 60 * 1000;
        return rows.filter((r) => tnum(r.guncelleme_tarihi) >= since);
    }, [rows]);
    const completedLast30 = last30.filter((r) => r.durum === STATUS.Tamamlandi && r.tamamlanma_tarihi);
    const durations = completedLast30.map((r) => daysBetween(r.olusturma_tarihi || r.giristarihi, r.tamamlanma_tarihi));
    const avg = durations.length ? (durations.reduce((s, n) => s + n, 0) / durations.length) : 0;
    const med = median(durations);

    // throughput (son 6 hafta - tamamlanan sayısı)
    const weekly = useMemo(() => {
        const buckets = bucketsLastNWeeks(6);
        for (const r of rows) {
            if (!(r.durum === STATUS.Tamamlandi && r.tamamlanma_tarihi)) continue;
            const done = new Date(r.tamamlanma_tarihi);
            for (const b of buckets) {
                if (done >= b.start && done < b.end) { b.count++; break; }
            }
        }
        return buckets;
    }, [rows]);

    // durum dağılımı
    const statusCounts = useMemo(() => {
        const map = { Yeni: 0, SirayaAlindi: 0, IslemeAlindi: 0, TestEdiliyor: 0, Tamamlandi: 0 };
        rows.forEach((r) => { map[r.durum] = (map[r.durum] || 0) + 1; });
        return map;
    }, [rows]);

    if (!canView) {
        return (
            <div className="mx">
                <div className="mx-top sticky modern">
                    <div className="brand">
                        <div className="logo">👀</div>
                        <div className="bt"><h1>Takip Ekranı</h1><span className="muted">Erişim yetkiniz yok</span></div>
                    </div>
                </div>
                <div className="mx-alert err">Bu ekran yalnızca <b>{ALLOWED_VIEWER}</b> için yetkilidir.</div>
            </div>
        );
    }

    return (
        <div className="mx">
            {/* Üst bar */}
            <div className="mx-top sticky modern">
                <div className="brand">
                    <div className="logo">📥</div>
                    <div className="bt">
                        <h1>{selectedUser} — Inbox</h1>
                        <span className="muted">Kullanıcı seç → üstte inbox, altta analizler</span>
                    </div>
                </div>

                <div className="cmd">
                    <select
                        className="input"
                        value={selectedUser}
                        onChange={(e) => setSelectedUser(e.target.value)}
                        style={{ minWidth: 260 }}
                        aria-label="Kullanıcı seç"
                    >
                        {MONITORED_USERS.map((u) => (
                            <option key={u.name} value={u.name}>{u.name}</option>
                        ))}
                    </select>

                    <div className="search-wrap">
                        <input
                            className="search"
                            placeholder="Ara: #id, başlık, kişi…"
                            value={q}
                            onChange={(e) => setQ(e.target.value)}
                        />
                    </div>

                    <label className="toggle">
                        <input type="checkbox" checked={onlySLA} onChange={(e) => setOnlySLA(e.target.checked)} />
                        <span>SLA (&lt;24s)</span>
                    </label>

                    <button className="btn primary" onClick={() => navigate("/inbox?assignee=" + encodeURIComponent(selectedUser))}>
                        {selectedUser.split(" ")[0]}'ın Inbox’ı (tam ekran)
                    </button>
                </div>
            </div>

            {err && <div className="mx-alert err">{err}</div>}
            {loading && <div className="mx-skel">Yükleniyor…</div>}

            {/* ---------- 1) INBOX LİSTESİ (tek sütun, yukarıda) ---------- */}
            <section className="col modern" style={{ marginTop: 10 }}>
                <div className="col-head mini">
                    <span className="dot" />
                    <h4 className="mini-title">Inbox — Son güncellenenler</h4>
                    <span className="count">{inboxList.length}</span>
                </div>

                <ul className="col-body" style={{ listStyle: "none", margin: 0, padding: 0 }}>
                    {inboxList.slice(0, 30).map((r) => (
                        <li key={r.id} style={{ marginBottom: 8 }}>
                            <button
                                className="card modern"
                                onClick={() => navigate(`/inbox?assignee=${encodeURIComponent(r.atanan_email || "")}`)}
                                style={{ width: "100%", textAlign: "left" }}
                                title={`#${r.id} — ${r.baslik}\nDurum: ${STATUS_DISPLAY[r.durum] || "—"}\nGüncellenme: ${fmtDT(r.guncelleme_tarihi)}`}
                            >
                                <div className="c-title line-1">#{r.id} — {r.baslik}</div>
                                <div className="c-desc line-2">{r.talep_eden_kullanici || r.talep_eden_email || "—"}</div>
                                <div className="c-meta">
                                    <b>{STATUS_DISPLAY[r.durum] || "—"}</b> • <span className="time">{fmtDT(r.guncelleme_tarihi)}</span>
                                </div>
                            </button>
                        </li>
                    ))}
                    {inboxList.length === 0 && <div className="col-empty">Kayıt yok</div>}
                </ul>
            </section>

            {/* ---------- 2) ANALİZ BÖLÜMÜ (KPI + mini grafikler) ---------- */}
            <section style={{ marginTop: 12 }}>
                {/* KPI kutuları */}
                <div className="board" style={{ gridTemplateColumns: "repeat(6, minmax(160px, 1fr))", gap: 10 }}>
                    <KPI title="Toplam (bu kullanıcı)" value={rows.length} />
                    <KPI title="Yeni" value={statusCounts[STATUS.Yeni] || 0} tone="blue" />
                    <KPI title="Sıraya Alındı" value={statusCounts[STATUS.SirayaAlindi] || 0} tone="amber" />
                    <KPI title="İşleme Alındı" value={statusCounts[STATUS.IslemeAlindi] || 0} tone="emerald" />
                    <KPI title="Test Ediliyor" value={statusCounts[STATUS.TestEdiliyor] || 0} tone="violet" />
                    <KPI title="Tamamlandı" value={statusCounts[STATUS.Tamamlandi] || 0} tone="zinc" />
                </div>

                {/* Tamamlama süreleri */}
                <div className="board" style={{ gridTemplateColumns: "repeat(3, minmax(220px, 1fr))", gap: 10, marginTop: 10 }}>
                    <KPI title="30 günde tamamlanan" value={completedLast30.length} tone="teal" />
                    <KPI title="Ort. tamamlama (gün)" value={avg ? avg.toFixed(1) : "—"} />
                    <KPI title="Medyan tamamlama (gün)" value={med ? med.toFixed(1) : "—"} />
                </div>

                {/* Throughput & Durum dağılımı (basit barlar) */}
                <div className="board" style={{ gridTemplateColumns: "repeat(2, minmax(260px, 1fr))", gap: 12, marginTop: 10 }}>
                    <Card title="Throughput — tamamlanan (haftalık, son 6)">
                        <Bars data={weekly.map(b => ({ label: b.label, value: b.count }))} />
                    </Card>

                    <Card title="Durum dağılımı">
                        <Bars
                            data={[
                                { label: "Yeni", value: statusCounts[STATUS.Yeni] || 0 },
                                { label: "Sıraya", value: statusCounts[STATUS.SirayaAlindi] || 0 },
                                { label: "İşleme", value: statusCounts[STATUS.IslemeAlindi] || 0 },
                                { label: "Test", value: statusCounts[STATUS.TestEdiliyor] || 0 },
                                { label: "Done", value: statusCounts[STATUS.Tamamlandi] || 0 },
                            ]}
                        />
                    </Card>
                </div>
            </section>

            <div className="muted" style={{ marginTop: 10 }}>
                Not: Grafikler basit CSS barlarıdır; istersen gerçek chart kütüphanesiyle (recharts/echarts) güncelleyebilirim.
            </div>
        </div>
    );
}

/* ---- Basit görsel bileşenler ---- */
function KPI({ title, value, tone = "zinc" }) {
    const ring = {
        blue: "inset 0 0 0 2px rgba(96,165,250,.45)",
        amber: "inset 0 0 0 2px rgba(251,191,36,.45)",
        emerald: "inset 0 0 0 2px rgba(52,211,153,.45)",
        violet: "inset 0 0 0 2px rgba(167,139,250,.45)",
        teal: "inset 0 0 0 2px rgba(45,212,191,.45)",
        zinc: "inset 0 0 0 2px rgba(161,161,170,.35)",
    }[tone] || "inset 0 0 0 2px rgba(161,161,170,.25)";

    return (
        <div className="card modern" style={{ padding: 14, boxShadow: ring }}>
            <div className="c-title" style={{ fontSize: 12, opacity: 0.85 }}>{title}</div>
            <div style={{ fontSize: 22, fontWeight: 700, marginTop: 6 }}>{value}</div>
        </div>
    );
}

function Card({ title, children }) {
    return (
        <div className="col modern">
            <div className="col-head mini">
                <span className="dot" />
                <h4 className="mini-title">{title}</h4>
            </div>
            <div className="col-body">{children}</div>
        </div>
    );
}

function Bars({ data }) {
    const max = Math.max(1, ...data.map(d => d.value || 0));
    return (
        <div style={{ display: "grid", gridTemplateColumns: `repeat(${data.length}, 1fr)`, gap: 8, alignItems: "end", minHeight: 120 }}>
            {data.map((d, i) => (
                <div key={i} title={`${d.label}: ${d.value}`} style={{ display: "grid", gridTemplateRows: "1fr auto", gap: 6 }}>
                    <div
                        style={{
                            height: `${(d.value / max) * 100}%`,
                            background: "linear-gradient(180deg, rgba(99,102,241,.8), rgba(99,102,241,.25))",
                            borderRadius: 8,
                            border: "1px solid rgba(255,255,255,.08)",
                        }}
                    />
                    <div style={{ fontSize: 11, color: "#a3a3aa", textAlign: "center" }}>{d.label}</div>
                </div>
            ))}
        </div>
    );
}
