import React, { useEffect, useMemo, useRef, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";
import supabase from "../supabaseClient";
import "./Inbox.css";

/**
 * NOT: Supabase Realtime iÃ§in tabloda yayÄ±n aÃ§Ä±k olmalÄ±:
 *   alter publication supabase_realtime add table public.talepler;
 */

const STATUS_DISPLAY = {
    Yeni: "Yeni",
    SirayaAlindi: "SÄ±raya AlÄ±ndÄ±",
    IslemeAlindi: "Ä°ÅŸleme AlÄ±ndÄ±",
    TestEdiliyor: "Test Ediliyor",
    Tamamlandi: "TamamlandÄ±",
};

const BOARD_COLUMNS = [
    { key: "Yeni", title: "Yeni" },
    { key: "SirayaAlindi", title: "SÄ±raya AlÄ±ndÄ±" },
    { key: "IslemeAlindi", title: "Ä°ÅŸleme AlÄ±ndÄ±" },
    { key: "TestEdiliyor", title: "Test Ediliyor" },
    { key: "Tamamlandi", title: "TamamlandÄ±" },
];

const PILL_COLOR = { P1: "p1", P2: "p2", P3: "p3" };

/* ---- YETKÄ° YARDIMCI ---- */
function trUp(s) {
    return (s || "").trim().toLocaleUpperCase("tr-TR");
}
const ALLOWED_NAMES = new Set([
    "GÃ–RKEM Ã‡ADIRCI",
    "FURKAN BÄ°LGÄ°LÄ°",
    "YAÄžIZ EFE BULUTCU",
]);
function computeIsEditor(profile) {
    // 1) Login sÄ±rasÄ±nda localStorage'a yazÄ±lan isEditor varsa onu kullan
    if (profile && typeof profile.isEditor === "boolean") return profile.isEditor;
    // 2) Fallback: local hesap
    return (
        trUp(profile?.birim) === "Ä°Åž VE SÃœREÃ‡ GELÄ°ÅžTÄ°RME" &&
        ALLOWED_NAMES.has(trUp(profile?.kullanici || profile?.email))
    );
}

export default function InboxModern() {
    const navigate = useNavigate();

    const me = useMemo(() => {
        try {
            return JSON.parse(localStorage.getItem("user_profile") || "{}");
        } catch {
            return {};
        }
    }, []);
    const myEmail = me?.email || "";
    const myName = (me?.kullanici || "").trim();
    const canEdit = useMemo(() => computeIsEditor(me), [me]); // <-- SADECE editÃ¶rler yazabilir

    const [view, setView] = useState("board"); // "board" | "list"
    const [q, setQ] = useState("");
    const [mineOnly, setMineOnly] = useState(false);
    const [onlySLA, setOnlySLA] = useState(false);
    const [loading, setLoading] = useState(false);
    const [saving, setSaving] = useState(false);      // ðŸ‘ˆ kaydetme gÃ¶stergesi
    const [err, setErr] = useState("");
    const [rows, setRows] = useState([]);
    const [active, setActive] = useState(null);

    // refs for stable access in callbacks
    const mineOnlyRef = useRef(mineOnly);
    useEffect(() => {
        mineOnlyRef.current = mineOnly;
    }, [mineOnly]);

    useEffect(() => {
        const token = localStorage.getItem("auth_token");
        if (!token) navigate("/login", { replace: true });
    }, [navigate]);

    useEffect(() => {
        fetchData();
        // eslint-disable-next-line
    }, [mineOnly, onlySLA, view]);

    // Realtime
    useEffect(() => {
        const channel = supabase
            .channel("talepler-realtime")
            .on(
                "postgres_changes",
                { event: "*", schema: "public", table: "talepler" },
                (payload) => {
                    setRows((prev) => applyRealtime(prev, payload, mineOnlyRef.current, view));
                }
            )
            .subscribe();
        return () => supabase.removeChannel(channel);
    }, [view]);

    // gÃ¶rÃ¼nÃ¼m deÄŸiÅŸince sÄ±rala
    useEffect(() => {
        setRows((prev) => sortRows(prev, mineOnly, view));
    }, [mineOnly, view]);

    async function fetchData() {
        if (!myEmail) return;
        setLoading(true);
        setErr("");
        try {
            const now = new Date();
            let query = supabase.from("talepler").select(`
        id, baslik, durum, oncelik,
        talep_eden_email, atanan_email,
        talep_eden_kullanici, atanan_kullanici,
        guncelleme_tarihi, bitis_tarihi, sla_son_tarih, aciklama,
        kolon_sira, atanan_sira, global_sira
      `);

            if (view === "list" && !mineOnly) {
                query = query
                    .order("global_sira", { ascending: true, nullsFirst: true })
                    .order("guncelleme_tarihi", { ascending: false });
            } else if (mineOnly) {
                query = query
                    .eq("atanan_email", myEmail)
                    .order("atanan_sira", { ascending: true, nullsFirst: false })
                    .order("guncelleme_tarihi", { ascending: false });
            } else {
                query = query
                    .order("kolon_sira", { ascending: true, nullsFirst: true })
                    .order("guncelleme_tarihi", { ascending: false });
            }

            if (onlySLA) {
                const t24 = new Date(now.getTime() + 24 * 60 * 60 * 1000).toISOString();
                query = query
                    .lt("sla_son_tarih", t24)
                    .not("durum", "in", '("Tamamlandi","Reddedildi")');
            }

            const { data, error } = await query.limit(500);
            if (error) throw error;
            setRows(sortRows(data || [], mineOnly, view));
        } catch (e) {
            console.error(e);
            setErr("KayÄ±tlar Ã§ekilemedi.");
        } finally {
            setLoading(false);
        }
    }

    /* ---------- helpers ---------- */
    function groupByStatus(items) {
        const map = Object.fromEntries(BOARD_COLUMNS.map((c) => [c.key, []]));
        items.forEach((i) => {
            (map[i.durum || "Yeni"] ??= []).push(i);
        });
        return map;
    }

    function recomputeAssigneeRanks(list) {
        const byA = {};
        list.forEach((r) => {
            if (!r.atanan_email) return;
            (byA[r.atanan_email] ??= []).push(r);
        });
        Object.values(byA).forEach((arr) => {
            arr.forEach((r, idx) => {
                r.atanan_sira = idx + 1;
            });
        });
    }

    // ðŸ‘‡ GÃ¼ncellendi: optimistic + "Kaydediliyorâ€¦" gÃ¶stergesi
    async function persistOrder(updates) {
        if (!canEdit) return; // <-- editÃ¶r olmayan yazamaz
        if (!updates?.length) return;
        const nowIso = new Date().toISOString();
        const rowsToSave = updates.map((u) => ({
            id: u.id,
            ...(u.durum !== undefined ? { durum: u.durum } : {}),
            ...(u.kolon_sira !== undefined ? { kolon_sira: u.kolon_sira } : {}),
            ...(u.atanan_sira !== undefined ? { atanan_sira: u.atanan_sira } : {}),
            guncelleme_tarihi: nowIso,
        }));

        try {
            setSaving(true);
            const results = await Promise.allSettled(
                rowsToSave.map(({ id, ...changes }) =>
                    supabase
                        .from("talepler")
                        .update(changes)
                        .eq("id", id)
                        .select("id", { count: "exact", head: true })
                )
            );

            const hasFailure = results.some(
                (r) => r.status === "rejected" || (r.value && r.value.error)
            );
            if (hasFailure) {
                const { error } = await supabase
                    .from("talepler")
                    .upsert(rowsToSave, { onConflict: "id" });
                if (error) console.error("order save error (upsert)", error);
            }
        } finally {
            setSaving(false);
        }
    }

    /* ---------- DnD: Kanban ---------- */
    async function onBoardDragEnd(result) {
        if (!canEdit) return; // <-- editÃ¶r deÄŸilse hiÃ§ Ã§alÄ±ÅŸmasÄ±n
        const { source, destination, draggableId } = result;
        if (!destination) return;

        const statusFrom = source.droppableId;
        const statusTo = destination.droppableId;

        const byStatus = groupByStatus([...rows]);
        const idx = byStatus[statusFrom].findIndex(
            (i) => String(i.id) === String(draggableId)
        );
        if (idx === -1) return;

        const item = byStatus[statusFrom][idx];
        if (item.atanan_email !== myEmail) return; // ek gÃ¼venlik: sadece atanan

        // optimistic: hemen ekranda taÅŸÄ±
        byStatus[statusFrom].splice(idx, 1);
        byStatus[statusTo].splice(destination.index, 0, item);

        const flattened = BOARD_COLUMNS.flatMap((c) => byStatus[c.key]);
        const prevById = new Map(rows.map((r) => [r.id, r]));

        const updates = [];
        BOARD_COLUMNS.forEach((col) => {
            byStatus[col.key].forEach((it, i) => {
                const newDurum = col.key;
                const newKolonSira = i + 1;
                const prev = prevById.get(it.id) || {};
                let dirty = false;
                if (it.durum !== newDurum) {
                    it.durum = newDurum;
                    dirty = true;
                }
                if (it.kolon_sira !== newKolonSira) {
                    it.kolon_sira = newKolonSira;
                    dirty = true;
                }
                if (dirty) updates.push({ id: it.id, durum: newDurum, kolon_sira: newKolonSira });
            });
        });

        recomputeAssigneeRanks(flattened);
        flattened.forEach((it) => {
            const prev = prevById.get(it.id) || {};
            if ((prev.atanan_sira ?? null) !== (it.atanan_sira ?? null)) {
                const u = updates.find((u) => u.id === it.id);
                if (u) u.atanan_sira = it.atanan_sira;
                else updates.push({ id: it.id, atanan_sira: it.atanan_sira });
            }
        });

        setRows(sortRows(flattened, mineOnly, view));
        await persistOrder(updates);
    }

    /* ---------- DnD: Liste (global + bana atananlar) ---------- */
    async function onListDragEnd(result) {
        const { destination, draggableId } = result;
        if (!destination) return;

        if (!mineOnly) {
            // GLOBAL sÄ±ra: yalnÄ±zca editÃ¶rler deÄŸiÅŸtirebilir
            if (!canEdit) return;

            const list = [...filtered];
            const from = list.findIndex((i) => String(i.id) === String(draggableId));
            if (from === -1) return;
            const moved = list[from];
            list.splice(from, 1);
            list.splice(destination.index, 0, moved);

            const ids = list.map((r) => Number(r.id));

            // optimistic: local global_sira ata
            const orderMap = new Map(ids.map((id, i) => [String(id), i + 1]));
            const newRows = rows.map((r) =>
                orderMap.has(String(r.id)) ? { ...r, global_sira: orderMap.get(String(r.id)) } : r
            );
            setRows(sortRows(newRows, mineOnly, view));

            setSaving(true);
            const { error } = await supabase.rpc("set_global_order", { ids });
            setSaving(false);

            if (error) {
                console.error(error);
                setErr("Global sÄ±ralama kaydedilemedi.");
            } else {
                fetchData();
            }
            return;
        }

        // ----- mineOnly: kiÅŸisel kuyruk sÄ±rasÄ± (yalnÄ±zca editÃ¶rler deÄŸiÅŸtirebilir) -----
        if (!canEdit) return;

        const movedRow = rows.find((r) => String(r.id) === String(draggableId));
        if (!movedRow || movedRow.atanan_email !== myEmail) return;

        const list = filtered.filter((r) => r.atanan_email === myEmail);
        const from = list.findIndex((i) => String(i.id) === String(draggableId));
        if (from === -1) return;

        const moved = list[from];
        list.splice(from, 1);
        list.splice(destination.index, 0, moved);

        list.forEach((r, i) => {
            r.atanan_sira = i + 1;
        });

        const newRows2 = rows.map((r) => {
            const hit = list.find((x) => x.id === r.id);
            return hit ? { ...r, atanan_sira: hit.atanan_sira } : r;
        });
        setRows(sortRows(newRows2, mineOnly, view));

        const updates = list
            .filter(
                (r) => r.id && rows.find((x) => x.id === r.id)?.atanan_sira !== r.atanan_sira
            )
            .map(({ id, atanan_sira }) => ({ id, atanan_sira }));

        await persistOrder(updates);
    }

    /* ---------- quick actions ---------- */
    async function takeOwnership(id) {
        if (!canEdit) return; // sadece editÃ¶rler Ã¼stlenebilir
        if (!myEmail) return;
        const row = rows.find((r) => r.id === id);
        if (row && row.atanan_email && row.atanan_email !== myEmail) {
            setErr("Bu kayÄ±t size atanmadÄ±ÄŸÄ± iÃ§in iÅŸlem yapamazsÄ±nÄ±z.");
            return;
        }
        const myColMax =
            rows
                .filter((r) => r.durum === "SirayaAlindi")
                .reduce((m, r) => Math.max(m, r.kolon_sira || 0), 0) + 1;
        const myQueueMax =
            rows
                .filter((r) => r.atanan_email === myEmail)
                .reduce((m, r) => Math.max(m, r.atanan_sira || 0), 0) + 1;

        // optimistic
        setRows((prev) => {
            const a = prev.map((r) =>
                r.id === id
                    ? {
                        ...r,
                        atanan_email: myEmail,
                        atanan_kullanici: myName || null,
                        durum: "SirayaAlindi",
                        kolon_sira: myColMax,
                        atanan_sira: myQueueMax,
                        guncelleme_tarihi: new Date().toISOString(),
                    }
                    : r
            );
            return sortRows(a, mineOnly, view);
        });

        setSaving(true);
        const { error } = await supabase
            .from("talepler")
            .update({
                atanan_email: myEmail,
                atanan_kullanici: myName || null,
                durum: "SirayaAlindi",
                kolon_sira: myColMax,
                atanan_sira: myQueueMax,
                guncelleme_tarihi: new Date().toISOString(),
            })
            .eq("id", id);
        setSaving(false);

        if (error) {
            console.error(error);
            setErr("Bu kayÄ±t Ã¼zerinde iÅŸlem yetkiniz yok.");
            fetchData(); // optimistic'i geri almak iÃ§in
            return;
        }
    }

    async function moveTo(id, statusKey) {
        if (!canEdit) return; // sadece editÃ¶rler statÃ¼ deÄŸiÅŸtirebilir
        const row = rows.find((r) => r.id === id);
        if (!row || row.atanan_email !== myEmail) {
            setErr("Bu kayÄ±t size atanmadÄ±ÄŸÄ± iÃ§in iÅŸlem yapamazsÄ±nÄ±z.");
            return;
        }
        const max =
            rows
                .filter((r) => r.durum === statusKey)
                .reduce((m, r) => Math.max(m, r.kolon_sira || 0), 0) + 1;

        // optimistic
        setRows((prev) => {
            const a = prev.map((r) =>
                r.id === id
                    ? {
                        ...r,
                        durum: statusKey,
                        kolon_sira: max,
                        guncelleme_tarihi: new Date().toISOString(),
                    }
                    : r
            );
            return sortRows(a, mineOnly, view);
        });

        setSaving(true);
        const { error } = await supabase
            .from("talepler")
            .update({
                durum: statusKey,
                kolon_sira: max,
                guncelleme_tarihi: new Date().toISOString(),
            })
            .eq("id", id);
        setSaving(false);

        if (error) {
            console.error(error);
            setErr("Bu kayÄ±t Ã¼zerinde iÅŸlem yetkiniz yok.");
            fetchData(); // optimistic dÃ¼zelt
            return;
        }
    }

    async function markDone(id) {
        await moveTo(id, "Tamamlandi");
    }

    function handleLogout() {
        localStorage.removeItem("auth_token");
        localStorage.removeItem("user_profile");
        navigate("/login", { replace: true });
    }

    /* ---------- filter ---------- */
    const filtered = rows.filter((r) => {
        if (!q) return true;
        const text = `${r.id} ${r.baslik} ${r.talep_eden_email} ${r.atanan_email || ""} ${r.talep_eden_kullanici || ""} ${r.atanan_kullanici || ""}`.toLowerCase();
        return text.includes(q.toLowerCase());
    });

    const grouped = groupByStatus(filtered);

    return (
        <div className="mx">
            {/* Command bar */}
            <div className="mx-top">
                <div className="brand">
                    <div className="logo">ðŸ§­</div>
                    <div className="bt">
                        <h1>Gelen Kutusu</h1>
                        <span className="muted">Ä°ÅŸ & SÃ¼reÃ§ GeliÅŸtirme</span>
                    </div>
                </div>

                <div className="cmd">
                    <div className="seg">
                        <button
                            className={`seg-btn ${view === "board" ? "on" : ""}`}
                            onClick={() => setView("board")}
                        >
                            Kanban
                        </button>
                        <button
                            className={`seg-btn ${view === "list" ? "on" : ""}`}
                            onClick={() => setView("list")}
                        >
                            Liste
                        </button>
                    </div>

                    <div className="search-wrap">
                        <input
                            className="search"
                            placeholder="Ara: #id, baÅŸlÄ±k, kiÅŸiâ€¦"
                            value={q}
                            onChange={(e) => setQ(e.target.value)}
                        />
                    </div>

                    <div className="toggles">
                        <label className="toggle">
                            <input
                                type="checkbox"
                                checked={mineOnly}
                                onChange={(e) => setMineOnly(e.target.checked)}
                            />
                            <span>Bana atananlar</span>
                        </label>
                        <label className="toggle">
                            <input
                                type="checkbox"
                                checked={onlySLA}
                                onChange={(e) => setOnlySLA(e.target.checked)}
                            />
                            <span>SLA (&lt;24s)</span>
                        </label>
                    </div>

                    <Link to="/newrequest" className="btn primary">
                        + Yeni Talep
                    </Link>
                    <div className="chip">
                        {(me?.kullanici || me?.email) + (canEdit ? " â€¢ editÃ¶r" : "")}
                    </div>
                    <button className="btn danger" onClick={handleLogout}>
                        Ã‡Ä±kÄ±ÅŸ
                    </button>

                    {/* ðŸ‘‡ Kaydetme gÃ¶stergesi */}
                    {saving && <div className="saving-ind">Kaydediliyorâ€¦</div>}
                </div>
            </div>

            {err && <div className="mx-alert err">{err}</div>}

            {loading ? (
                <div className="mx-skel">YÃ¼kleniyorâ€¦</div>
            ) : view === "board" ? (
