/* components */
function Card({ data, onOpen, onTake, onMove, onDone, me }) {
    const mine = data.atanan_email && data.atanan_email === me;
    const slaSoon =
        data.sla_son_tarih &&
        new Date(data.sla_son_tarih).getTime() - Date.now() < 24 * 60 * 60 * 1000;

    const fromLabel = data.talep_eden_kullanici || data.talep_eden_email || "â€”";
    const toLabel = data.atanan_kullanici || data.atanan_email || "â€”";

    return (
        <div className={`card ${mine ? "mine" : ""}`}>
            <div className="c-top">
                <span className={`pill ${PILL_COLOR[data.oncelik] || "p3"}`}>{data.oncelik || "P3"}</span>
                <span className={`badge s-${slug(STATUS_DISPLAY[data.durum] || "Yeni")}`}>
                    {STATUS_DISPLAY[data.durum] || "Yeni"}
                </span>
                {slaSoon && <span className="sla">SLA</span>}
                <button className="icn" onClick={onOpen} title="Detay">
                    â‹¯
                </button>
            </div>

            <div className="c-title">#{data.id} â€” {data.baslik}</div>
            <div className="c-sub">
                <span>{fromLabel}</span>
                <span>â†’</span>
                <span>{toLabel}</span>
            </div>

            <div className="c-foot">
                <span className="time">{fmtTime(data.guncelleme_tarihi)}</span>
                <div className="act">
                    {!data.atanan_email && (
                        <button className="btn ghost xs" onClick={onTake}>
                            Ãœstlen
                        </button>
                    )}
                    <button
                        className="btn ghost xs"
                        onClick={() => mine && onMove("SirayaAlindi")}
                        disabled={!mine}
                        title={!mine ? "Sadece atanan iÅŸlem yapabilir" : undefined}
                    >
                        SÄ±raya alÄ±ndÄ±
                    </button>
                    <button
                        className="btn ghost xs"
                        onClick={() => mine && onMove("IslemeAlindi")}
                        disabled={!mine}
                        title={!mine ? "Sadece atanan iÅŸlem yapabilir" : undefined}
                    >
                        Ä°ÅŸleme alÄ±ndÄ±
                    </button>
                    <button
                        className="btn ghost xs"
                        onClick={() => mine && onMove("TestEdiliyor")}
                        disabled={!mine}
                        title={!mine ? "Sadece atanan iÅŸlem yapabilir" : undefined}
