/* utils */
function slug(s) {
    return (s || "")
        .toLowerCase()
        .replaceAll("Ä±", "i")
        .replaceAll("ÄŸ", "g")
        .replaceAll("ÅŸ", "s")
        .replaceAll("Ã¶", "o")
        .replaceAll("Ã§", "c")
        .replaceAll("Ã¼", "u")
        .replace(/\s+/g, "-");
}
function fmtTime(ts) {
    try {
        return new Date(ts).toLocaleString();
    } catch {
        return "â€”";
    }
}
function fmtDate(ts) {
    try {
        return new Date(ts).toLocaleDateString();
    } catch {
        return "â€”";
    }
}

/* ------ realtime yardÄ±mcÄ±larÄ± ------ */
function dateNum(v) {
    try {
        return new Date(v).getTime() || 0;
    } catch {
        return 0;
    }
}

function sortRows(list, mineOnly, view) {
    const a = [...(list || [])];
    if (view === "list" && !mineOnly) {
        a.sort((x, y) => {
            const gx = x.global_sira ?? 1e9;
            const gy = y.global_sira ?? 1e9;
            if (gx !== gy) return gx - gy;
            const tx = dateNum(y.guncelleme_tarihi) - dateNum(x.guncelleme_tarihi);
            if (tx !== 0) return tx;
            return (x.id ?? 0) - (y.id ?? 0);
        });
    } else if (mineOnly) {
        a.sort((x, y) => {
            const ax = x.atanan_sira ?? 1e9;
            const ay = y.atanan_sira ?? 1e9;
            if (ax !== ay) return ax - ay;
            const tx = dateNum(y.guncelleme_tarihi) - dateNum(x.guncelleme_tarihi);
            if (tx !== 0) return tx;
            return (x.id ?? 0) - (y.id ?? 0);
        });
    } else {
        a.sort((x, y) => {
            const kx = x.kolon_sira ?? 1e9;
            const ky = y.kolon_sira ?? 1e9;
            if (kx !== ky) return kx - ky;
            const tx = dateNum(y.guncelleme_tarihi) - dateNum(x.guncelleme_tarihi);
            if (tx !== 0) return tx;
            return (x.id ?? 0) - (y.id ?? 0);
        });
    }
    return a;
}

function applyRealtime(prev, payload, mineOnly, view) {
    const type = payload.eventType;
    const next = [...prev];
    if (type === "DELETE") {
        return next.filter((r) => r.id !== payload.old?.id);
    }
    const row = payload.new;
    const i = next.findIndex((r) => r.id === row.id);
    if (i === -1) next.push(row);
    else next[i] = { ...next[i], ...row };
    return sortRows(next, mineOnly, view);
}
