// src/pages/TeamMonitor.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import supabase from "../supabaseClient";
import "./Inbox.css"; // mevcut stillerden yararlanÄ±yoruz

/* ---- Sabitler ---- */
const STATUS = {
    Yeni: "Yeni",
    SirayaAlindi: "SirayaAlindi",
    IslemeAlindi: "IslemeAlindi",
    TestEdiliyor: "TestEdiliyor",
    Tamamlandi: "Tamamlandi",
};

const STATUS_DISPLAY = {
    [STATUS.Yeni]: "Yeni",
    [STATUS.SirayaAlindi]: "SÄ±raya AlÄ±ndÄ±",
    [STATUS.IslemeAlindi]: "Ä°ÅŸleme AlÄ±ndÄ±",
    [STATUS.TestEdiliyor]: "Test Ediliyor",
    [STATUS.Tamamlandi]: "TamamlandÄ±",
};

const BOARD_COLUMNS = [
    { key: STATUS.Yeni, title: STATUS_DISPLAY[STATUS.Yeni] },
    { key: STATUS.SirayaAlindi, title: STATUS_DISPLAY[STATUS.SirayaAlindi] },
    { key: STATUS.IslemeAlindi, title: STATUS_DISPLAY[STATUS.IslemeAlindi] },
    { key: STATUS.TestEdiliyor, title: STATUS_DISPLAY[STATUS.TestEdiliyor] },
    { key: STATUS.Tamamlandi, title: STATUS_DISPLAY[STATUS.Tamamlandi] },
];

// Ä°zlenecek kullanÄ±cÄ±lar
const MONITORED_USERS = [
    { name: "GÃ–RKEM Ã‡ADIRCI" },
    { name: "FURKAN BÄ°LGÄ°LÄ°" },
    { name: "YAÄžIZ EFE BULUTCU" },
];

// Bu sayfayÄ± gÃ¶rebilecek kiÅŸi
const ALLOWED_VIEWER = "ONUR KEREM Ã–ZTÃœRK";

/* ---- Utils ---- */
function slug(s) {
    return (s || "")
        .toLowerCase()
        .replaceAll("Ä±", "i").replaceAll("ÄŸ", "g").replaceAll("ÅŸ", "s")
        .replaceAll("Ã¶", "o").replaceAll("Ã§", "c").replaceAll("Ã¼", "u")
        .replace(/\s+/g, "-");
}
function fmtTime(ts) { try { return new Date(ts).toLocaleString(); } catch { return "â€”"; } }
function dateNum(v) { try { return new Date(v).getTime() || 0; } catch { return 0; } }

function sortByKanbanOrder(list) {
    const a = [...(list || [])];
    a.sort((x, y) => {
        const kx = x.kolon_sira ?? 1e15;
        const ky = y.kolon_sira ?? 1e15;
        if (kx !== ky) return kx - ky;
        const tx = dateNum(y.guncelleme_tarihi) - dateNum(x.guncelleme_tarihi);
        if (tx !== 0) return tx;
        return (x.id ?? 0) - (y.id ?? 0);
    });
    return a;
}

export default function TeamMonitor() {
    const navigate = useNavigate();

    // login + yetki kontrolÃ¼
    const me = useMemo(() => {
        try { return JSON.parse(localStorage.getItem("user_profile") || "{}"); }
        catch { return {}; }
    }, []);
    const myName = (me?.kullanici || "").trim();
    useEffect(() => {
        if (!localStorage.getItem("auth_token")) {
            navigate("/login", { replace: true });
            return;
        }
    }, [navigate]);
    const canView = useMemo(() => {
        const up = (myName || "").toLocaleUpperCase("tr-TR");
        return up === ALLOWED_VIEWER;
    }, [myName]);

    const [q, setQ] = useState("");
    const [onlySLA, setOnlySLA] = useState(false);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");
    const [rows, setRows] = useState([]);

    // data fetch
    useEffect(() => {
        if (!canView) return;
        fetchData();
        // eslint-disable-next-line
    }, [onlySLA]);

    // realtime
    useEffect(() => {
        if (!canView) return;
        const ch = supabase
            .channel("talepler-realtime-monitor")
            .on("postgres_changes", { event: "*", schema: "public", table: "talepler" }, (payload) => {
                setRows((prev) => applyRealtime(prev, payload));
            })
            .subscribe();
        return () => supabase.removeChannel(ch);
    }, [canView]);

    async function fetchData() {
        try {
            setLoading(true);
            setErr("");

            const names = MONITORED_USERS.map(u => u.name);
            let query = supabase.from("talepler").select("*").in("atanan_kullanici", names);

            if (onlySLA) {
                const t24 = new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString();
                query = query.lt("sla_son_tarih", t24).not("durum", "in", '("Tamamlandi","Reddedildi")');
            }

            // Kanbanâ€™da tutarlÄ± gÃ¶rÃ¼nÃ¼m iÃ§in kolon_sira + gÃ¼ncelleme_tarihi
            query = query
                .order("kolon_sira", { ascending: true, nullsFirst: true })
                .order("guncelleme_tarihi", { ascending: false })
                .limit(1000);

            const { data, error } = await query;
            if (error) throw error;
            setRows(sortByKanbanOrder(data || []));
        } catch (e) {
            console.error(e);
            setErr("KayÄ±tlar Ã§ekilemedi.");
        } finally {
            setLoading(false);
        }
    }

    function applyRealtime(prev, payload) {
        const next = [...prev];
        if (payload.eventType === "DELETE") {
            return next.filter(r => r.id !== payload.old?.id);
        }
        const row = payload.new;
        // Sadece izlenen kullanÄ±cÄ±lar
        if (!MONITORED_USERS.some(u => u.name === row?.atanan_kullanici)) return prev;

        const i = next.findIndex(r => r.id === row.id);
        if (i === -1) next.push(row);
        else next[i] = { ...next[i], ...row };
        return sortByKanbanOrder(next);
    }

    // filtreleme
    const filtered = rows.filter((r) => {
        if (!q) return true;
        const text = `${r.id} ${r.baslik} ${r.talep_eden_email} ${r.atanan_email || ""} ${r.talep_eden_kullanici || ""} ${r.atanan_kullanici || ""}`.toLowerCase();
        return text.includes(q.toLowerCase());
    });

    // kullanÄ±cÄ± -> status -> items
    const byUser = useMemo(() => {
        const map = new Map();
        MONITORED_USERS.forEach(u => {
            map.set(u.name, Object.fromEntries(BOARD_COLUMNS.map(c => [c.key, []])));
        });
        for (const r of filtered) {
            const user = r.atanan_kullanici;
            if (!map.has(user)) continue;
            const k = r.durum || STATUS.Yeni;
            map.get(user)[k].push(r);
        }
        // iÃ§ listeleri sÄ±rala
        for (const [, colMap] of map) {
            for (const k of Object.keys(colMap)) {
                colMap[k] = sortByKanbanOrder(colMap[k]);
            }
        }
        return map;
    }, [filtered]);

    if (!canView) {
        return (
            <div className="mx">
                <div className="mx-top">
                    <div className="brand">
                        <div className="logo">ðŸ‘€</div>
                        <div className="bt"><h1>Takip EkranÄ±</h1><span className="muted">EriÅŸim yetkiniz yok</span></div>
                    </div>
                </div>
                <div className="mx-alert err">Bu ekran yalnÄ±zca <b>{ALLOWED_VIEWER}</b> iÃ§in yetkilidir.</div>
            </div>
        );
    }

    return (
        <div className="mx">
            <div className="mx-top">
                <div className="brand">
                    <div className="logo">ðŸ“Š</div>
                    <div className="bt">
                        <h1>Takip EkranÄ±</h1>
                        <span className="muted">GÃ–RKEM â€¢ FURKAN â€¢ YAÄžIZ â€” canlÄ± durum</span>
                    </div>
                </div>

                <div className="cmd">
                    <div className="search-wrap">
                        <input
                            className="search"
                            placeholder="Ara: #id, baÅŸlÄ±k, kiÅŸiâ€¦"
                            value={q}
                            onChange={(e) => setQ(e.target.value)}
                        />
                    </div>
                    <div className="toggles">
                        <label className="toggle">
                            <input type="checkbox" checked={onlySLA} onChange={(e) => setOnlySLA(e.target.checked)} />
                            <span>SLA (&lt;24s)</span>
                        </label>
                    </div>
                    <button className="btn ghost" onClick={fetchData}>Yenile</button>
                    <button
                        className="btn primary"
                        onClick={() => navigate("/inbox")}
                    >
                        Benim Inboxâ€™Ä±m
                    </button>
                </div>
            </div>

            {err && <div className="mx-alert err">{err}</div>}
            {loading && <div className="mx-skel">YÃ¼kleniyorâ€¦</div>}

            {/* Ã–zet barÄ± */}
            <div className="board" style={{ gap: 8, marginTop: 8 }}>
                {MONITORED_USERS.map(u => {
                    const cols = byUser.get(u.name);
                    const total = cols ? Object.values(cols).reduce((n, arr) => n + arr.length, 0) : 0;
                    return (
                        <div key={u.name} className="col" style={{ minWidth: 320 }}>
                            <div className="col-head">
                                <span className="dot"></span>
                                <h3>{u.name}</h3>
                                <span className="count">{total}</span>
                            </div>

                            <div className="col-body">
                                {/* Her kullanÄ±cÄ± iÃ§in yatay mini-kanban */}
                                <div className="board" style={{ gridTemplateColumns: `repeat(${BOARD_COLUMNS.length}, minmax(220px,1fr))`, gap: 8 }}>
                                    {BOARD_COLUMNS.map(col => {
                                        const items = cols?.[col.key] || [];
                                        return (
                                            <div key={col.key} className="col" style={{ minWidth: 220 }}>
                                                <div className="col-head">
                                                    <span className={`dot s-${slug(col.title)}`}></span>
                                                    <h4 style={{ margin: 0 }}>{col.title}</h4>
                                                    <span className="count">{items.length}</span>
                                                </div>
                                                <div className="col-body">
                                                    {items.length === 0 && <div className="col-empty">Yok</div>}
                                                    {items.slice(0, 6).map(item => (
                                                        <button
                                                            key={item.id}
                                                            className={`card ${item.durum === STATUS.Tamamlandi ? "mine" : ""}`}
                                                            style={{ textAlign: "left", cursor: "pointer" }}
                                                            onClick={() => navigate(`/inbox?assignee=${encodeURIComponent(item.atanan_email || "")}`)}
                                                            title={`#${item.id} â€” ${item.baslik}\nGÃ¼ncellenme: ${fmtTime(item.guncelleme_tarihi)}`}
                                                        >
                                                            <div className="c-title">#{item.id} â€” {item.baslik}</div>
                                                            <div className="c-desc" style={{ opacity: 0.8 }}>
                                                                {item.talep_eden_kullanici || item.talep_eden_email || "â€”"}
                                                            </div>
                                                        </button>
                                                    ))}
                                                    {items.length > 6 && (
                                                        <div className="muted" style={{ marginTop: 6 }}>
                                                            +{items.length - 6} daha
                                                        </div>
                                                    )}
                                                </div>
                                            </div>
                                        );
                                    })}
                                </div>
                            </div>
                        </div>
                    );
                })}
            </div>

            <div className="muted" style={{ marginTop: 8 }}>
                Kartlara tÄ±klayÄ±nca ilgili kiÅŸinin inboxâ€™Ä±na gidersin (URLâ€™de <code>assignee</code> parametresiyle).
            </div>
        </div>
    );
}
