import React, { useEffect, useMemo, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import supabase from "../supabaseClient";
import "./Inbox.css";

/** Durum -> Kolon başlığı / sırası */
const BOARD_COLUMNS = [
    { key: "Yeni", title: "Yeni" },
    { key: "İnceleme", title: "İnceleme" },
    { key: "Atandı", title: "Atandı" },
    { key: "Çalışılıyor", title: "Çalışılıyor" },
    { key: "Beklemede", title: "Beklemede" },
    { key: "Tamamlandı", title: "Tamamlandı" },
];

const PILL_COLOR = { P1: "p1", P2: "p2", P3: "p3" };

export default function InboxModern() {
    const navigate = useNavigate();

    // --- auth/profile
    const me = useMemo(() => {
        try { return JSON.parse(localStorage.getItem("user_profile") || "{}"); }
        catch { return {}; }
    }, []);
    const myEmail = me?.email || "";

    // --- ui state
    const [view, setView] = useState("board"); // "board" | "list"
    const [q, setQ] = useState(""); // search
    const [mineOnly, setMineOnly] = useState(false);
    const [onlySLA, setOnlySLA] = useState(false);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");
    const [rows, setRows] = useState([]);
    const [active, setActive] = useState(null); // detay çekmecesi

    // --- guard
    useEffect(() => {
        const token = localStorage.getItem("auth_token");
        if (!token) navigate("/login", { replace: true });
    }, [navigate]);

    // --- data
    useEffect(() => { fetchData(); /* eslint-disable-next-line */ }, [mineOnly, onlySLA]);

    async function fetchData() {
        if (!myEmail) return;
        setLoading(true); setErr("");

        try {
            const now = new Date();
            let query = supabase.from("requests").select(`
        id, title, status, priority, requester_email, assignee_email,
        updated_at, due_at, sla_deadline_at, description
      `).order("updated_at", { ascending: false }).limit(300);

            if (mineOnly) {
                query = query.eq("assignee_email", myEmail);
            }
            if (onlySLA) {
                const t24 = new Date(now.getTime() + 24 * 60 * 60 * 1000).toISOString();
                query = query.lt("sla_deadline_at", t24).not("status", "in", ["Tamamlandı", "Reddedildi"]);
            }

            const { data, error } = await query;
            if (error) throw error;

            setRows(data || []);
        } catch (e) {
            console.error(e);
            setErr("Kayıtlar çekilemedi.");
        } finally {
            setLoading(false);
        }
    }

    // --- actions
    async function takeOwnership(id) {
        if (!myEmail) return;
        const { error } = await supabase.from("requests").update({
            assignee_email: myEmail, status: "İnceleme", updated_at: new Date().toISOString()
        }).eq("id", id);
        if (!error) fetchData();
    }

    async function moveTo(id, status) {
        const { error } = await supabase.from("requests").update({
            status, updated_at: new Date().toISOString()
        }).eq("id", id);
        if (!error) fetchData();
    }

    async function markDone(id) {
        await moveTo(id, "Tamamlandı");
    }

    function handleLogout() {
        localStorage.removeItem("auth_token");
        localStorage.removeItem("user_profile");
        navigate("/login", { replace: true });
    }

    // --- filter/search
    const filtered = rows.filter(r => {
        if (!q) return true;
        const text = `${r.id} ${r.title} ${r.requester_email} ${r.assignee_email || ""}`.toLowerCase();
        return text.includes(q.toLowerCase());
    });

    // --- board grouping
    const group = BOARD_COLUMNS.map(col => ({
        ...col,
        items: filtered.filter(r => (r.status || "Yeni") === col.key)
    }));

    return (
        <div className="mx">
            {/* Command bar */}
            <div className="mx-top">
                <div className="brand">
                    <div className="logo">🧭</div>
                    <div className="bt">
                        <h1>Gelen Kutusu</h1>
                        <span className="muted">İş & Süreç Geliştirme</span>
                    </div>
                </div>

                <div className="cmd">
                    <div className="seg">
                        <button className={`seg-btn ${view === "board" ? "on" : ""}`} onClick={() => setView("board")}>Kanban</button>
                        <button className={`seg-btn ${view === "list" ? "on" : ""}`} onClick={() => setView("list")}>Liste</button>
                    </div>

                    <div className="search-wrap">
                        <input
                            className="search"
                            placeholder="Ara: #id, başlık, kişi…"
                            value={q}
                            onChange={(e) => setQ(e.target.value)}
                        />
                    </div>

                    <div className="toggles">
                        <label className="toggle">
                            <input type="checkbox" checked={mineOnly} onChange={e => setMineOnly(e.target.checked)} />
                            <span>Bana atananlar</span>
                        </label>
                        <label className="toggle">
                            <input type="checkbox" checked={onlySLA} onChange={e => setOnlySLA(e.target.checked)} />
                            <span>SLA (&lt;24s)</span>
                        </label>
                    </div>

                    <Link to="/newrequest" className="btn primary">+ Yeni Talep</Link>
                    <div className="chip">{me?.kullanici || me?.email}</div>
                    <button className="btn danger" onClick={handleLogout}>Çıkış</button>
                </div>
            </div>

            {err && <div className="mx-alert err">{err}</div>}

            {/* Content */}
            {loading ? (
                <div className="mx-skel">Yükleniyor…</div>
            ) : view === "board" ? (
                <div className="board">
                    {group.map(col => (
                        <div className="col" key={col.key}>
                            <div className="col-head">
                                <span className={`dot s-${slug(col.key)}`}></span>
                                <h3>{col.title}</h3>
                                <span className="count">{col.items.length}</span>
                            </div>
                            <div className="col-body">
                                {col.items.length === 0 && <div className="col-empty">Henüz kayıt yok</div>}
                                {col.items.map(item => (
                                    <Card
                                        key={item.id}
                                        data={item}
                                        onOpen={() => setActive(item)}
                                        onTake={() => takeOwnership(item.id)}
                                        onMove={st => moveTo(item.id, st)}
                                        onDone={() => markDone(item.id)}
                                        me={myEmail}
                                    />
                                ))}
                            </div>
                        </div>
                    ))}
                </div>
            ) : (
                <div className="listv">
                    {filtered.length === 0 && <div className="col-empty">Kayıt bulunamadı</div>}
                    {filtered.map(r => (
                        <div key={r.id} className="rowv" onClick={() => setActive(r)}>
                            <span className={`pill ${PILL_COLOR[r.priority] || "p3"}`}>{r.priority || "P3"}</span>
                            <span className={`badge s-${slug(r.status || "Yeni")}`}>{r.status || "Yeni"}</span>
                            <span className="r-title">#{r.id} — {r.title}</span>
                            <span className="r-sub">{r.requester_email} → {r.assignee_email || "—"}</span>
                            <span className="r-time">{fmtTime(r.updated_at)}</span>
                        </div>
                    ))}
                </div>
            )}

            {/* Detay çekmecesi */}
            {active && (
                <div className="drawer" role="dialog" aria-modal="true">
                    <div className="drawer-panel">
                        <button className="drawer-close" onClick={() => setActive(null)}>×</button>

                        <div className="drawer-top">
                            <span className={`pill ${PILL_COLOR[active.priority] || "p3"}`}>{active.priority || "P3"}</span>
                            <span className={`badge s-${slug(active.status || "Yeni")}`}>{active.status || "Yeni"}</span>
                            <h2>#{active.id} — {active.title}</h2>
                            <div className="d-sub">
                                <span>İsteyen: <b>{active.requester_email}</b></span>
                                <span> · </span>
                                <span>Atanan: <b>{active.assignee_email || "—"}</b></span>
                                <span> · </span>
                                <span>Güncellendi: {fmtTime(active.updated_at)}</span>
                            </div>
                        </div>

                        <div className="drawer-body">
                            <Section title="Açıklama">
                                <p className="desc">
                                    {active.description || "Açıklama girilmemiş."}
                                </p>
                            </Section>

                            <Section title="Hızlı İşlemler">
                                <div className="act-grid">
                                    {!active.assignee_email && (
                                        <button className="btn ghost" onClick={() => { setActive(null); takeOwnership(active.id); }}>Üstlen</button>
                                    )}
                                    <button className="btn ghost" onClick={() => { setActive(null); moveTo(active.id, "İnceleme"); }}>İnceleme</button>
                                    <button className="btn ghost" onClick={() => { setActive(null); moveTo(active.id, "Atandı"); }}>Atandı</button>
                                    <button className="btn ghost" onClick={() => { setActive(null); moveTo(active.id, "Çalışılıyor"); }}>Çalışılıyor</button>
                                    <button className="btn ghost" onClick={() => { setActive(null); moveTo(active.id, "Beklemede"); }}>Beklemede</button>
                                    <button className="btn primary" onClick={() => { setActive(null); markDone(active.id); }}>✓ Tamamlandı</button>
                                </div>
                            </Section>

                            <Section title="Zaman">
                                <div className="kv">
                                    <div><span>Hedef Tarih:</span><b>{active.due_at ? fmtDate(active.due_at) : "—"}</b></div>
                                    <div><span>SLA Son:</span><b>{active.sla_deadline_at ? fmtDate(active.sla_deadline_at) : "—"}</b></div>
                                </div>
                            </Section>
                        </div>
                    </div>
                    <div className="drawer-backdrop" onClick={() => setActive(null)} />
                </div>
            )}
        </div>
    );
}

/* -------- components -------- */

function Card({ data, onOpen, onTake, onMove, onDone, me }) {
    const mine = data.assignee_email && data.assignee_email === me;
    const slaSoon = data.sla_deadline_at && new Date(data.sla_deadline_at).getTime() - Date.now() < 24 * 60 * 60 * 1000;

    return (
        <div className={`card ${mine ? "mine" : ""}`}>
            <div className="c-top">
                <span className={`pill ${PILL_COLOR[data.priority] || "p3"}`}>{data.priority || "P3"}</span>
                <span className={`badge s-${slug(data.status || "Yeni")}`}>{data.status || "Yeni"}</span>
                {slaSoon && <span className="sla">SLA</span>}
                <button className="icn" onClick={onOpen} title="Detay">⋯</button>
            </div>

            <div className="c-title">#{data.id} — {data.title}</div>
            <div className="c-sub">
                <span>{data.requester_email}</span>
                <span>→</span>
                <span>{data.assignee_email || "—"}</span>
            </div>

            <div className="c-foot">
                <span className="time">{fmtTime(data.updated_at)}</span>

                <div className="act">
                    {!data.assignee_email && <button className="btn ghost xs" onClick={onTake}>Üstlen</button>}
                    <button className="btn ghost xs" onClick={() => onMove("İnceleme")}>İnceleme</button>
                    <button className="btn ghost xs" onClick={() => onMove("Çalışılıyor")}>Çalışılıyor</button>
                    <button className="btn primary xs" onClick={onDone}>Tamamla</button>
                </div>
            </div>
        </div>
    );
}

function Section({ title, children }) {
    return (
        <section className="sec">
            <div className="sec-h">
                <h4>{title}</h4>
            </div>
            <div className="sec-b">{children}</div>
        </section>
    );
}

/* -------- utils -------- */
function slug(s) { return (s || "").toLowerCase().replaceAll("ı", "i").replaceAll("ğ", "g").replaceAll("ş", "s").replaceAll("ö", "o").replaceAll("ç", "c").replaceAll("ü", "u").replace(/\s+/g, "-"); }
function fmtTime(ts) { try { return new Date(ts).toLocaleString(); } catch { return "—"; } }
function fmtDate(ts) { try { return new Date(ts).toLocaleDateString(); } catch { return "—"; } }
