// src/pages/Inbox.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";
import supabase from "../supabaseClient";
import "./Inbox.css";

/**
 * Realtime için:
 *   alter publication supabase_realtime add table public.talepler;
 *
 * (Opsiyonel) Global liste sırası için:
 *   create or replace function set_global_order(ids int[])
 *   returns void language plpgsql as $$
 *   begin
 *     update talepler t set global_sira = s.ord
 *     from (select unnest(ids) as id, generate_series(1, array_length(ids,1)) as ord) s
 *     where t.id = s.id;
 *   end $$;
 */

/* ---- DURUMLAR ----
   DB kolonundaki "durum" değerleri *aynen* aşağıdakiler gibi olmalı.
   (CHECK constraint bu değerleri bekliyor)
*/
const STATUS = {
    Yeni: "Yeni",
    SirayaAlindi: "SirayaAlindi",
    IslemeAlindi: "IslemeAlindi",
    TestEdiliyor: "TestEdiliyor",
    Tamamlandi: "Tamamlandi",
    // Reddedildi: "Reddedildi",
};

/* UI'de görünen etiketler */
const STATUS_DISPLAY = {
    [STATUS.Yeni]: "Yeni",
    [STATUS.SirayaAlindi]: "Sıraya Alındı",
    [STATUS.IslemeAlindi]: "İşleme Alındı",
    [STATUS.TestEdiliyor]: "Test Ediliyor",
    [STATUS.Tamamlandi]: "Tamamlandı",
    // [STATUS.Reddedildi]: "Reddedildi",
};

const BOARD_COLUMNS = [
    { key: STATUS.Yeni, title: STATUS_DISPLAY[STATUS.Yeni] },
    { key: STATUS.SirayaAlindi, title: STATUS_DISPLAY[STATUS.SirayaAlindi] },
    { key: STATUS.IslemeAlindi, title: STATUS_DISPLAY[STATUS.IslemeAlindi] },
    { key: STATUS.TestEdiliyor, title: STATUS_DISPLAY[STATUS.TestEdiliyor] },
    { key: STATUS.Tamamlandi, title: STATUS_DISPLAY[STATUS.Tamamlandi] },
    // { key: STATUS.Reddedildi, title: STATUS_DISPLAY[STATUS.Reddedildi] },
];

const PILL_COLOR = { P1: "p1", P2: "p2", P3: "p3" };

/* ---- YETKİ ---- */
function trUp(s) {
    return (s || "").trim().toLocaleUpperCase("tr-TR");
}
const ALLOWED_NAMES = new Set(["GÖRKEM ÇADIRCI", "FURKAN BİLGİLİ", "YAĞIZ EFE BULUTCU"]);
function computeIsEditor(profile) {
    if (profile && typeof profile.isEditor === "boolean") return profile.isEditor;
    return (
        trUp(profile?.birim) === "İŞ VE SÜREÇ GELİŞTİRME" &&
        ALLOWED_NAMES.has(trUp(profile?.kullanici || profile?.email))
    );
}

export default function InboxModern() {
    const navigate = useNavigate();

    const me = useMemo(() => {
        try { return JSON.parse(localStorage.getItem("user_profile") || "{}"); }
        catch { return {}; }
    }, []);
    const myEmail = me?.email || "";
    const myName = (me?.kullanici || "").trim();
    const canEdit = useMemo(() => computeIsEditor(me), [me]);

    const [view, setView] = useState("board"); // "board" | "list"
    const [q, setQ] = useState("");
    const [mineOnly, setMineOnly] = useState(false);
    const [onlySLA, setOnlySLA] = useState(false);
    const [loading, setLoading] = useState(false);
    const [saving, setSaving] = useState(false);
    const [err, setErr] = useState("");
    const [rows, setRows] = useState([]);
    const [active, setActive] = useState(null);

    const mineOnlyRef = useRef(mineOnly);
    useEffect(() => { mineOnlyRef.current = mineOnly; }, [mineOnly]);

    // auth guard
    useEffect(() => {
        const token = localStorage.getItem("auth_token");
        if (!token) navigate("/login", { replace: true });
    }, [navigate]);

    // data
    useEffect(() => { fetchData(); /* eslint-disable-next-line */ }, [mineOnly, onlySLA, view]);

    // realtime
    useEffect(() => {
        const channel = supabase
            .channel("talepler-realtime")
            .on("postgres_changes", { event: "*", schema: "public", table: "talepler" },
                (payload) => setRows((prev) => applyRealtime(prev, payload, mineOnlyRef.current, view)))
            .subscribe();
        return () => supabase.removeChannel(channel);
    }, [view]);

    // görünüm değişince sırala
    useEffect(() => { setRows((prev) => sortRows(prev, mineOnly, view)); }, [mineOnly, view]);

    async function fetchData() {
        if (!myEmail) return;
        setLoading(true);
        setErr("");
        try {
            let query = supabase.from("talepler").select(`
        id, baslik, durum, oncelik,
        talep_eden_email, atanan_email,
        talep_eden_kullanici, atanan_kullanici,
        guncelleme_tarihi, bitis_tarihi, sla_son_tarih, aciklama,
        kolon_sira, atanan_sira, global_sira
      `);

            if (view === "list" && !mineOnly) {
                query = query
                    .order("global_sira", { ascending: true, nullsFirst: true })
                    .order("guncelleme_tarihi", { ascending: false });
            } else if (mineOnly) {
                query = query
                    .eq("atanan_email", myEmail)
                    .order("atanan_sira", { ascending: true, nullsFirst: false })
                    .order("guncelleme_tarihi", { ascending: false });
            } else {
                query = query
                    .order("kolon_sira", { ascending: true, nullsFirst: true })
                    .order("guncelleme_tarihi", { ascending: false });
            }

            if (onlySLA) {
                const t24 = new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString();
                query = query
                    .lt("sla_son_tarih", t24)
                    .not("durum", "in", '("Tamamlandi","Reddedildi")');
            }

            const { data, error } = await query.limit(500);
            if (error) throw error;

            setRows(sortRows(data || [], mineOnly, view));
        } catch (e) {
            console.error(e);
            setErr("Kayıtlar çekilemedi.");
        } finally {
            setLoading(false);
        }
    }

    /* ---------- helpers ---------- */
    function groupByStatus(items) {
        const map = Object.fromEntries(BOARD_COLUMNS.map((c) => [c.key, []]));
        items.forEach((i) => { (map[i.durum || STATUS.Yeni] ??= []).push(i); });
        return map;
    }

    function recomputeAssigneeRanks(list) {
        const byA = {};
        list.forEach((r) => {
            if (!r.atanan_email) return;
            (byA[r.atanan_email] ??= []).push(r);
        });
        Object.values(byA).forEach((arr) => { arr.forEach((r, idx) => { r.atanan_sira = idx + 1; }); });
    }

    // SADECE UPDATE — upsert yok — durum değeri DB'nin beklediği *kod* ile yazılır
    async function persistOrder(updates) {
        if (!canEdit) return;
        if (!updates?.length) return;

        const nowIso = new Date().toISOString();
        const rowsToSave = updates.map((u) => {
            const changes = { guncelleme_tarihi: nowIso };
            if (u.durum !== undefined) changes.durum = u.durum;          // <— ÖNEMLİ
            if (u.kolon_sira !== undefined) changes.kolon_sira = u.kolon_sira;
            if (u.atanan_sira !== undefined) changes.atanan_sira = u.atanan_sira;
            return { id: u.id, changes };
        });

        try {
            setSaving(true);
            const results = await Promise.all(
                rowsToSave.map(({ id, changes }) => supabase.from("talepler").update(changes).eq("id", id))
            );
            const anyErr = results.find((r) => r.error);
            if (anyErr) {
                console.error("persistOrder/update error(s):", results.map((r) => r.error).filter(Boolean));
                setErr("Bazı kayıtlar kaydedilemedi.");
            }
        } finally {
            setSaving(false);
        }
    }

    /* ---------- DnD: Kanban ---------- */
    async function onBoardDragEnd(result) {
        if (!canEdit) return;
        const { source, destination, draggableId } = result;
        if (!destination) return;

        const statusFrom = source.droppableId;
        const statusTo = destination.droppableId;

        const byStatus = groupByStatus([...rows]);
        const idx = byStatus[statusFrom].findIndex((i) => String(i.id) === String(draggableId));
        if (idx === -1) return;

        const item = byStatus[statusFrom][idx];
        if (item.atanan_email !== myEmail) return; // sadece atanan taşıyabilir

        // optimistic
        byStatus[statusFrom].splice(idx, 1);
        byStatus[statusTo].splice(destination.index, 0, item);

        const flattened = BOARD_COLUMNS.flatMap((c) => byStatus[c.key]);
        const prevById = new Map(rows.map((r) => [r.id, r]));

        const updates = [];
        BOARD_COLUMNS.forEach((col) => {
            byStatus[col.key].forEach((it, i) => {
                const newDurum = col.key;      // <— DB'ye yazılacak kod
                const newKolonSira = i + 1;
                const prev = prevById.get(it.id) || {};
                let dirty = false;
                if (it.durum !== newDurum) { it.durum = newDurum; dirty = true; }
                if (it.kolon_sira !== newKolonSira) { it.kolon_sira = newKolonSira; dirty = true; }
                if (dirty) updates.push({ id: it.id, durum: newDurum, kolon_sira: newKolonSira });
            });
        });

        recomputeAssigneeRanks(flattened);
        flattened.forEach((it) => {
            const prev = prevById.get(it.id) || {};
            if ((prev.atanan_sira ?? null) !== (it.atanan_sira ?? null)) {
                const u = updates.find((u) => u.id === it.id);
                if (u) u.atanan_sira = it.atanan_sira;
                else updates.push({ id: it.id, atanan_sira: it.atanan_sira });
            }
        });

        setRows(sortRows(flattened, mineOnly, view));
        await persistOrder(updates);
    }

    /* ---------- DnD: Liste ---------- */
    async function onListDragEnd(result) {
        const { destination, draggableId } = result;
        if (!destination) return;

        if (!mineOnly) {
            // GLOBAL sıra: yalnızca yetkililer
            if (!canEdit) return;

            const list = [...filtered];
            const from = list.findIndex((i) => String(i.id) === String(draggableId));
            if (from === -1) return;
            const moved = list[from];
            list.splice(from, 1);
            list.splice(destination.index, 0, moved);

            const ids = list.map((r) => Number(r.id));

            // optimistic
            const orderMap = new Map(ids.map((id, i) => [String(id), i + 1]));
            const newRows = rows.map((r) =>
                orderMap.has(String(r.id)) ? { ...r, global_sira: orderMap.get(String(r.id)) } : r
            );
            setRows(sortRows(newRows, mineOnly, view));

            setSaving(true);
            const { error } = await supabase.rpc("set_global_order", { ids });
            setSaving(false);

            if (error) {
                console.error(error);
                setErr("Global sıralama kaydedilemedi.");
            } else {
                fetchData();
            }
            return;
        }

        // mineOnly: kişisel kuyruk (atanan_sira)
        if (!canEdit) return;

        const movedRow = rows.find((r) => String(r.id) === String(draggableId));
        if (!movedRow || movedRow.atanan_email !== myEmail) return;

        const list = filtered.filter((r) => r.atanan_email === myEmail);
        const from = list.findIndex((i) => String(i.id) === String(draggableId));
        if (from === -1) return;

        const moved = list[from];
        list.splice(from, 1);
        list.splice(destination.index, 0, moved);

        list.forEach((r, i) => { r.atanan_sira = i + 1; });

        const newRows2 = rows.map((r) => {
            const hit = list.find((x) => x.id === r.id);
            return hit ? { ...r, atanan_sira: hit.atanan_sira } : r;
        });
        setRows(sortRows(newRows2, mineOnly, view));

        const updates = list
            .filter((r) => r.id && rows.find((x) => x.id === r.id)?.atanan_sira !== r.atanan_sira)
            .map(({ id, atanan_sira }) => ({ id, atanan_sira }));

        await persistOrder(updates);
    }

    /* ---------- quick actions ---------- */
    async function takeOwnership(id) {
        if (!canEdit) return;
        if (!myEmail) return;
        const row = rows.find((r) => r.id === id);
        if (row && row.atanan_email && row.atanan_email !== myEmail) {
            setErr("Bu kayıt size atanmadığı için işlem yapamazsınız.");
            return;
        }
        const myColMax =
            rows.filter((r) => r.durum === STATUS.SirayaAlindi)
                .reduce((m, r) => Math.max(m, r.kolon_sira || 0), 0) + 1;
        const myQueueMax =
            rows.filter((r) => r.atanan_email === myEmail)
                .reduce((m, r) => Math.max(m, r.atanan_sira || 0), 0) + 1;

        // optimistic
        setRows((prev) => {
            const a = prev.map((r) =>
                r.id === id
                    ? {
                        ...r,
                        atanan_email: myEmail,
                        atanan_kullanici: myName || null,
                        durum: STATUS.SirayaAlindi,       // <— DB kodu
                        kolon_sira: myColMax,
                        atanan_sira: myQueueMax,
                        guncelleme_tarihi: new Date().toISOString(),
                    }
                    : r
            );
            return sortRows(a, mineOnly, view);
        });

        setSaving(true);
        const { error } = await supabase
            .from("talepler")
            .update({
                atanan_email: myEmail,
                atanan_kullanici: myName || null,
                durum: STATUS.SirayaAlindi,           // <— DB kodu
                kolon_sira: myColMax,
                atanan_sira: myQueueMax,
                guncelleme_tarihi: new Date().toISOString(),
            })
            .eq("id", id);
        setSaving(false);

        if (error) {
            console.error(error);
            setErr("Bu kayıt üzerinde işlem yetkiniz yok.");
            fetchData();
        }
    }

    async function moveTo(id, statusKey /* one of STATUS.* */) {
        if (!canEdit) return;
        const row = rows.find((r) => r.id === id);
        if (!row || row.atanan_email !== myEmail) {
            setErr("Bu kayıt size atanmadığı için işlem yapamazsınız.");
            return;
        }
        const max =
            rows.filter((r) => r.durum === statusKey)
                .reduce((m, r) => Math.max(m, r.kolon_sira || 0), 0) + 1;

        // optimistic
        setRows((prev) => {
            const a = prev.map((r) =>
                r.id === id
                    ? { ...r, durum: statusKey, kolon_sira: max, guncelleme_tarihi: new Date().toISOString() }
                    : r
            );
            return sortRows(a, mineOnly, view);
        });

        setSaving(true);
        const { error } = await supabase
            .from("talepler")
            .update({
                durum: statusKey,                      // <— DB kodu
                kolon_sira: max,
                guncelleme_tarihi: new Date().toISOString(),
            })
            .eq("id", id);
        setSaving(false);

        if (error) {
            console.error(error);
            setErr("Bu kayıt üzerinde işlem yetkiniz yok.");
            fetchData();
        }
    }

    async function markDone(id) {
        await moveTo(id, STATUS.Tamamlandi);
    }

    function handleLogout() {
        localStorage.removeItem("auth_token");
        localStorage.removeItem("user_profile");
        navigate("/login", { replace: true });
    }

    /* ---------- filter ---------- */
    const filtered = rows.filter((r) => {
        if (!q) return true;
        const text = `${r.id} ${r.baslik} ${r.talep_eden_email} ${r.atanan_email || ""} ${r.talep_eden_kullanici || ""} ${r.atanan_kullanici || ""}`.toLowerCase();
        return text.includes(q.toLowerCase());
    });

    const grouped = groupByStatus(filtered);

    return (
        <div className="mx">
            {/* Command bar */}
            <div className="mx-top">
                <div className="brand">
                    <div className="logo">🧭</div>
                    <div className="bt">
                        <h1>Gelen Kutusu</h1>
                        <span className="muted">İş & Süreç Geliştirme</span>
                    </div>
                </div>

                <div className="cmd">
                    <div className="seg">
                        <button className={`seg-btn ${view === "board" ? "on" : ""}`} onClick={() => setView("board")}>Kanban</button>
                        <button className={`seg-btn ${view === "list" ? "on" : ""}`} onClick={() => setView("list")}>Liste</button>
                    </div>

                    <div className="search-wrap">
                        <input className="search" placeholder="Ara: #id, başlık, kişi…" value={q} onChange={(e) => setQ(e.target.value)} />
                    </div>

                    <div className="toggles">
                        <label className="toggle">
                            <input type="checkbox" checked={mineOnly} onChange={(e) => setMineOnly(e.target.checked)} />
                            <span>Bana atananlar</span>
                        </label>
                        <label className="toggle">
                            <input type="checkbox" checked={onlySLA} onChange={(e) => setOnlySLA(e.target.checked)} />
                            <span>SLA (&lt;24s)</span>
                        </label>
                    </div>

                    <Link to="/newrequest" className="btn primary">+ Yeni Talep</Link>
                    <div className="chip">{(me?.kullanici || me?.email) + (canEdit ? " • editör" : "")}</div>
                    <button className="btn danger" onClick={handleLogout}>Çıkış</button>
                    {saving && <div className="saving-ind">Kaydediliyor…</div>}
                </div>
            </div>

            {err && <div className="mx-alert err">{err}</div>}

            {loading ? (
                <div className="mx-skel">Yükleniyor…</div>
            ) : view === "board" ? (
                <DragDropContext onDragEnd={onBoardDragEnd}>
                    <div className="board">
                        {BOARD_COLUMNS.map((col) => {
                            const items = grouped[col.key] || [];
                            return (
                                <Droppable droppableId={col.key} key={col.key} isDropDisabled={!canEdit}>
                                    {(provided) => (
                                        <div className="col" ref={provided.innerRef} {...provided.droppableProps}>
                                            <div className="col-head">
                                                <span className={`dot s-${slug(col.title)}`}></span>
                                                <h3>{col.title}</h3>
                                                <span className="count">{items.length}</span>
                                            </div>
                                            <div className="col-body">
                                                {items.length === 0 && <div className="col-empty">Henüz kayıt yok</div>}
                                                {items.map((item, index) => (
                                                    <Draggable
                                                        draggableId={String(item.id)}
                                                        index={index}
                                                        key={item.id}
                                                        isDragDisabled={!canEdit || !(item.atanan_email && item.atanan_email === myEmail)}
                                                    >
                                                        {(pp) => (
                                                            <div ref={pp.innerRef} {...pp.draggableProps} {...pp.dragHandleProps}>
                                                                <Card
                                                                    data={item}
                                                                    onOpen={() => setActive(item)}
                                                                    onTake={() => canEdit && takeOwnership(item.id)}
                                                                    onMove={(st) => canEdit && moveTo(item.id, st)}
                                                                    onDone={() => canEdit && markDone(item.id)}
                                                                    me={myEmail}
                                                                />
                                                            </div>
                                                        )}
                                                    </Draggable>
                                                ))}
                                                {provided.placeholder}
                                            </div>
                                        </div>
                                    )}
                                </Droppable>
                            );
                        })}
                    </div>
                </DragDropContext>
            ) : (
                <DragDropContext onDragEnd={onListDragEnd}>
                    <Droppable droppableId="listMine" isDropDisabled={!canEdit && !mineOnly}>
                        {(provided) => (
                            <div className="listv" ref={provided.innerRef} {...provided.droppableProps}>
                                {filtered.length === 0 && <div className="col-empty">Kayıt bulunamadı</div>}
                                {filtered.map((r, index) => (
                                    <Draggable
                                        draggableId={String(r.id)}
                                        index={index}
                                        key={r.id}
                                        isDragDisabled={!canEdit && !mineOnly}
                                    >
                                        {(pp) => (
                                            <div
                                                ref={pp.innerRef}
                                                {...pp.draggableProps}
                                                {...pp.dragHandleProps}
                                                className="rowv"
                                                onClick={() => setActive(r)}
                                            >
                                                <span className={`pill ${PILL_COLOR[r.oncelik] || "p3"}`}>{r.oncelik || "P3"}</span>
                                                <span className={`badge s-${slug(STATUS_DISPLAY[r.durum] || "Yeni")}`}>
                                                    {STATUS_DISPLAY[r.durum] || "Yeni"}
                                                </span>
                                                <span className="r-title">#{r.id} — {r.baslik}</span>
                                                <span className="r-sub">
                                                    {(r.talep_eden_kullanici || r.talep_eden_email) || "—"} → {(r.atanan_kullanici || r.atanan_email) || "—"}
                                                    {mineOnly && r.atanan_email === myEmail && (
                                                        <b style={{ marginLeft: 8, opacity: 0.8 }}>(Sıra: {r.atanan_sira ?? "—"})</b>
                                                    )}
                                                </span>
                                                <span className="r-time">{fmtTime(r.guncelleme_tarihi)}</span>
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                                {!mineOnly && (
                                    <div className="hint" style={{ padding: 8, opacity: 0.7 }}>
                                        {canEdit
                                            ? "Bu listede sürüklediğin sıra herkese yansır."
                                            : "Sıralamayı yalnızca yetkili kullanıcılar değiştirebilir."}
                                    </div>
                                )}
                            </div>
                        )}
                    </Droppable>
                </DragDropContext>
            )}

            {/* Detay çekmecesi */}
            {active && (
                <div className="drawer" role="dialog" aria-modal="true">
                    <div className="drawer-panel">
                        <button className="drawer-close" onClick={() => setActive(null)}>×</button>

                        <div className="drawer-top">
                            <span className={`pill ${PILL_COLOR[active.oncelik] || "p3"}`}>{active.oncelik || "P3"}</span>
                            <span className={`badge s-${slug(STATUS_DISPLAY[active.durum] || "Yeni")}`}>
                                {STATUS_DISPLAY[active.durum] || "Yeni"}
                            </span>
                            {active.sla_son_tarih &&
                                new Date(active.sla_son_tarih).getTime() - Date.now() < 24 * 60 * 60 * 1000 && (
                                    <span className="sla">SLA</span>
                                )}
                            <h2>#{active.id} — {active.baslik}</h2>
                            <div className="d-sub">
                                <span>İsteyen: <b>{active.talep_eden_kullanici || active.talep_eden_email}</b></span>
                                <span> · </span>
                                <span>Atanan: <b>{active.atanan_kullanici || active.atanan_email || "—"}</b></span>
                                <span> · </span>
                                <span>Güncellendi: {fmtTime(active.guncelleme_tarihi)}</span>
                            </div>
                        </div>

                        <div className="drawer-body">
                            <Section title="Açıklama">
                                <p className="desc">{active.aciklama || "Açıklama girilmemiş."}</p>
                            </Section>

                            <Section title="Hızlı İşlemler">
                                <div className="act-grid">
                                    <button
                                        className="btn ghost"
                                        onClick={() => { if (canEdit) { setActive(null); takeOwnership(active.id); } }}
                                        disabled={!canEdit || (!!active.atanan_email && active.atanan_email !== myEmail)}
                                        title={
                                            !canEdit
                                                ? "Yalnızca yetkili kullanıcılar işlem yapabilir"
                                                : active.atanan_email && active.atanan_email !== myEmail
                                                    ? "Sadece atanan işlem yapabilir"
                                                    : undefined
                                        }
                                    >
                                        Üstlen
                                    </button>

                                    <button
                                        className="btn ghost"
                                        onClick={() => { if (canEdit && active.atanan_email === myEmail) { setActive(null); moveTo(active.id, STATUS.SirayaAlindi); } }}
                                        disabled={!canEdit || active.atanan_email !== myEmail}
                                        title={!canEdit ? "Yalnızca yetkili kullanıcılar" : active.atanan_email !== myEmail ? "Sadece atanan" : undefined}
                                    >
                                        Sıraya alındı
                                    </button>

                                    <button
                                        className="btn ghost"
                                        onClick={() => { if (canEdit && active.atanan_email === myEmail) { setActive(null); moveTo(active.id, STATUS.IslemeAlindi); } }}
                                        disabled={!canEdit || active.atanan_email !== myEmail}
                                        title={!canEdit ? "Yalnızca yetkili kullanıcılar" : active.atanan_email !== myEmail ? "Sadece atanan" : undefined}
                                    >
                                        İşleme alındı
                                    </button>

                                    <button
                                        className="btn ghost"
                                        onClick={() => { if (canEdit && active.atanan_email === myEmail) { setActive(null); moveTo(active.id, STATUS.TestEdiliyor); } }}
                                        disabled={!canEdit || active.atanan_email !== myEmail}
                                        title={!canEdit ? "Yalnızca yetkili kullanıcılar" : active.atanan_email !== myEmail ? "Sadece atanan" : undefined}
                                    >
                                        Test ediliyor
                                    </button>

                                    <button
                                        className="btn primary"
                                        onClick={() => { if (canEdit && active.atanan_email === myEmail) { setActive(null); markDone(active.id); } }}
                                        disabled={!canEdit || active.atanan_email !== myEmail}
                                        title={!canEdit ? "Yalnızca yetkili kullanıcılar" : active.atanan_email !== myEmail ? "Sadece atanan" : undefined}
                                    >
                                        ✓ Tamamlandı
                                    </button>
                                </div>
                            </Section>

                            <Section title="Zaman">
                                <div className="kv">
                                    <div><span>Hedef Tarih:</span><b>{active.bitis_tarihi ? fmtDate(active.bitis_tarihi) : "—"}</b></div>
                                    <div><span>SLA Son:</span><b>{active.sla_son_tarih ? fmtDate(active.sla_son_tarih) : "—"}</b></div>
                                </div>
                            </Section>
                        </div>
                    </div>
                    <div className="drawer-backdrop" onClick={() => setActive(null)} />
                </div>
            )}
        </div>
    );
}

/* components */
function Card({ data, onOpen, onTake, onMove, onDone, me }) {
    const mine = data.atanan_email && data.atanan_email === me;
    const slaSoon =
        data.sla_son_tarih &&
        new Date(data.sla_son_tarih).getTime() - Date.now() < 24 * 60 * 60 * 1000;

    const fromLabel = data.talep_eden_kullanici || data.talep_eden_email || "—";
    const toLabel = data.atanan_kullanici || data.atanan_email || "—";

    return (
        <div className={`card ${mine ? "mine" : ""}`}>
            <div className="c-top">
                <span className={`pill ${PILL_COLOR[data.oncelik] || "p3"}`}>{data.oncelik || "P3"}</span>
                <span className={`badge s-${slug(STATUS_DISPLAY[data.durum] || "Yeni")}`}>
                    {STATUS_DISPLAY[data.durum] || "Yeni"}
                </span>
                {slaSoon && <span className="sla">SLA</span>}
                <button className="icn" onClick={onOpen} title="Detay">⋯</button>
            </div>

            <div className="c-title">#{data.id} — {data.baslik}</div>
            <div className="c-sub">
                <span>{fromLabel}</span>
                <span>→</span>
                <span>{toLabel}</span>
            </div>

            <div className="c-foot">
                <span className="time">{fmtTime(data.guncelleme_tarihi)}</span>
                <div className="act">
                    {!data.atanan_email && (
                        <button className="btn ghost xs" onClick={onTake}>Üstlen</button>
                    )}
                    <button
                        className="btn ghost xs"
                        onClick={() => mine && onMove(STATUS.SirayaAlindi)}
                        disabled={!mine}
                        title={!mine ? "Sadece atanan işlem yapabilir" : undefined}
                    >
                        Sıraya alındı
                    </button>
                    <button
                        className="btn ghost xs"
                        onClick={() => mine && onMove(STATUS.IslemeAlindi)}
                        disabled={!mine}
                        title={!mine ? "Sadece atanan işlem yapabilir" : undefined}
                    >
                        İşleme alındı
                    </button>
                    <button
                        className="btn ghost xs"
                        onClick={() => mine && onMove(STATUS.TestEdiliyor)}
                        disabled={!mine}
                        title={!mine ? "Sadece atanan işlem yapabilir" : undefined}
                    >
                        Test ediliyor
                    </button>
                    <button
                        className="btn primary xs"
                        onClick={mine ? onDone : undefined}
                        disabled={!mine}
                        title={!mine ? "Sadece atanan işlem yapabilir" : undefined}
                    >
                        Tamamla
                    </button>
                </div>
            </div>
        </div>
    );
}

function Section({ title, children }) {
    return (
        <section className="sec">
            <div className="sec-h"><h4>{title}</h4></div>
            <div className="sec-b">{children}</div>
        </section>
    );
}

/* utils */
function slug(s) {
    return (s || "")
        .toLowerCase()
        .replaceAll("ı", "i")
        .replaceAll("ğ", "g")
        .replaceAll("ş", "s")
        .replaceAll("ö", "o")
        .replaceAll("ç", "c")
        .replaceAll("ü", "u")
        .replace(/\s+/g, "-");
}
function fmtTime(ts) { try { return new Date(ts).toLocaleString(); } catch { return "—"; } }
function fmtDate(ts) { try { return new Date(ts).toLocaleDateString(); } catch { return "—"; } }

/* ------ realtime yardımcıları ------ */
function dateNum(v) { try { return new Date(v).getTime() || 0; } catch { return 0; } }

function sortRows(list, mineOnly, view) {
    const a = [...(list || [])];
    if (view === "list" && !mineOnly) {
        a.sort((x, y) => {
            const gx = x.global_sira ?? 1e9;
            const gy = y.global_sira ?? 1e9;
            if (gx !== gy) return gx - gy;
            const tx = dateNum(y.guncelleme_tarihi) - dateNum(x.guncelleme_tarihi);
            if (tx !== 0) return tx;
            return (x.id ?? 0) - (y.id ?? 0);
        });
    } else if (mineOnly) {
        a.sort((x, y) => {
            const ax = x.atanan_sira ?? 1e9;
            const ay = y.atanan_sira ?? 1e9;
            if (ax !== ay) return ax - ay;
            const tx = dateNum(y.guncelleme_tarihi) - dateNum(x.guncelleme_tarihi);
            if (tx !== 0) return tx;
            return (x.id ?? 0) - (y.id ?? 0);
        });
    } else {
        a.sort((x, y) => {
            const kx = x.kolon_sira ?? 1e9;
            const ky = y.kolon_sira ?? 1e9;
            if (kx !== ky) return kx - ky;
            const tx = dateNum(y.guncelleme_tarihi) - dateNum(x.guncelleme_tarihi);
            if (tx !== 0) return tx;
            return (x.id ?? 0) - (y.id ?? 0);
        });
    }
    return a;
}

function applyRealtime(prev, payload, mineOnly, view) {
    const type = payload.eventType;
    const next = [...prev];
    if (type === "DELETE") {
        return next.filter((r) => r.id !== payload.old?.id);
    }
    const row = payload.new;
    const i = next.findIndex((r) => r.id === row.id);
    if (i === -1) next.push(row);
    else next[i] = { ...next[i], ...row };
    return sortRows(next, mineOnly, view);
}
