// src/pages/Login.jsx
import React, { useState } from "react";
import supabase from "../supabaseClient";
import "./Login.css";

export default function Login() {
    const [email, setEmail] = useState("");
    const [pw, setPw] = useState("");
    const [showPw, setShowPw] = useState(false);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");

    // (OPSİYONEL) — sadece development: tek tıkla test kullanıcı aç
    async function createTestUser() {
        setErr("");
        const { data, error } = await supabase.auth.signUp({
            email: "admin@example.com",
            password: "123456",
            options: { data: { kullanici: "Admin", birim: "IT", title: "Uzman" } },
        });
        if (error) {
            console.error("signUp error:", error);
            setErr(`Kayıt başarısız: ${error.message}`);
        } else {
            alert("Test kullanıcı oluşturuldu. Eğer e-posta onayı açıksa, Studio’dan Confirm yapın ya da onayı kapatın.");
        }
    }

    async function handleSubmit(e) {
        e.preventDefault();
        setErr("");
        if (!email || !pw) return setErr("E-posta ve parola zorunludur");
        try {
            setLoading(true);
            const { data, error } = await supabase.auth.signInWithPassword({
                email,
                password: pw,
            });

            if (error) {
                console.error("Auth error:", { message: error.message, status: error.status });
                // Örnek mesajlar: "Invalid login credentials", "Email not confirmed"
                setErr(error.message || "Giriş başarısız");
                return;
            }

            const { session, user } = data || {};
            localStorage.setItem("auth_token", session?.access_token || "");
            const meta = user?.user_metadata || {};
            alert(`Hoş geldin ${meta.kullanici || user?.email}!`);
            // TODO: navigate("/");
        } catch (e2) {
            console.error(e2);
            setErr("Beklenmeyen bir hata oluştu");
        } finally {
            setLoading(false);
        }
    }

    return (
        <div className="login-wrap">
            <form className="login-card" onSubmit={handleSubmit}>
                <h1>Talep Sistemi</h1>
                <p className="muted">Giriş yapın</p>

                <label className="field">
                    <span>E-posta</span>
                    <input
                        type="email"
                        placeholder="ornek@firma.com"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        autoComplete="email"
                    />
                </label>

                <label className="field">
                    <span>Parola</span>
                    <div className="pw">
                        <input
                            type={showPw ? "text" : "password"}
                            placeholder="••••••"
                            value={pw}
                            onChange={(e) => setPw(e.target.value)}
                            autoComplete="current-password"
                        />
                        <button type="button" className="ghost-btn" onClick={() => setShowPw((v) => !v)}>
                            {showPw ? "Gizle" : "Göster"}
                        </button>
                    </div>
                </label>

                {err && <div className="error">{err}</div>}

                <button className="primary-btn" disabled={loading}>
                    {loading ? "Giriş yapılıyor…" : "Giriş Yap"}
                </button>

                {/* sadece local geliştirme için */}
                <div className="hint" style={{ marginTop: 10 }}>
                    Sorun mu yaşıyorsun?{" "}
                    <button type="button" className="ghost-btn" onClick={createTestUser}>
                        Test kullanıcısı oluştur
                    </button>
                </div>
            </form>
        </div>
    );
}
