import React, { useState } from "react";
import supabase from "../supabaseClient";
import "./Login.css";

export default function Login() {
    const [email, setEmail] = useState("");
    const [pw, setPw] = useState("");
    const [showPw, setShowPw] = useState(false);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErr("");
        if (!email || !pw) return setErr("E-posta ve parola zorunludur");

        try {
            setLoading(true);
            // RPC: check_login
            const { data, error } = await supabase.rpc("check_login", {
                p_email: email,
                p_sifre: pw,
            });

            if (error) throw error;

            const user = Array.isArray(data) ? data[0] : null;
            if (!user) {
                setErr("E-posta veya parola hatalı");
                return;
            }

            // Basit oturum işareti (örnek)
            localStorage.setItem("auth_token", "ok-" + Date.now());
            localStorage.setItem("user_profile", JSON.stringify(user));

            alert(`Hoş geldin ${user.kullanici || user.email}!`);
            // TODO: burada /dashboard'a yönlendirebilirsin
            // navigate("/");

        } catch (e) {
            console.error(e);
            setErr("Giriş yapılamadı");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-wrap">
            <form className="login-card" onSubmit={handleSubmit}>
                <h1>Talep Sistemi</h1>
                <p className="muted">Giriş yapın</p>

                <label className="field">
                    <span>E-posta</span>
                    <input
                        type="email"
                        placeholder="ornek@firma.com"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </label>

                <label className="field">
                    <span>Parola</span>
                    <div className="pw">
                        <input
                            type={showPw ? "text" : "password"}
                            placeholder="••••••"
                            value={pw}
                            onChange={(e) => setPw(e.target.value)}
                        />
                        <button type="button" className="ghost-btn" onClick={() => setShowPw(v => !v)}>
                            {showPw ? "Gizle" : "Göster"}
                        </button>
                    </div>
                </label>

                {err && <div className="error">{err}</div>}

                <button className="primary-btn" disabled={loading}>
                    {loading ? "Giriş yapılıyor…" : "Giriş Yap"}
                </button>
            </form>
        </div>
    );
}
