import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import supabase from "../supabaseClient";
import "./Requests.css";

export default function Requests() {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const profileStr = localStorage.getItem("user_profile");
        if (!profileStr) {
            navigate("/login");
            return;
        }
        const profile = JSON.parse(profileStr);
        setUser(profile);

        fetchRequests(profile.email);
    }, [navigate]);

    async function fetchRequests(email) {
        try {
            const { data, error } = await supabase
                .from("talep")
                .select("*")
                .eq("talep_eden_email", email)
                .order("guncelleme_tarihi", { ascending: false });

            if (error) {
                console.error(error);
                return;
            }
            setRequests(data || []);
        } catch (e) {
            console.error(e);
        } finally {
            setLoading(false);
        }
    }

    return (
        <div className="requests-page">
            <header className="requests-header">
                <h1>ðŸ“‹ Taleplerim</h1>
                <button className="new-btn" onClick={() => navigate("/new-request")}>
                    âž• Yeni Talep AÃ§
                </button>
            </header>

            {loading ? (
                <p>YÃ¼kleniyor...</p>
            ) : requests.length === 0 ? (
                <div className="empty">
                    <p>HenÃ¼z talebiniz yok.</p>
                    <button onClick={() => navigate("/new-request")}>Yeni Talep AÃ§</button>
                </div>
            ) : (
                <table className="requests-table">
                    <thead>
                        <tr>
                            <th>BaÅŸlÄ±k</th>
                            <th>Durum</th>
                            <th>Ã–ncelik</th>
                            <th>GÃ¼ncelleme</th>
                            <th>BitiÅŸ</th>
                        </tr>
                    </thead>
                    <tbody>
                        {requests.map((req) => (
                            <tr key={req.id}>
                                <td>{req.baslik}</td>
                                <td>{req.durum}</td>
                                <td>{req.oncelik}</td>
                                <td>{req.guncelleme_tarihi}</td>
                                <td>{req.bitis_tarihi || "-"}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}
