// src/App.jsx
import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Inbox from "./pages/Inbox";
import NewRequest from "./pages/NewRequest";
import Requests from "./pages/Requests";
import TeamMonitor from "./pages/TeamMonitor"; // <-- yeni ekranı import et

function RequireAuth({ children }) {
    const token = localStorage.getItem("auth_token");
    const email = localStorage.getItem("user_email"); // email bilgisini login sonrası set etmelisiniz

    if (!token) {
        return <Navigate to="/login" replace />;
    }

    // özel email için yönlendirme
    if (email === "kerem.ozturk@odaklojistik.com.tr") {
        return <Navigate to="/teammonitor" replace />;
    }

    return children;
}

export default function App() {
    return (
        <BrowserRouter>
            <Routes>
                {/* Login sayfası */}
                <Route path="/" element={<Login />} />
                <Route path="/login" element={<Login />} />

                {/* Korumalı sayfalar */}
                <Route
                    path="/requests"
                    element={
                        <RequireAuth>
                            <Requests />
                        </RequireAuth>
                    }
                />
                <Route
                    path="/newrequest"
                    element={
                        <RequireAuth>
                            <NewRequest />
                        </RequireAuth>
                    }
                />
                <Route
                    path="/inbox"
                    element={
                        <RequireAuth>
                            <Inbox />
                        </RequireAuth>
                    }
                />

                {/* TeamMonitor sayfası */}
                <Route
                    path="/teammonitor"
                    element={
                        <RequireAuth>
                            <TeamMonitor />
                        </RequireAuth>
                    }
                />

                {/* Bilinmeyen rota */}
                <Route path="*" element={<Navigate to="/login" replace />} />
            </Routes>
        </BrowserRouter>
    );
}
