// src/pages/Requests.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import supabase from "../supabaseClient";
import "./Requests.css";

const STATUS_LABEL = {
    Yeni: "Yeni",
    SirayaAlindi: "Sƒ±raya Alƒ±ndƒ±",
    IslemeAlindi: "ƒ∞≈üleme Alƒ±ndƒ±",
    TestEdiliyor: "Test Ediliyor",
    Tamamlandi: "Tamamlandƒ±",
};

export default function Requests() {
    const navigate = useNavigate();

    // profil
    const profile = useMemo(() => {
        try { return JSON.parse(localStorage.getItem("user_profile") || "{}"); }
        catch { return {}; }
    }, []);
    const userEmail = profile?.email || "";
    const userName = profile?.kullanici || userEmail;

    // state
    const [rows, setRows] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const [q, setQ] = useState("");
    const [onlyOpen, setOnlyOpen] = useState(false); // tamamlananlarƒ± gizle

    useEffect(() => {
        // auth yoksa login'e
        if (!localStorage.getItem("auth_token")) {
            navigate("/login", { replace: true });
            return;
        }
        if (userEmail) {
            fetchData();
        } else {
            setLoading(false);
            setError("Kullanƒ±cƒ± e-postasƒ± bulunamadƒ±.");
        }
        // eslint-disable-next-line
    }, [userEmail, onlyOpen]);

    async function fetchData() {
        try {
            setLoading(true);
            setError("");

            // temel select ‚Äî sadece sende olan kolonlar
            let query = supabase
                .from("talepler")
                .select(`
          id, baslik, aciklama, oncelik, durum,
          talep_eden_email, atanan_email,
          guncelleme_tarihi, bitis_tarihi, sla_son_tarih,
          kolon_sira, atanan_sira, global_sira,
          talep_eden_kullanici, atanan_kullanici
        `)
                .eq("talep_eden_email", userEmail)
                .order("guncelleme_tarihi", { ascending: false });

            if (onlyOpen) {
                // Tamamlandi/Reddedildi dƒ±≈üƒ±ndakiler
                query = query.not("durum", "in", '("Tamamlandi","Reddedildi")');
            }

            const { data, error } = await query;
            if (error) throw error;

            setRows(data || []);
        } catch (err) {
            console.error("fetch error (Supabase):", err);
            setError("Talepler alƒ±namadƒ±.");
        } finally {
            setLoading(false);
        }
    }

    const filtered = rows.filter(r => {
        if (!q) return true;
        const hay = `${r.id} ${r.baslik} ${r.aciklama || ""} ${r.talep_eden_kullanici || ""} ${r.atanan_kullanici || ""} ${r.durum || ""}`.toLowerCase();
        return hay.includes(q.toLowerCase());
    });

    function fmtDateTime(ts) {
        if (!ts) return "‚Äî";
        try { return new Date(ts).toLocaleString(); } catch { return ts; }
    }

    return (
        <div className="rq">
            {/* √úst bar */}
            <div className="rq-top">
                <div className="brand">
                    <div className="logo">üìã</div>
                    <div className="bt">
                        <h1>Taleplerim</h1>
                        <span className="muted">{userName}</span>
                    </div>
                </div>

                <div className="cmd">
                    <div className="search-wrap">
                        <input
                            className="search"
                            placeholder="Ara: #id, ba≈ülƒ±k, ki≈üi‚Ä¶"
                            value={q}
                            onChange={(e) => setQ(e.target.value)}
                        />
                    </div>

                    <label className="toggle">
                        <input
                            type="checkbox"
                            checked={onlyOpen}
                            onChange={(e) => setOnlyOpen(e.target.checked)}
                        />
                        <span>Devam edenler</span>
                    </label>

                    <Link to="/newrequest" className="btn primary">+ Yeni Talep</Link>
                </div>
            </div>

            {/* ƒ∞√ßerik */}
            <div className="rq-body">
                {loading && <div className="rq-skel">Y√ºkleniyor‚Ä¶</div>}
                {error && !loading && <div className="rq-alert err">{error}</div>}

                {!loading && !error && filtered.length === 0 && (
                    <div className="empty">
                        <p>Hen√ºz talebiniz yok.</p>
                        <Link to="/newrequest" className="btn primary">Yeni Talep Olu≈ütur</Link>
                    </div>
                )}

                {!loading && !error && filtered.length > 0 && (
                    <div className="card">
                        <table className="rq-table">
                            <thead>
                                <tr>
                                    <th style={{ width: 72 }}>ID</th>
                                    <th>Ba≈ülƒ±k</th>
                                    <th style={{ width: 120 }}>Durum</th>
                                    <th style={{ width: 90 }}>√ñncelik</th>
                                    <th style={{ width: 80 }}>Sƒ±ra</th>
                                    <th style={{ width: 200 }}>Atanan</th>
                                    <th style={{ width: 190 }}>G√ºncelleme</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filtered.map((r) => (
                                    <tr key={r.id}>
                                        <td className="mono">#{r.id}</td>
                                        <td className="title">{r.baslik}</td>
                                        <td>
                                            <span className={`badge s-${(STATUS_LABEL[r.durum] || "Yeni").toLowerCase().replace(/\s+/g, "-")}`}>
                                                {STATUS_LABEL[r.durum] || "Yeni"}
                                            </span>
                                        </td>
                                        <td>
                                            <span className={`pill ${String(r.oncelik || "P3").toLowerCase()}`}>{r.oncelik || "P3"}</span>
                                        </td>
                                        <td className="mono">{r.atanan_sira ?? "‚Äî"}</td>
                                        <td>{r.atanan_kullanici || r.atanan_email || "‚Äî"}</td>
                                        <td className="mono">{fmtDateTime(r.guncelleme_tarihi)}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>

                        <div className="legend">
                            <span className="pill p1">P1</span>
                            <span className="pill p2">P2</span>
                            <span className="pill p3">P3</span>
                            <span className="muted">‚Äì ‚ÄúSƒ±ra‚Äù: atanan ki≈üinin kuyruƒüundaki pozisyonu</span>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}
