// src/App.js
import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Inbox from "./pages/Inbox";
import NewRequest from "./pages/NewRequest";
import { getUser, isAuthenticated, isISurec } from "./utils/auth";

// Basit korumalı yol
function ProtectedRoute({ element, allow }) {
    // allow: "all" | "isg" (İş & Süreç Geliştirme)
    if (!isAuthenticated()) return <Navigate to="/login" replace />;

    const user = getUser();
    if (allow === "isg" && !isISurec(user)) {
        // ISG değilse talep sayfasına
        return <Navigate to="/talep" replace />;
    }
    return element;
}

// Root’ta birime göre yönlendir
function HomeRedirect() {
    if (!isAuthenticated()) return <Navigate to="/login" replace />;
    const user = getUser();
    return isISurec(user) ? <Navigate to="/inbox" replace /> : <Navigate to="/talep" replace />;
}

export default function App() {
    return (
        <BrowserRouter>
            <Routes>
                <Route path="/" element={<HomeRedirect />} />
                <Route path="/login" element={<Login />} />

                {/* Sadece İŞ & SÜREÇ GELİŞTİRME görebilsin */}
                <Route
                    path="/inbox"
                    element={<ProtectedRoute allow="isg" element={<Inbox />} />}
                />

                {/* Her giriş yapmış kullanıcı */}
                <Route
                    path="/talep"
                    element={<ProtectedRoute allow="all" element={<NewRequest />} />}
                />

                <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
        </BrowserRouter>
    );
}
