import React, { useEffect, useMemo, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import supabase from "../supabaseClient";
import "./Requests.css";

const STATUS_LABEL = {
    Yeni: "Yeni",
    SirayaAlindi: "SÄ±raya AlÄ±ndÄ±",
    IslemeAlindi: "Ä°ÅŸleme AlÄ±ndÄ±",
    TestEdiliyor: "Test Ediliyor",
    Tamamlandi: "TamamlandÄ±",
};

export default function Requests() {
    const navigate = useNavigate();

    // profil
    const profile = useMemo(() => {
        try { return JSON.parse(localStorage.getItem("user_profile") || "{}"); }
        catch { return {}; }
    }, []);
    const userEmail = profile?.email || "";
    const userName = profile?.kullanici || userEmail;

    // state
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const [q, setQ] = useState("");
    const [onlyOpen, setOnlyOpen] = useState(false); // tamamlananlarÄ± gizle

    useEffect(() => {
        if (!localStorage.getItem("auth_token")) {
            navigate("/login", { replace: true });
            return;
        }
        if (userEmail) fetchRequests();
        // eslint-disable-next-line
    }, [userEmail, onlyOpen]);

    async function fetchRequests() {
        try {
            setLoading(true);
            setError("");

            let query = supabase
                .from("talepler")
                .select(`
          id, baslik, durum, oncelik,
          talep_eden_email, talep_eden_kullanici,
          atanan_email, atanan_kullanici,
          guncelleme_tarihi, bitis_tarihi, sla_son_tarih
        `)
                .eq("talep_eden_email", userEmail)
                .order("guncelleme_tarihi", { ascending: false });

            if (onlyOpen) {
                query = query.not("durum", "in", '("Tamamlandi","Reddedildi")');
            }

            const { data, error } = await query;
            if (error) throw error;

            setRequests(data || []);
        } catch (err) {
            console.error("fetchRequests error:", err);
            setError("Talepler alÄ±namadÄ±");
        } finally {
            setLoading(false);
        }
    }

    const filtered = requests.filter(r => {
        if (!q) return true;
        const hay = `${r.id} ${r.baslik} ${r.talep_eden_kullanici || ""} ${r.atanan_kullanici || ""} ${r.durum || ""}`.toLowerCase();
        return hay.includes(q.toLowerCase());
    });

    function fmtDateTime(ts) {
        if (!ts) return "â€”";
        try {
            const d = new Date(ts);
            return d.toLocaleString(); // yerel tarih-saat
        } catch { return ts; }
    }

    return (
        <div className="rq">
            {/* Ãœst bar */}
            <div className="rq-top">
                <div className="brand">
                    <div className="logo">ðŸ“‹</div>
                    <div className="bt">
                        <h1>Taleplerim</h1>
                        <span className="muted">{userName}</span>
                    </div>
                </div>

                <div className="cmd">
                    <div className="search-wrap">
                        <input
                            className="search"
                            placeholder="Ara: #id, baÅŸlÄ±k, kiÅŸiâ€¦"
                            value={q}
                            onChange={(e) => setQ(e.target.value)}
                        />
                    </div>

                    <label className="toggle">
                        <input
                            type="checkbox"
                            checked={onlyOpen}
                            onChange={(e) => setOnlyOpen(e.target.checked)}
                        />
                        <span>Devam edenler</span>
                    </label>

                    <Link to="/newrequest" className="btn primary">+ Yeni Talep</Link>
                </div>
            </div>

            {/* Ä°Ã§erik */}
            <div className="rq-body">
                {loading && <div className="rq-skel">YÃ¼kleniyorâ€¦</div>}
                {error && !loading && <div className="rq-alert err">{error}</div>}

                {!loading && !error && filtered.length === 0 && (
                    <div className="empty">
                        <p>HenÃ¼z talebiniz yok.</p>
                        <Link to="/newrequest" className="btn primary">Yeni Talep OluÅŸtur</Link>
                    </div>
                )}

                {!loading && !error && filtered.length > 0 && (
                    <div className="card">
                        <table className="rq-table">
                            <thead>
                                <tr>
                                    <th style={{ width: 72 }}>ID</th>
                                    <th>BaÅŸlÄ±k</th>
                                    <th style={{ width: 140 }}>Durum</th>
                                    <th style={{ width: 110 }}>Ã–ncelik</th>
                                    <th style={{ width: 200 }}>Atanan</th>
                                    <th style={{ width: 190 }}>GÃ¼ncelleme</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filtered.map((r) => (
                                    <tr key={r.id}>
                                        <td className="mono">#{r.id}</td>
                                        <td className="title">{r.baslik}</td>
                                        <td>
                                            <span className={`badge s-${(STATUS_LABEL[r.durum] || "Yeni").toLowerCase().replace(/\s+/g, "-")}`}>
                                                {STATUS_LABEL[r.durum] || "Yeni"}
                                            </span>
                                        </td>
                                        <td>
                                            <span className={`pill ${r.oncelik?.toLowerCase() || "p3"}`}>{r.oncelik || "P3"}</span>
                                        </td>
                                        <td>{r.atanan_kullanici || r.atanan_email || "â€”"}</td>
                                        <td className="mono">{fmtDateTime(r.guncelleme_tarihi)}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                        <div className="legend">
                            <span className="pill p1">P1</span>
                            <span className="pill p2">P2</span>
                            <span className="pill p3">P3</span>
                            <span className="muted">â€“ Ã–ncelik seviyeleri</span>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}
