// src/pages/TeamMonitor.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import supabase from "../supabaseClient";
import "./Inbox.css";

/* ---- Sabitler ---- */
const STATUS = {
    Yeni: "Yeni",
    SirayaAlindi: "SirayaAlindi",
    IslemeAlindi: "IslemeAlindi",
    TestEdiliyor: "TestEdiliyor",
    Tamamlandi: "Tamamlandi",
};

const STATUS_DISPLAY = {
    [STATUS.Yeni]: "Yeni",
    [STATUS.SirayaAlindi]: "SÄ±raya AlÄ±ndÄ±",
    [STATUS.IslemeAlindi]: "Ä°ÅŸleme AlÄ±ndÄ±",
    [STATUS.TestEdiliyor]: "Test Ediliyor",
    [STATUS.Tamamlandi]: "TamamlandÄ±",
};

const BOARD_COLUMNS = [
    { key: STATUS.Yeni, title: STATUS_DISPLAY[STATUS.Yeni] },
    { key: STATUS.SirayaAlindi, title: STATUS_DISPLAY[STATUS.SirayaAlindi] },
    { key: STATUS.IslemeAlindi, title: STATUS_DISPLAY[STATUS.IslemeAlindi] },
    { key: STATUS.TestEdiliyor, title: STATUS_DISPLAY[STATUS.TestEdiliyor] },
    { key: STATUS.Tamamlandi, title: STATUS_DISPLAY[STATUS.Tamamlandi] },
];

const MONITORED_USERS = [
    { name: "GÃ–RKEM Ã‡ADIRCI" },
    { name: "FURKAN BÄ°LGÄ°LÄ°" },
    { name: "YAÄžIZ EFE BULUTCU" },
];

const ALLOWED_VIEWER = "ONUR KEREM Ã–ZTÃœRK";

/* ---- Utils ---- */
const fmt = (ts) => {
    try { return new Date(ts).toLocaleString(); } catch { return "â€”"; }
};
const ts = (v) => {
    try { return new Date(v).getTime() || 0; } catch { return 0; }
};
const sortKanban = (arr = []) => {
    const a = [...arr];
    a.sort((x, y) => {
        const kx = x.kolon_sira ?? 1e15;
        const ky = y.kolon_sira ?? 1e15;
        if (kx !== ky) return kx - ky;
        const gx = ts(y.guncelleme_tarihi) - ts(x.guncelleme_tarihi);
        if (gx) return gx;
        return (x.id ?? 0) - (y.id ?? 0);
    });
    return a;
};

/* ---- BileÅŸen ---- */
export default function TeamMonitor() {
    const navigate = useNavigate();

    // auth
    const me = useMemo(() => {
        try { return JSON.parse(localStorage.getItem("user_profile") || "{}"); }
        catch { return {}; }
    }, []);
    const myName = (me?.kullanici || "").trim();
    useEffect(() => {
        if (!localStorage.getItem("auth_token")) navigate("/login", { replace: true });
    }, [navigate]);
    const canView = useMemo(() => (myName || "").toLocaleUpperCase("tr-TR") === ALLOWED_VIEWER, [myName]);

    // UI state
    const [selectedUser, setSelectedUser] = useState(MONITORED_USERS[0].name);
    const [q, setQ] = useState("");
    const [onlySLA, setOnlySLA] = useState(false);

    // data state
    const [rows, setRows] = useState([]);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");

    // fetch (sadece seÃ§ili kullanÄ±cÄ±yÄ± Ã§ek)
    useEffect(() => {
        if (!canView) return;
        (async () => {
            try {
                setLoading(true);
                setErr("");

                let query = supabase
                    .from("talepler")
                    .select("*")
                    .eq("atanan_kullanici", selectedUser);

                if (onlySLA) {
                    const t24 = new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString();
                    query = query.lt("sla_son_tarih", t24).not("durum", "in", '("Tamamlandi","Reddedildi")');
                }

                query = query
                    .order("kolon_sira", { ascending: true, nullsFirst: true })
                    .order("guncelleme_tarihi", { ascending: false })
                    .limit(1000);

                const { data, error } = await query;
                if (error) throw error;
                setRows(sortKanban(data || []));
            } catch (e) {
                console.error(e);
                setErr("KayÄ±tlar Ã§ekilemedi.");
            } finally {
                setLoading(false);
            }
        })();
    }, [selectedUser, onlySLA, canView]);

    // realtime (sadece seÃ§ili kullanÄ±cÄ±)
    useEffect(() => {
        if (!canView) return;
        const ch = supabase
            .channel("talepler-realtime-monitor-one")
            .on("postgres_changes", { event: "*", schema: "public", table: "talepler" }, (payload) => {
                setRows((prev) => {
                    const row = payload.new || payload.old || {};
                    if (row.atanan_kullanici !== selectedUser) return prev;
                    if (payload.eventType === "DELETE") return prev.filter(r => r.id !== row.id);
                    const next = [...prev];
                    const i = next.findIndex(r => r.id === row.id);
                    if (i === -1 && payload.eventType === "INSERT") next.push(row);
                    else if (i > -1) next[i] = { ...next[i], ...row };
                    return sortKanban(next);
                });
            })
            .subscribe();
        return () => supabase.removeChannel(ch);
    }, [selectedUser, canView]);

    // arama
    const rowsSearch = useMemo(() => {
        if (!q) return rows;
        const qq = q.toLowerCase();
        return rows.filter(r => {
            const hay = `${r.id} ${r.baslik} ${r.talep_eden_kullanici || ""} ${r.talep_eden_email || ""} ${r.atanan_email || ""}`.toLowerCase();
            return hay.includes(qq);
        });
    }, [rows, q]);

    // metrikler
    const now = Date.now();
    const counts = useMemo(() => {
        const c = { total: rowsSearch.length, overdue: 0 };
        for (const k of Object.keys(STATUS)) c[STATUS[k]] = 0;
        rowsSearch.forEach(r => {
            const d = r.durum || STATUS.Yeni;
            c[d] = (c[d] || 0) + 1;
            const sla = ts(r.sla_son_tarih);
            const isDone = d === STATUS.Tamamlandi || r.durum === "Reddedildi";
            if (!isDone && sla && sla < now) c.overdue++;
        });
        return c;
    }, [rowsSearch, now]);

    // mini-kanban (sadece seÃ§ili kullanÄ±cÄ±)
    const byStatus = useMemo(() => {
        const map = new Map(BOARD_COLUMNS.map(c => [c.key, []]));
        rowsSearch.forEach(r => {
            const k = BOARD_COLUMNS.some(c => c.key === r.durum) ? r.durum : STATUS.Yeni;
            map.get(k).push(r);
        });
        for (const [k, arr] of map) map.set(k, sortKanban(arr));
        return map;
    }, [rowsSearch]);

    if (!canView) {
        return (
            <div className="mx">
                <div className="mx-top sticky modern">
                    <div className="brand">
                        <div className="logo">ðŸ‘€</div>
                        <div className="bt"><h1>Takip EkranÄ±</h1><span className="muted">EriÅŸim yetkiniz yok</span></div>
                    </div>
                </div>
                <div className="mx-alert err">Bu ekran yalnÄ±zca <b>{ALLOWED_VIEWER}</b> iÃ§in yetkilidir.</div>
            </div>
        );
    }

    return (
        <div className="mx">
            {/* Ãœst bar */}
            <div className="mx-top sticky modern">
                <div className="brand">
                    <div className="logo">ðŸ“Š</div>
                    <div className="bt">
                        <h1>Takip EkranÄ±</h1>
                        <span className="muted">KullanÄ±cÄ± seÃ§ â†’ anlÄ±k analiz</span>
                    </div>
                </div>

                <div className="cmd">
                    <select
                        className="input"
                        value={selectedUser}
                        onChange={e => setSelectedUser(e.target.value)}
                        style={{ minWidth: 260 }}
                        aria-label="KullanÄ±cÄ± seÃ§"
                    >
                        {MONITORED_USERS.map(u => (
                            <option key={u.name} value={u.name}>{u.name}</option>
                        ))}
                    </select>

                    <div className="search-wrap">
                        <input
                            className="search"
                            placeholder="Ara: #id, baÅŸlÄ±k, kiÅŸiâ€¦"
                            value={q}
                            onChange={(e) => setQ(e.target.value)}
                        />
                    </div>

                    <label className="toggle">
                        <input type="checkbox" checked={onlySLA} onChange={(e) => setOnlySLA(e.target.checked)} />
                        <span>SLA (&lt;24s)</span>
                    </label>

                    <button className="btn ghost" onClick={() => window.location.reload()}>Yenile</button>
                    <button className="btn primary" onClick={() => navigate("/inbox?assignee=" + encodeURIComponent(selectedUser))}>
                        {selectedUser.split(" ")[0]}'Ä±n Inboxâ€™Ä±
                    </button>
                </div>
            </div>

            {err && <div className="mx-alert err">{err}</div>}
            {loading && <div className="mx-skel">YÃ¼kleniyorâ€¦</div>}

            {/* KPI kutularÄ± */}
            <div className="board" style={{ gridTemplateColumns: "repeat(6, minmax(160px, 1fr))", gap: 10 }}>
                <KPI title="Toplam" value={counts.total} />
                <KPI title="Yeni" value={counts[STATUS.Yeni] || 0} tone="blue" />
                <KPI title="SÄ±raya AlÄ±ndÄ±" value={counts[STATUS.SirayaAlindi] || 0} tone="amber" />
                <KPI title="Ä°ÅŸleme AlÄ±ndÄ±" value={counts[STATUS.IslemeAlindi] || 0} tone="emerald" />
                <KPI title="Test Ediliyor" value={counts[STATUS.TestEdiliyor] || 0} tone="violet" />
                <KPI title="TamamlandÄ±" value={counts[STATUS.Tamamlandi] || 0} tone="zinc" />
            </div>

            {/* SLA / Overdue barÄ± */}
            <div className="board" style={{ gridTemplateColumns: "repeat(2, minmax(220px, 1fr))", gap: 10, marginTop: 8 }}>
                <KPI title="SLA GeÃ§en" value={counts.overdue} tone="rose" />
                <KPI title="SLA Ä°Ã§inde" value={Math.max(0, (counts.total || 0) - (counts.overdue || 0))} tone="teal" />
            </div>

            {/* Mini Kanban (seÃ§ili kullanÄ±cÄ±) */}
            <div className="board" style={{ gridTemplateColumns: `repeat(${BOARD_COLUMNS.length}, minmax(260px,1fr))`, gap: 10, marginTop: 12 }}>
                {BOARD_COLUMNS.map(col => {
                    const items = byStatus.get(col.key) || [];
                    return (
                        <div key={col.key} className="col modern">
                            <div className="col-head mini">
                                <span className="dot" />
                                <h4 className="mini-title">{col.title}</h4>
                                <span className="count">{items.length}</span>
                            </div>
                            <div className="col-body">
                                {items.length === 0 && <div className="col-empty">Yok</div>}
                                {items.slice(0, 8).map(item => (
                                    <button
                                        key={item.id}
                                        className="card modern"
                                        onClick={() => navigate(`/inbox?assignee=${encodeURIComponent(item.atanan_email || "")}`)}
                                        title={`#${item.id} â€” ${item.baslik}\nGÃ¼ncellenme: ${fmt(item.guncelleme_tarihi)}`}
                                        style={{ textAlign: "left" }}
                                    >
                                        <div className="c-title">{`#${item.id} â€” ${item.baslik}`}</div>
                                        <div className="c-desc">{item.talep_eden_kullanici || item.talep_eden_email || "â€”"}</div>
                                        <div className="c-meta"><span className="time">{fmt(item.guncelleme_tarihi)}</span></div>
                                    </button>
                                ))}
                                {items.length > 8 && <div className="muted more">+{items.length - 8} daha</div>}
                            </div>
                        </div>
                    );
                })}
            </div>

            {/* Son gÃ¼ncellemeler */}
            <div className="col modern" style={{ marginTop: 12 }}>
                <div className="col-head mini">
                    <span className="dot" />
                    <h4 className="mini-title">Son GÃ¼ncellemeler</h4>
                    <span className="count">{rowsSearch.length}</span>
                </div>
                <ul className="col-body" style={{ listStyle: "none", padding: 0, margin: 0 }}>
                    {rowsSearch
                        .slice()
                        .sort((a, b) => ts(b.guncelleme_tarihi) - ts(a.guncelleme_tarihi))
                        .slice(0, 12)
                        .map(r => (
                            <li key={r.id} style={{ marginBottom: 8 }}>
                                <button
                                    className="card modern"
                                    onClick={() => navigate(`/inbox?assignee=${encodeURIComponent(r.atanan_email || "")}`)}
                                    title={`#${r.id} â€” ${r.baslik}\nGÃ¼ncellenme: ${fmt(r.guncelleme_tarihi)}`}
                                    style={{ width: "100%", textAlign: "left" }}
                                >
                                    <div className="c-title">{`#${r.id} â€” ${r.baslik}`}</div>
                                    <div className="c-meta">
                                        <b>{STATUS_DISPLAY[r.durum] || "â€”"}</b> â€¢ <span className="time">{fmt(r.guncelleme_tarihi)}</span>
                                    </div>
                                </button>
                            </li>
                        ))}
                    {rowsSearch.length === 0 && <div className="col-empty">KayÄ±t yok</div>}
                </ul>
            </div>

            <div className="muted" style={{ marginTop: 8 }}>
                Kartlara tÄ±klayÄ±nca ilgili kiÅŸinin inboxâ€™Ä±na gidersin (URLâ€™de <code>assignee</code> parametresi).
            </div>
        </div>
    );
}

/* Basit KPI kartÄ± */
function KPI({ title, value, tone = "zinc" }) {
    const toneMap = {
        blue: "box-shadow: inset 0 0 0 2px rgba(96,165,250,.45);",
        amber: "box-shadow: inset 0 0 0 2px rgba(251,191,36,.45);",
        emerald: "box-shadow: inset 0 0 0 2px rgba(52,211,153,.45);",
        violet: "box-shadow: inset 0 0 0 2px rgba(167,139,250,.45);",
        rose: "box-shadow: inset 0 0 0 2px rgba(244,63,94,.45);",
        teal: "box-shadow: inset 0 0 0 2px rgba(45,212,191,.45);",
        zinc: "box-shadow: inset 0 0 0 2px rgba(161,161,170,.35);",
    }[tone] || "";
    return (
        <div className="card modern" style={{ padding: 14, ...(tone ? {} : {}), cssText: toneMap }}>
            <div className="c-title" style={{ fontSize: 12, opacity: .85 }}>{title}</div>
            <div style={{ fontSize: 22, fontWeight: 700, marginTop: 6 }}>{value}</div>
        </div>
    );
}
