// src/pages/TeamMonitor.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import supabase from "../supabaseClient";
import "./Inbox.css"; // mevcut stillerden yararlanıyoruz

/* ---- Sabitler ---- */
const STATUS = {
    Yeni: "Yeni",
    SirayaAlindi: "SirayaAlindi",
    IslemeAlindi: "IslemeAlindi",
    TestEdiliyor: "TestEdiliyor",
    Tamamlandi: "Tamamlandi",
};

const STATUS_DISPLAY = {
    [STATUS.Yeni]: "Yeni",
    [STATUS.SirayaAlindi]: "Sıraya Alındı",
    [STATUS.IslemeAlindi]: "İşleme Alındı",
    [STATUS.TestEdiliyor]: "Test Ediliyor",
    [STATUS.Tamamlandi]: "Tamamlandı",
};

// Kolon sırası (soldan sağa)
const BOARD_COLUMNS = [
    { key: STATUS.Yeni, title: STATUS_DISPLAY[STATUS.Yeni] },
    { key: STATUS.SirayaAlindi, title: STATUS_DISPLAY[STATUS.SirayaAlindi] },
    { key: STATUS.IslemeAlindi, title: STATUS_DISPLAY[STATUS.IslemeAlindi] },
    { key: STATUS.TestEdiliyor, title: STATUS_DISPLAY[STATUS.TestEdiliyor] },
    { key: STATUS.Tamamlandi, title: STATUS_DISPLAY[STATUS.Tamamlandi] },
];

// İzlenecek kullanıcılar (sütun üst başlıkları)
const MONITORED_USERS = [
    { name: "GÖRKEM ÇADIRCI" },
    { name: "FURKAN BİLGİLİ" },
    { name: "YAĞIZ EFE BULUTCU" },
];

// Bu sayfayı görebilecek kişi
const ALLOWED_VIEWER = "ONUR KEREM ÖZTÜRK";

/* ---- Utils ---- */
function slug(s) {
    return (s || "")
        .toLowerCase()
        .replaceAll("ı", "i").replaceAll("ğ", "g").replaceAll("ş", "s")
        .replaceAll("ö", "o").replaceAll("ç", "c").replaceAll("ü", "u")
        .replace(/\s+/g, "-");
}
function fmtTime(ts) {
    try {
        return new Date(ts).toLocaleString();
    } catch {
        return "—";
    }
}
function dateNum(v) {
    try {
        return new Date(v).getTime() || 0;
    } catch {
        return 0;
    }
}
function sortByKanbanOrder(list) {
    const a = [...(list || [])];
    a.sort((x, y) => {
        const kx = x.kolon_sira ?? 1e15;
        const ky = y.kolon_sira ?? 1e15;
        if (kx !== ky) return kx - ky;
        const tx = dateNum(y.guncelleme_tarihi) - dateNum(x.guncelleme_tarihi);
        if (tx !== 0) return tx;
        return (x.id ?? 0) - (y.id ?? 0);
    });
    return a;
}

/* ---- Görsel yardımcılar ---- */
// Kolon rozet noktacığı için sınıf
const STATUS_DOT_CLASS = {
    [STATUS.Yeni]: "s-yeni",
    [STATUS.SirayaAlindi]: "s-siraya-alindi",
    [STATUS.IslemeAlindi]: "s-isleme-alindi",
    [STATUS.TestEdiliyor]: "s-test-ediliyor",
    [STATUS.Tamamlandi]: "s-tamamlandi",
};

// Kart çerçevesi (yumuşak renkler)
const STATUS_RING_CLASS = {
    [STATUS.Yeni]: "ring-blue",
    [STATUS.SirayaAlindi]: "ring-amber",
    [STATUS.IslemeAlindi]: "ring-emerald",
    [STATUS.TestEdiliyor]: "ring-violet",
    [STATUS.Tamamlandi]: "ring-zinc",
};

export default function TeamMonitor() {
    const navigate = useNavigate();

    // login + yetki kontrolü
    const me = useMemo(() => {
        try {
            return JSON.parse(localStorage.getItem("user_profile") || "{}");
        } catch {
            return {};
        }
    }, []);
    const myName = (me?.kullanici || "").trim();

    useEffect(() => {
        if (!localStorage.getItem("auth_token")) {
            navigate("/login", { replace: true });
            return;
        }
    }, [navigate]);

    const canView = useMemo(() => {
        const up = (myName || "").toLocaleUpperCase("tr-TR");
        return up === ALLOWED_VIEWER;
    }, [myName]);

    const [q, setQ] = useState("");
    const [onlySLA, setOnlySLA] = useState(false);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");
    const [rows, setRows] = useState([]);

    // data fetch
    useEffect(() => {
        if (!canView) return;
        fetchData();
        // eslint-disable-next-line
    }, [onlySLA, canView]);

    // realtime
    useEffect(() => {
        if (!canView) return;
        const ch = supabase
            .channel("talepler-realtime-monitor")
            .on("postgres_changes", { event: "*", schema: "public", table: "talepler" }, (payload) => {
                setRows((prev) => applyRealtime(prev, payload));
            })
            .subscribe();
        return () => supabase.removeChannel(ch);
    }, [canView]);

    async function fetchData() {
        try {
            setLoading(true);
            setErr("");

            const names = MONITORED_USERS.map((u) => u.name);
            let query = supabase.from("talepler").select("*").in("atanan_kullanici", names);

            if (onlySLA) {
                // Son 24 saat içinde dolacak/ geçmiş kayıtlar (örn. < 24s)
                const t24 = new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString();
                query = query.lt("sla_son_tarih", t24).not("durum", "in", '("Tamamlandi","Reddedildi")');
            }

            query = query
                .order("kolon_sira", { ascending: true, nullsFirst: true })
                .order("guncelleme_tarihi", { ascending: false })
                .limit(1000);

            const { data, error } = await query;
            if (error) throw error;
            setRows(sortByKanbanOrder(data || []));
        } catch (e) {
            console.error(e);
            setErr("Kayıtlar çekilemedi.");
        } finally {
            setLoading(false);
        }
    }

    function applyRealtime(prev, payload) {
        const next = [...prev];
        if (payload.eventType === "DELETE") {
            return next.filter((r) => r.id !== payload.old?.id);
        }
        const row = payload.new;
        // Sadece izlenen kullanıcılar
        if (!MONITORED_USERS.some((u) => u.name === row?.atanan_kullanici)) return prev;

        const i = next.findIndex((r) => r.id === row.id);
        if (i === -1) next.push(row);
        else next[i] = { ...next[i], ...row };
        return sortByKanbanOrder(next);
    }

    // filtreleme
    const filtered = rows.filter((r) => {
        if (!q) return true;
        const text = `${r.id} ${r.baslik} ${r.talep_eden_email} ${r.atanan_email || ""} ${r.talep_eden_kullanici || ""} ${r.atanan_kullanici || ""}`.toLowerCase();
        return text.includes(q.toLowerCase());
    });

    // kullanıcı -> status -> items
    const byUser = useMemo(() => {
        const map = new Map();
        MONITORED_USERS.forEach((u) => {
            map.set(u.name, Object.fromEntries(BOARD_COLUMNS.map((c) => [c.key, []])));
        });
        for (const r of filtered) {
            const user = r.atanan_kullanici;
            if (!map.has(user)) continue;
            const k = r.durum || STATUS.Yeni;
            map.get(user)[k].push(r);
        }
        // iç listeleri sırala
        for (const [, colMap] of map) {
            for (const k of Object.keys(colMap)) {
                colMap[k] = sortByKanbanOrder(colMap[k]);
            }
        }
        return map;
    }, [filtered]);

    if (!canView) {
        return (
            <div className="mx">
                <div className="mx-top sticky">
                    <div className="brand">
                        <div className="logo">👀</div>
                        <div className="bt">
                            <h1>Takip Ekranı</h1>
                            <span className="muted">Erişim yetkiniz yok</span>
                        </div>
                    </div>
                </div>
                <div className="mx-alert err">
                    Bu ekran yalnızca <b>{ALLOWED_VIEWER}</b> için yetkilidir.
                </div>
            </div>
        );
    }

    return (
        <div className="mx">
            {/* Üst bar */}
            <div className="mx-top sticky modern">
                <div className="brand">
                    <div className="logo">📊</div>
                    <div className="bt">
                        <h1>Takip Ekranı</h1>
                        <span className="muted">GÖRKEM • FURKAN • YAĞIZ — canlı durum</span>
                    </div>
                </div>

                <div className="cmd">
                    <div className="search-wrap">
                        <input
                            className="search"
                            placeholder="Ara: #id, başlık, kişi…"
                            value={q}
                            onChange={(e) => setQ(e.target.value)}
                        />
                    </div>
                    <div className="toggles">
                        <label className="toggle">
                            <input type="checkbox" checked={onlySLA} onChange={(e) => setOnlySLA(e.target.checked)} />
                            <span>SLA (&lt;24s)</span>
                        </label>
                    </div>
                    <button className="btn ghost" onClick={fetchData}>
                        Yenile
                    </button>
                    <button className="btn primary" onClick={() => navigate("/inbox")}>
                        Benim Inbox’ım
                    </button>
                </div>
            </div>

            {err && <div className="mx-alert err">{err}</div>}
            {loading && <div className="mx-skel">Yükleniyor…</div>}

            {/* Kullanıcı satırları */}
            <div className="board board--users" style={{ gap: 12, marginTop: 8 }}>
                {MONITORED_USERS.map((u) => {
                    const cols = byUser.get(u.name);
                    const total = cols ? Object.values(cols).reduce((n, arr) => n + arr.length, 0) : 0;
                    return (
                        <section key={u.name} className="user-lane">
                            {/* Kullanıcı başlık kartı */}
                            <header className="col-head user-head">
                                <div className="user-pill">
                                    <span className="dot" />
                                    <span className="user-name">{u.name}</span>
                                </div>
                                <span className="count">{total}</span>
                            </header>

                            {/* Kullanıcıya ait mini-kanban */}
                            <div
                                className="board board--columns"
                                style={{ gridTemplateColumns: `repeat(${BOARD_COLUMNS.length}, minmax(240px, 1fr))`, gap: 10 }}
                            >
                                {BOARD_COLUMNS.map((col) => {
                                    const items = cols?.[col.key] || [];
                                    return (
                                        <div key={col.key} className="col modern">
                                            <div className="col-head mini">
                                                <span className={`dot ${STATUS_DOT_CLASS[col.key] || ""}`} />
                                                <h4 className="mini-title">{col.title}</h4>
                                                <span className="count">{items.length}</span>
                                            </div>

                                            <div className="col-body">
                                                {items.length === 0 && <div className="col-empty">Yok</div>}

                                                {items.slice(0, 6).map((item) => (
                                                    <button
                                                        key={item.id}
                                                        className={[
                                                            "card modern",
                                                            STATUS_RING_CLASS[item.durum] || "",
                                                            item.durum === STATUS.Tamamlandi ? "is-done" : "",
                                                        ].join(" ")}
                                                        onClick={() =>
                                                            navigate(`/inbox?assignee=${encodeURIComponent(item.atanan_email || "")}`)
                                                        }
                                                        title={`#${item.id} — ${item.baslik}\nGüncellenme: ${fmtTime(item.guncelleme_tarihi)}`}
                                                    >
                                                        <div className="c-title line-1">
                                                            #{item.id} — {item.baslik}
                                                        </div>
                                                        <div className="c-desc line-2">
                                                            {item.talep_eden_kullanici || item.talep_eden_email || "—"}
                                                        </div>
                                                        <div className="c-meta">
                                                            <span className="time">{fmtTime(item.guncelleme_tarihi)}</span>
                                                        </div>
                                                    </button>
                                                ))}

                                                {items.length > 6 && (
                                                    <div className="muted more">+{items.length - 6} daha</div>
                                                )}
                                            </div>
                                        </div>
                                    );
                                })}
                            </div>
                        </section>
                    );
                })}
            </div>

            <div className="muted tips" style={{ marginTop: 8 }}>
                Kartlara tıklayınca ilgili kişinin inbox’ına gidersin (URL’de <code>assignee</code> parametresiyle).
            </div>
        </div>
    );
}
