// src/App.jsx
import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Inbox from "./pages/Inbox";
import NewRequest from "./pages/NewRequest";
import Requests from "./pages/Requests";
import TeamMonitor from "./pages/TeamMonitor";

function RequireAuth({ children }) {
    const isAuthed = !!localStorage.getItem("auth_token");
    return isAuthed ? children : <Navigate to="/login" replace />;
}

// localStorage'dan kullanıcıyı güvenle çek
function getUserProfile() {
    try { return JSON.parse(localStorage.getItem("user_profile") || "{}"); }
    catch { return {}; }
}
function isOnurUser() {
    const p = getUserProfile();
    const name = String(p?.kullanici || "").trim().toLocaleUpperCase("tr-TR");
    return name === "ONUR KEREM ÖZTÜRK";
}

function HomeRedirect() {
    const isAuthed = !!localStorage.getItem("auth_token");
    if (!isAuthed) return <Login />; // oturum yoksa login aç
    return <Navigate to={isOnurUser() ? "/team-monitor" : "/inbox"} replace />;
}

export default function App() {
    return (
        <BrowserRouter>
            <Routes>
                {/* Giriş: oturum varsa Onur -> team-monitor, diğerleri -> inbox */}
                <Route path="/" element={<HomeRedirect />} />
                <Route path="/login" element={<Login />} />

                {/* Korumalı sayfalar */}
                <Route
                    path="/requests"
                    element={
                        <RequireAuth>
                            <Requests />
                        </RequireAuth>
                    }
                />
                <Route
                    path="/newrequest"
                    element={
                        <RequireAuth>
                            <NewRequest />
                        </RequireAuth>
                    }
                />
                <Route
                    path="/inbox"
                    element={
                        <RequireAuth>
                            <Inbox />
                        </RequireAuth>
                    }
                />

                {/* TeamMonitor: sadece Onur görebilsin */}
                <Route
                    path="/team-monitor"
                    element={
                        <RequireAuth>
                            {isOnurUser() ? <TeamMonitor /> : <Navigate to="/inbox" replace />}
                        </RequireAuth>
                    }
                />

                {/* Bilinmeyen rota -> login */}
                <Route path="*" element={<Navigate to="/login" replace />} />
            </Routes>
        </BrowserRouter>
    );
}
