// src/App.jsx
import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Inbox from "./pages/Inbox";
import NewRequest from "./pages/NewRequest";
import Requests from "./pages/Requests";
import TeamMonitor from "./pages/TeamMonitor"; // ✅ yeni sayfa

function RequireAuth({ children }) {
    const isAuthed = !!localStorage.getItem("auth_token");
    return isAuthed ? children : <Navigate to="/login" replace />;
}

export default function App() {
    // Kullanıcı bilgisi localStorage’da varsayıyoruz
    const currentUser =
        localStorage.getItem("user_name") || ""; // örn: "ONUR KEREM ÖZTÜRK"
    const isOnur =
        currentUser.toLocaleUpperCase("tr-TR") === "ONUR KEREM ÖZTÜRK";

    return (
        <BrowserRouter>
            <Routes>
                {/* Login sayfası */}
                <Route path="/" element={<Login />} />
                <Route path="/login" element={<Login />} />

                {/* Korumalı sayfalar */}
                <Route
                    path="/requests"
                    element={
                        <RequireAuth>
                            {isOnur ? (
                                <Navigate to="/team-monitor" replace />
                            ) : (
                                <Requests />
                            )}
                        </RequireAuth>
                    }
                />
                <Route
                    path="/newrequest"
                    element={
                        <RequireAuth>
                            {isOnur ? (
                                <Navigate to="/team-monitor" replace />
                            ) : (
                                <NewRequest />
                            )}
                        </RequireAuth>
                    }
                />
                <Route
                    path="/inbox"
                    element={
                        <RequireAuth>
                            {isOnur ? (
                                <Navigate to="/team-monitor" replace />
                            ) : (
                                <Inbox />
                            )}
                        </RequireAuth>
                    }
                />
                <Route
                    path="/team-monitor"
                    element={
                        <RequireAuth>
                            <TeamMonitor />
                        </RequireAuth>
                    }
                />

                {/* Bilinmeyen rota */}
                <Route path="*" element={<Navigate to="/login" replace />} />
            </Routes>
        </BrowserRouter>
    );
}
