// src/pages/Login.jsx
import React, { useState } from "react";
import supabase from "../supabaseClient";
import "./Login.css";

export default function Login() {
    const [email, setEmail] = useState("");
    const [pw, setPw] = useState("");
    const [showPw, setShowPw] = useState(false);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");

    // (OPSİYONEL) — sadece development: tek tıkla test kullanıcı aç
    async function createTestUser() {
        setErr("");
        const { data, error } = await supabase.auth.signUp({
            email: "admin@example.com",
            password: "123456",
            options: { data: { kullanici: "Admin", birim: "IT", title: "Uzman" } },
        });
        if (error) {
            console.error("signUp error:", error);
            setErr(`Kayıt başarısız: ${error.message}`);
        } else {
            alert("Test kullanıcı oluşturuldu. Eğer e-posta onayı açıksa, Studio’dan Confirm yapın ya da onayı kapatın.");
        }
    }

    // src/pages/Login.jsx (handleSubmit'i BÖYLE yap)
    async function handleSubmit(e) {
        e.preventDefault();
        setErr("");

        const emailTrim = (email || "").trim().toLowerCase();
        const pwTrim = (pw || "").trim();
        if (!emailTrim || !pwTrim) return setErr("E-posta ve parola zorunludur");

        try {
            setLoading(true);

            // Auth yerine doğrudan tabloya bakıyoruz:
            const { data, error } = await supabase
                .from("login")
                .select("id, email, kullanici, birim, title")
                .eq("email", emailTrim)
                .eq("sifre", pwTrim)        // şifre 6 haneli, birebir eşleşecek
                .maybeSingle();             // eşleşme yoksa data=null döner

            if (error) {
                console.error("DB error:", error);
                setErr("Giriş yapılamadı");
                return;
            }

            if (!data) {
                setErr("E-posta veya parola hatalı");
                return;
            }

            // Başarılı: basit oturum işareti ve profil sakla
            localStorage.setItem("auth_token", "ok-" + Date.now());
            localStorage.setItem("user_profile", JSON.stringify(data));

            alert(`Hoş geldin ${data.kullanici || data.email}!`);
            // TODO: navigate("/"); // yönlendirme ekleyeceksen
        } catch (e2) {
            console.error(e2);
            setErr("Beklenmeyen bir hata oluştu");
        } finally {
            setLoading(false);
        }
    }

    return (
        <div className="login-wrap">
            <form className="login-card" onSubmit={handleSubmit}>
                <h1>Talep Sistemi</h1>
                <p className="muted">Giriş yapın</p>

                <label className="field">
                    <span>E-posta</span>
                    <input
                        type="email"
                        placeholder="ornek@firma.com"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        autoComplete="email"
                    />
                </label>

                <label className="field">
                    <span>Parola</span>
                    <div className="pw">
                        <input
                            type={showPw ? "text" : "password"}
                            placeholder="••••••"
                            value={pw}
                            onChange={(e) => setPw(e.target.value)}
                            autoComplete="current-password"
                        />
                        <button type="button" className="ghost-btn" onClick={() => setShowPw((v) => !v)}>
                            {showPw ? "Gizle" : "Göster"}
                        </button>
                    </div>
                </label>

                {err && <div className="error">{err}</div>}

                <button className="primary-btn" disabled={loading}>
                    {loading ? "Giriş yapılıyor…" : "Giriş Yap"}
                </button>

                {/* sadece local geliştirme için */}
                <div className="hint" style={{ marginTop: 10 }}>
                    Sorun mu yaşıyorsun?{" "}
                    <button type="button" className="ghost-btn" onClick={createTestUser}>
                        Test kullanıcısı oluştur
                    </button>
                </div>
            </form>
        </div>
    );
}
