import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import supabase from "../supabaseClient";
import "./Login.css";

export default function Login() {
    const navigate = useNavigate();

    const [email, setEmail] = useState("");
    const [pw, setPw] = useState("");
    const [showPw, setShowPw] = useState(false);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");

    // Merkezi bildirim durumu
    const [notice, setNotice] = useState(null); // { type: 'success' | 'error', title, message }

    const showNotice = (type, message, title = type === "success" ? "Giri≈ü ba≈üarƒ±lƒ±" : "Hata") => {
        setNotice({ type, title, message });
        // 2.5s sonra otomatik kapat
        window.clearTimeout(showNotice._t);
        showNotice._t = window.setTimeout(() => setNotice(null), 2500);
    };

    async function handleSubmit(e) {
        e.preventDefault();
        setErr("");

        const emailTrim = (email || "").trim().toLowerCase();
        const pwTrim = (pw || "").trim();
        if (!emailTrim || !pwTrim) {
            showNotice("error", "E-posta ve parola zorunludur");
            return;
        }

        try {
            setLoading(true);

            // Tablo ile doƒürulama
            const { data, error } = await supabase
                .from("login")
                .select("id, email, kullanici, birim, title")
                .eq("email", emailTrim)
                .eq("sifre", pwTrim)
                .maybeSingle();

            if (error) {
                console.error("DB error:", error);
                showNotice("error", "Giri≈ü yapƒ±lamadƒ±. L√ºtfen tekrar deneyin.");
                return;
            }
            if (!data) {
                showNotice("error", "E-posta veya parola hatalƒ±");
                return;
            }

            localStorage.setItem("auth_token", "ok-" + Date.now());
            localStorage.setItem("user_profile", JSON.stringify(data));

            showNotice("success", `Ho≈ü geldin ${data.kullanici || data.email}!`);

            // === Y√∂nlendirme mantƒ±ƒüƒ± ===
            // T√ºrk√ße b√ºy√ºk harf kar≈üƒ±la≈ütƒ±rmasƒ± i√ßin 'tr-TR' kullanƒ±yoruz
            const name = (data.kullanici || "").trim().toLocaleUpperCase("tr-TR");
            const isGorkem = name === "G√ñRKEM √áADIRCI";

            // Bildirim bir an g√∂r√ºns√ºn diye √ßok kƒ±sa gecikme; istersen 0 da yapabilirsin
            setTimeout(() => {
                navigate(isGorkem ? "/inbox" : "/newrequest", { replace: true });
            }, 400);
        } catch (e2) {
            console.error(e2);
            showNotice("error", "Beklenmeyen bir hata olu≈ütu");
        } finally {
            setLoading(false);
        }
    }

    return (
        <div className="auth">
            <div className="auth-card">
                <div className="brand">
                    <div className="logo" aria-hidden="true">üß≠</div>
                    <div className="title">
                        <h1>Talep Sistemi</h1>
                        <p className="muted">Hesabƒ±nƒ±za giri≈ü yapƒ±n</p>
                    </div>
                </div>

                <form onSubmit={handleSubmit} className="form">
                    <label className="field">
                        <span className="label">E-posta</span>
                        <input
                            type="email"
                            placeholder="ornek@firma.com"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            autoComplete="email"
                            className="input"
                        />
                    </label>

                    <label className="field">
                        <span className="label">Parola</span>
                        <div className="pw">
                            <input
                                type={showPw ? "text" : "password"}
                                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                                value={pw}
                                onChange={(e) => setPw(e.target.value)}
                                autoComplete="current-password"
                                className="input"
                            />
                            <button
                                type="button"
                                className="ghost-btn"
                                onClick={() => setShowPw((v) => !v)}
                                aria-label={showPw ? "Parolayƒ± gizle" : "Parolayƒ± g√∂ster"}
                            >
                                {showPw ? "Gizle" : "G√∂ster"}
                            </button>
                        </div>
                    </label>

                    {/* Artƒ±k satƒ±r-i√ßi hata yerine merkez bildirim var; bu blok istersen kaldƒ±rƒ±labilir */}
                    {err && <div className="error">{err}</div>}

                    <button className="primary-btn" disabled={loading}>
                        {loading ? <span className="spinner" /> : "Giri≈ü Yap"}
                    </button>
                </form>

                <div className="footer">
                    <span className="muted">¬© {new Date().getFullYear()} Biriminiz</span>
                </div>
            </div>

            {/* Saƒü bilgilendirme paneli */}
            <div className="auth-aside" aria-hidden="false">
                <div className="aside-content">
                    <span className="pill">ƒ∞≈û VE S√úRE√á GELƒ∞≈ûTƒ∞RME</span>
                    <h2>Taleplerinizi buradan iletin</h2>
                    <p>
                        Ekibimize ilettiƒüiniz talepler; √∂ncelik, durum ve hedef tarihleriyle ≈üeffaf bi√ßimde izlenir.
                        S√ºreci ba≈ütan sona g√∂r√ºn, hƒ±zla sonu√ßlandƒ±rƒ±n.
                    </p>

                    <ul className="benefits">
                        <li><strong>Tek noktadan</strong> talep olu≈üturma ve takip</li>
                        <li><strong>Ger√ßek-zamanlƒ±</strong> durum g√ºncellemeleri</li>
                        <li><strong>Standartla≈ütƒ±rƒ±lmƒ±≈ü</strong> onay akƒ±≈ülarƒ±</li>
                    </ul>

                    <div className="mini-cta">
                        <span className="dot" /> Destekleyen birim: <b>ƒ∞≈ü &amp; S√ºre√ß Geli≈ütirme</b>
                    </div>
                </div>
            </div>

            {/* Merkezi bildirim (toast/modal) */}
            {notice && (
                <div className="toast-wrap" role="status" aria-live="polite">
                    <div className={`toast ${notice.type}`}>
                        <div className="toast-icon" aria-hidden="true">
                            {notice.type === "success" ? "‚úÖ" : "‚ö†Ô∏è"}
                        </div>
                        <div className="toast-body">
                            <div className="toast-title">{notice.title}</div>
                            <div className="toast-msg">{notice.message}</div>
                        </div>
                        <button className="toast-close" onClick={() => setNotice(null)} aria-label="Kapat">√ó</button>
                    </div>
                </div>
            )}
        </div>
    );
}
