import React, { useState } from "react";
import {
    LineChart,
    Line,
    BarChart,
    Bar,
    PieChart,
    Pie,
    Tooltip,
    XAxis,
    YAxis,
    CartesianGrid,
    Legend,
    ResponsiveContainer,
    Cell,
} from "recharts";

// Örnek veri
const users = ["Kerem", "Ayşe", "Mehmet"];

const inboxData = {
    Kerem: [
        { id: 1, subject: "Talep 1", status: "Tamamlandı", created: "2025-08-10", completed: "2025-08-11" },
        { id: 2, subject: "Talep 2", status: "Beklemede", created: "2025-08-12", completed: null },
    ],
    Ayşe: [
        { id: 3, subject: "Talep 3", status: "Tamamlandı", created: "2025-08-09", completed: "2025-08-10" },
        { id: 4, subject: "Talep 4", status: "Tamamlandı", created: "2025-08-11", completed: "2025-08-13" },
    ],
    Mehmet: [
        { id: 5, subject: "Talep 5", status: "Beklemede", created: "2025-08-15", completed: null },
        { id: 6, subject: "Talep 6", status: "Tamamlandı", created: "2025-08-16", completed: "2025-08-17" },
    ],
};

// Grafik için örnek veri üretici
const getAnalysisData = (inbox) => {
    const completedTasks = inbox.filter((t) => t.status === "Tamamlandı");
    const avgTimes = completedTasks.map((t) => {
        const created = new Date(t.created);
        const completed = new Date(t.completed);
        return (completed - created) / (1000 * 60 * 60 * 24); // gün farkı
    });

    return {
        completedCount: completedTasks.length,
        avgTime: avgTimes.length ? (avgTimes.reduce((a, b) => a + b, 0) / avgTimes.length).toFixed(1) : 0,
        statusData: [
            { name: "Tamamlandı", value: completedTasks.length },
            { name: "Beklemede", value: inbox.length - completedTasks.length },
        ],
        timelineData: inbox.map((t) => ({
            name: t.subject,
            days: t.completed ? (new Date(t.completed) - new Date(t.created)) / (1000 * 60 * 60 * 24) : 0,
        })),
    };
};

export default function TeamMonitor() {
    const [selectedUser, setSelectedUser] = useState(null);

    const inbox = selectedUser ? inboxData[selectedUser] : [];
    const analysis = selectedUser ? getAnalysisData(inbox) : null;

    return (
        <div className="p-6 bg-gray-100 min-h-screen">
            <h1 className="text-3xl font-bold mb-6">Team Monitor</h1>

            {/* Kullanıcı seçme */}
            <div className="flex gap-4 mb-6">
                {users.map((u) => (
                    <button
                        key={u}
                        onClick={() => setSelectedUser(u)}
                        className={`px-4 py-2 rounded text-white ${selectedUser === u ? "bg-blue-600" : "bg-gray-500"
                            }`}
                    >
                        {u}
                    </button>
                ))}
            </div>

            {selectedUser ? (
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                    {/* Inbox */}
                    <div className="bg-white p-4 rounded-lg shadow">
                        <h2 className="text-xl font-semibold mb-4">
                            {selectedUser} - Inbox
                        </h2>
                        <ul className="space-y-2">
                            {inbox.map((t) => (
                                <li
                                    key={t.id}
                                    className="p-3 border rounded flex justify-between items-center"
                                >
                                    <span>{t.subject}</span>
                                    <span
                                        className={`px-2 py-1 rounded text-sm ${t.status === "Tamamlandı"
                                                ? "bg-green-200 text-green-800"
                                                : "bg-yellow-200 text-yellow-800"
                                            }`}
                                    >
                                        {t.status}
                                    </span>
                                </li>
                            ))}
                        </ul>
                    </div>

                    {/* Analizler */}
                    <div className="bg-white p-4 rounded-lg shadow">
                        <h2 className="text-xl font-semibold mb-4">Analizler</h2>
                        <p className="mb-2">
                            ✅ Tamamlanan İşler: {analysis.completedCount}
                        </p>
                        <p className="mb-4">
                            ⏱ Ortalama Tamamlama Süresi: {analysis.avgTime} gün
                        </p>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            {/* Bar Chart */}
                            <div className="h-64">
                                <ResponsiveContainer width="100%" height="100%">
                                    <BarChart data={analysis.timelineData}>
                                        <CartesianGrid strokeDasharray="3 3" />
                                        <XAxis dataKey="name" />
                                        <YAxis />
                                        <Tooltip />
                                        <Bar dataKey="days" fill="#8884d8" />
                                    </BarChart>
                                </ResponsiveContainer>
                            </div>

                            {/* Pie Chart */}
                            <div className="h-64">
                                <ResponsiveContainer width="100%" height="100%">
                                    <PieChart>
                                        <Pie
                                            data={analysis.statusData}
                                            dataKey="value"
                                            nameKey="name"
                                            cx="50%"
                                            cy="50%"
                                            outerRadius={80}
                                            label
                                        >
                                            <Cell fill="#4ade80" />
                                            <Cell fill="#facc15" />
                                        </Pie>
                                        <Tooltip />
                                    </PieChart>
                                </ResponsiveContainer>
                            </div>
                        </div>
                    </div>
                </div>
            ) : (
                <p className="text-gray-600">Lütfen bir kullanıcı seçiniz.</p>
            )}
        </div>
    );
}
